import{o as t,f as l,g as e}from"./motion.e7f3e1ab.js";const a='{"title":"Архитектура","description":"","frontmatter":{"title":"Архитектура","tags":"visual"},"relativePath":"art/visual/architecture.md","lastUpdated":1618316627399}',r={},n=[e("p",null,"В архитектуре меня привлекает ее фомирующая, воспитывающая роль. Сама форма и расположение здания, помещения, элемента задают то, как ими будут пользоваться люди. Профессиональные архитекторы работают в узких рамках множества требований как со стороны нормативной документации, так и со стороны заказчика проекта. Сложно представить свободного от подобных ограничений архитектора.",-1),e("p",null,"Однако именно к такой форме пришел я в своих экспериментах. Я стал придумывать пространства, созданные для совместного творчества. Начинал с планов переделки одного или нескольких морских контейнеров. Разрабатывал планировки мастерских в заданных рамках.",-1),e("p",null,"Затем взялся за крупный и сложный проект круглого здания с множеством разноплановых мастерских. Я пришел к названию УНИЦЕХ, говорящему о многообразии его производственных возможностей.",-1),e("p",null,"Я рисовал эскизы видов на здание. Проектировал каркас из преднапряженного железобетона, рассчитывал вертикальное остекление и вентилируемые фасады, разрабатывал поэтажную планировку и рассчитывал нагрузки на купол атриума. Мой инженерный опыт ракрылся здесь по-максимуму. Даже сопромат как-то смог переосмыслить.",-1),e("p",null,"Но самое интересное для меня — именно способность архитектуры создавать новые смыслы и процессы в обществе. Именно так я пришел и к работе над проектами Помещений для Медитации. Этим же путем добрался до работы над Открытым Форумом — открытым пространством для общественной дискуссии и принятия коллективных решений.",-1)];r.render=function(e,a,r,i,u,o){return t(),l("div",null,n)};export default r;export{a as __pageData};
