import{o as t,g as a,j as n}from"./motion.2c60df92.js";const o='{"title":"Фотография","description":"","frontmatter":{"title":"Фотография","tags":"visual","list":"photo","art":"film.jpg"},"relativePath":"art/visual/photo/index.md","lastUpdated":1620744938292}',l={},r=[n("p",null,"Фотография — одно из первых моих серьезных увлечений. Начал снимать лет в 17. Снимал на Зенит, печатал фотографии вручную с пленки в ванной, делал пинхолы и играл с ломо. Много снимал на Canon 300V.",-1),n("p",null,"Потом приобрел свой первый цифровик фуджи. С ним понял, чего хочу и переходил с него на Canon 5D. Его удачно сменил на Canon 5D mark II, с которым пока и остался. Дальше по пути развития зеркалок не пошел. Сейчас снимаю на телефон или на небольшой Canon G9X mark II. Он дает raw и достаточно компактный, чтобы носить с собой.",-1),n("p",null,"Для него у меня есть Zhiyun Crane – трехосевой стабилизатор для плавной съемки. Получается неплохой комплект для репортажей.",-1)];l.render=function(n,o,l,i,e,s){return t(),a("div",null,r)};export{o as __pageData,l as default};
