import{_ as e,o as s,c as a,a as t}from"./chunks/framework.c2f1a08b.js";const B=JSON.parse('{"title":"Скульптура","description":"","frontmatter":{"title":"Скульптура","tags":"visual"},"headers":[],"relativePath":"art/visual/sculpture.md","filePath":"art/visual/sculpture.md"}'),o={name:"art/visual/sculpture.md"},l=t("p",null,"Все мы строим скульптуры в детстве. Из пластилина, песка, снега, конструкторов. Вопрос — продолжаем ли мы этим заниматься и дальше? Лишь немногие.",-1),n=t("p",null,"А это ведь не просто узкая специализация, а один из базовых способов прямого взаимодействия с реальностью.",-1),c=t("p",null,"Я то и дело создаю скульптуры. От простых моделек в 3D до гипсовых, глиняных и бетонных работ различного масштаба.",-1),r=t("p",null,"Помню, как примерно в 10-11 классе у нас была контрольная по МХК и я по-хулигански вместо того, чтобы читать и писать, вырезал из ластика скульптуру в стиле индейцев Майя и сдал её как свою контрольную. Из-за таких выходок с четверками только по МХК и ОБЖ золотой медали от школы я не получил. Что-ж, зато получил ценные навыки работы с пространственной формой.",-1),_=t("p",null,"Слепить форму из пластилина, нередко на каркасе. Снять с нее гипсовые или силиконовые разъемные формы, залить в них гипс, бетон, эпоксидную смолу, металл или что-то иное. Дать этому застыть и, разобрав форму, зачистить отливку от облоя и готово. Дальше еще шлифовка, грунтовка, покраска и другая обработка. Так я воплощаю в объеме идеи и образы, которые я обнаруживаю в своей голове.",-1),u=t("p",null,"Экспериментирую с различными способами работы с деревом. Создавал работы из спилов, деревянных шаров и бамбуковых палочек. В сочетании с термоклеем они становятся удобной системой сборки полигональных форм. Мои работы геометричны, наполнены сакральным смыслом, который находит способ проявиться через меня.",-1),i=[l,n,c,r,_,u];function p(d,h,m,f,v,$){return s(),a("div",null,i)}const g=e(o,[["render",p]]);export{B as __pageData,g as default};
