import{o as t,g as l,j as n}from"./motion.2c60df92.js";const o='{"title":"Слесарное дело","description":"","frontmatter":{"title":"Слесарное дело","tags":"workshop"},"relativePath":"art/workshop/metal.md","lastUpdated":1618311699463}',r={},a=[n("p",null,"Первые эксперименты по работе с металлом я ставил еще в школе, когда на большом точиле для ножей затачивал и придавал разные формы 50-рублевым монетам, вышедшим из употребления после деноминации.",-1),n("p",null,"Азы слесарной работы осваивал на своем первом велосипеде BMX — что-то подкрутить, подточить, просверлить, смазать. Эти умения пригодились потом с машиной и, в особенности, со скутером. Там в сиденье у меня собирался очень неплохой комплект ключей и запчастей. Не все мне удавалось починить самому, но базовое обслуживание и экстренный ремонт мне удавался.",-1),n("p",null,"Мне давно было интересно освоить сварку и однажды я собрался и купил неплохой полуавтомат. Порошковой проволокой мне варить понравилось гораздо меньше, чем электродом и когда однажды этот аппарат у меня украли, вместо него я приобрел более простой инвертер для электрода.",-1),n("p",null,"Спасибо Глебу за то, что дал некоторый опыт работы с аргоннодуговой сваркой. Вот это, конечно, вещь. Свой аппарат для сварки неплавящимся электродом еще предстоит купить и освоить. Также очень интересны импульсные варианты этой технологии.",-1),n("p",null,"Ковать металл я пробовал давно, просто нагревая его газовой горелкой. На полноценном горне поковать получилось уже только на ЛОМе вместе с Мишей Кургановичем. Вот уж кто мастер металла. Он работает в оновном с титаном, что куда сложнее стали. Там целый мир нетривиальных задач.",-1)];r.render=function(n,o,r,e,p,s){return t(),l("div",null,a)};export{o as __pageData,r as default};
