import{_ as e,o,c as s,a as t}from"./chunks/framework.550780c6.js";const B=JSON.parse('{"title":"Слесарное дело","description":"","frontmatter":{"title":"Слесарное дело","tags":"workshop"},"headers":[],"relativePath":"art/workshop/metal.md","filePath":"art/workshop/metal.md"}'),a={name:"art/workshop/metal.md"},n=t("p",null,"Первые эксперименты по работе с металлом я ставил еще в школе, когда на большом точиле для ножей затачивал и придавал разные формы 50-рублевым монетам, вышедшим из употребления после деноминации.",-1),r=t("p",null,"Азы слесарной работы осваивал на своем первом велосипеде BMX — что-то подкрутить, подточить, просверлить, смазать. Эти умения пригодились потом с машиной и, в особенности, со скутером. Там в сиденье у меня собирался очень неплохой комплект ключей и запчастей. Не все мне удавалось починить самому, но базовое обслуживание и экстренный ремонт мне удавался.",-1),l=t("p",null,"Мне давно было интересно освоить сварку и однажды я собрался и купил неплохой полуавтомат. Порошковой проволокой мне варить понравилось гораздо меньше, чем электродом и когда однажды этот аппарат у меня украли, вместо него я приобрел более простой инвертер для электрода.",-1),_=t("p",null,"Спасибо Глебу за то, что дал некоторый опыт работы с аргоннодуговой сваркой. Вот это, конечно, вещь. Свой аппарат для сварки неплавящимся электродом еще предстоит купить и освоить. Также очень интересны импульсные варианты этой технологии.",-1),c=t("p",null,"Ковать металл я пробовал давно, просто нагревая его газовой горелкой. На полноценном горне поковать получилось уже только на ЛОМе вместе с Мишей Кургановичем. Вот уж кто мастер металла. Он работает в оновном с титаном, что куда сложнее стали. Там целый мир нетривиальных задач.",-1),p=[n,r,l,_,c];function d(i,h,m,u,f,k){return o(),s("div",null,p)}const $=e(a,[["render",d]]);export{B as __pageData,$ as default};
