import{k as r,f as t,g as e,J as a}from"./motion.f7ed0388.js";const n='{"title":"Архив записей и заметок до 2012 года","description":"","frontmatter":{"title":"Архив записей и заметок до 2012 года","tags":"writing","list":"writingArchive"},"relativePath":"art/writing/archive/index.md","lastUpdated":1620551650165}',l={},i=e("p",null,"Собрание заметок и сочинений до 2012 года. Первоначально публиковался в ВК, теперь эти живут здесь. Написал несколько коротких рассказов, но все больше — анализа и рассуждений на сложные темы. С тех пор утекло много воды и формулируемые тогда мысли прошли ещё много стадий осмысления и трансформации. За эти годы мировоззрение стало намного шире и юношеский радикализм утих, сменившись более мудрым отношением к жизни. И так интересно снова воспринимать его не как данность, а как результат долгого процесса обтачивания в рассуждениях, спорах и экспериментах.",-1),o=e("p",null,"Когда в школе нам задали написать сочинение не про какое-то конкретное произведение, а на более свободную тему, я выбрал говорить про искусство в моей жизни. Это сочинение высоко оценила учительница, зачитывала его даже другим классам. У меня даже где-то сохранился его скан. С тех пор и собирается некоторый архив лучших произведений.",-1),s=e("p",null,[a("Потом писательство проявлялось на форумах ("),e("a",{href:"https://forum.kamrad.ru/",class:"external-link",target:"_blank",rel:"noopener noreferrer"},"kamrad.ru"),a(", потом на "),e("a",{href:"https://lurkmore.to/Avanturist",class:"external-link",target:"_blank",rel:"noopener noreferrer"},"avanturist.org"),a(") и в долгих ночных философских беседах в ICQ и затем в ВК. Заметки в ВК стали конденсироваться из общения и размышлений.")],-1);l.render=function(e,a,n,l,u,p){return r(),t("div",null,[i,o,s])};export default l;export{n as __pageData};
