import{_ as r,o as a,c as n,a as e,b as t}from"./chunks/framework.c2f1a08b.js";const x=JSON.parse('{"title":"Архив записей и заметок до 2012 года","description":"","frontmatter":{"title":"Архив записей и заметок до 2012 года","tags":"writing","list":"writingArchive"},"headers":[],"relativePath":"art/writing/archive/index.md","filePath":"art/writing/archive/index.md"}'),i={name:"art/writing/archive/index.md"},s=e("p",null,"Собрание заметок и сочинений до 2012 года. Первоначально публиковался в ВК, теперь эти живут здесь. Написал несколько коротких рассказов, но все больше — анализа и рассуждений на сложные темы. С тех пор утекло много воды и формулируемые тогда мысли прошли ещё много стадий осмысления и трансформации. За эти годы мировоззрение стало намного шире и юношеский радикализм утих, сменившись более мудрым отношением к жизни. И так интересно снова воспринимать его не как данность, а как результат долгого процесса обтачивания в рассуждениях, спорах и экспериментах.",-1),o=e("p",null,"Когда в школе нам задали написать сочинение не про какое-то конкретное произведение, а на более свободную тему, я выбрал говорить про искусство в моей жизни. Это сочинение высоко оценила учительница, зачитывала его даже другим классам. У меня даже где-то сохранился его скан. С тех пор и собирается некоторый архив лучших произведений.",-1),l=e("p",null,[t("Потом писательство проявлялось на форумах ("),e("a",{href:"https://forum.kamrad.ru/",class:"external-link",target:"_blank",rel:"noreferrer"},"kamrad.ru"),t(", потом на "),e("a",{href:"https://lurkmore.to/Avanturist",class:"external-link",target:"_blank",rel:"noreferrer"},"avanturist.org"),t(") и в долгих ночных философских беседах в ICQ и затем в ВК. Заметки в ВК стали конденсироваться из общения и размышлений.")],-1),c=[s,o,l];function d(_,h,p,u,f,m){return a(),n("div",null,c)}const k=r(i,[["render",d]]);export{x as __pageData,k as default};
