import{o as l,f as t,g as n}from"./motion.e7f3e1ab.js";const e='{"title":"Про любовь","description":"","frontmatter":{"title":"Про любовь","tags":"writingArchive","date":"2005-05-04T00:00:00.000Z"},"relativePath":"art/writing/archive/love.md","lastUpdated":1617905210479}',r={},i=[n("p",null,"Я лежал на полу и смотрел в потолок. Потоки холодного воздуха поглаживали мое лицо и, нагреваясь, поднимались вверх. Я чувствовал холод паркета и тепло своего сердца. Я слышал тишину огромного зала и громкий шум в висках. Я просто лежал. Сделал один глубокий вдох и потом выдох. Прислушался. Нет, не к гулкой тишине зала, а к тому шуму, что гремел в голове. Ритм, мелодия, песня. Я закрыл глаза и увидел рой маленьких искорок, хаотично бегающих перед мыслимым взором. Треугольник, гора, прекраснейший горный пейзаж. Я положил руку на холодный паркет и ощутил его безжизненный холод. Вдруг – движение, тепло, волна. Рука словно лежала на поверхности огромного, почти бесконечного океана. Вскоре и все тело погрузилось в теплое ласкающее море. Вот он – мир внутри меня. Это не скучный маленький садик с гнилой скамейкой и сломанными детскими качелями. Это огромный бескрайний океан жизни. Чего здесь нет? Что осталось там, во внешнем мире и что совершенно лишнее здесь?",-1),n("p",null,"Наконец-то понял. Понял, что мне нужно. Точнее – то, чего мне не нужно. Слова. Да, слова. Зачем нужно знать, зачем нужно все знать и понимать, нужно просто любить. Любить чисто, по-детски. Как ребенок, не понимая ни слова, любит свою мать. Как собака любит своего хозяина. Как водитель любит свою машину. Он все поймет, всегда поддержит, но ему совершенно все равно, что было раньше. Почему мы никогда не спрашиваем у моря, как оно добродушно приютило и выходило жизнь на этой планете, не ругаем за миллионы жизней, которые оно унесло, не пытаемся выяснить для чего оно здесь и любит ли оно нас. Мы просто смотрим, как солнце в очередной раз погружается за горизонт и наслаждаемся самым прекрасным закатом. Мы просто смотрим и понимаем все, что оно хочет нам сказать.",-1),n("p",null,"Мне не нужно все обо всех знать, я люблю людей. Люблю без всего, люблю не за что-то, а просто бессловно люблю. Я всегда пойму, всегда выслушаю и никогда не брошу, но мне не надо ничего знать того, что мне не хотят говорить. Даже встретив нового человека, мне не нужно знать ничего о нем. Я счастлив лишь тем, что он есть и что он рядом. Я общаюсь именно сейчас, именно в данную секунду я люблю человека. Мне не нужно ничего, что будет потом или было раньше. Здесь и сейчас.",-1),n("p",null,"Хочешь мне что-то сказать – говори обязательно – я всегда рад услышать. Хочешь что-то узнать – спроси- я всегда рад ответить. Но у меня нет необходимости знать больше, чем ты захочешь мне сказать. Со стороны это может показаться глупым, эгоистичным, скучным, скупым. Зря.",-1),n("p",null,"Если есть в этом мире человек, который будет любить без слов – это мой человек. Если есть в этом мире чувство, которому не нужно прошлого и будущего, которое просто есть в один момент – это мое чувство. Оно горит яркой искрой секунду, а потом может потухнуть и остаться в прошлом, но это уже не важно. Прошлое не имеет значения.",-1),n("p",null,"Если бы был человек, который мог бы просто быть со мной, без слов и мыслей, с которым можно было бы общаться без всех условностей, просто на уровне того самого чистого и настоящего чувства. Я мог бы просидеть пять часов с ней, любуясь закатом, еще десять – лежа на кровати и наслаждаясь каждым мгновением близости. Я мог бы дарить миллионы алых роз без слов и записок, без намеков и намерений. Просто так. Бессловно.",-1),n("p",null,"Просто взглянув в глаза, мы бы поняли и знали все, что нужно. Просто сделав вдох, мы бы знали, все наше прошлое, а сделав выдох – увидели бы будущее до самого конца. Знать, но не помнить. Просто знать, что любишь, знать, что любим. Просто знать, что будет, знать, что было. Не нужно слов. Один взгляд в глаза, одно мгновение и ты знаешь все. Не нужно помнить, не нужно мечтать. Просто знать.",-1),n("p",null,"И даже если не вместе, если далеко, ты просто знаешь. Ты знаешь, что где-то есть я, я знаю, что где-то есть ты. Ты знаешь все, что я делаю, знаешь, о чем я думаю, но никогда меня в этом не упрекнешь. Я знаю где и с кем ты, но никогда не скажу ни слова. Твои глаза скажут мне все. Не будет ни обиды, ни злобы, не будет ни радости, ни печали. Будет лишь любовь. А если надоест, если все кончится – глаза расскажут и об этом. Не нужно ничего говорить, просто сделай вдох, а потом выдох.",-1),n("p",null,[n("em",null,"2004-2005 год, кажется")],-1)];r.render=function(n,e,r,a,p,u){return l(),t("div",null,i)};export default r;export{e as __pageData};
