import{_ as t}from"./plugin-vue_export-helper.21dcd24c.js";import{b as o,o as e,k as p}from"./motion.8d69bf4d.js";const v='{"title":"\u041E \u043D\u0435\u043F\u0440\u043E\u0442\u0438\u0432\u043E\u0440\u0435\u0447\u0438\u0432\u043E\u0439 \u0446\u0435\u043B\u043E\u0441\u0442\u043D\u043E\u0439 \u043A\u0430\u0440\u0442\u0438\u043D\u0435 \u043C\u0438\u0440\u0430.","description":"","frontmatter":{"title":"\u041E \u043D\u0435\u043F\u0440\u043E\u0442\u0438\u0432\u043E\u0440\u0435\u0447\u0438\u0432\u043E\u0439 \u0446\u0435\u043B\u043E\u0441\u0442\u043D\u043E\u0439 \u043A\u0430\u0440\u0442\u0438\u043D\u0435 \u043C\u0438\u0440\u0430.","date":"2010-12-08T00:00:00.000Z"},"relativePath":"art/writing/archive/picture.md","lastUpdated":1617903042000}',r={},a=p('<p>\u0417\u0430\u043C\u0435\u0442\u043A\u0430-\u0440\u0430\u0437\u043C\u044B\u0448\u043B\u0435\u043D\u0438\u0435 \u043E \u043F\u043E\u0441\u043B\u0435\u0434\u043D\u0438\u0445 \u0432\u043E\u0439\u043D\u0430\u0445 \u0442\u0443\u0442 \u0432 \u043A\u043E\u043C\u043C\u0435\u043D\u0442\u0430\u0440\u0438\u044F\u0445 \u0441 \u0434\u0440\u0443\u0437\u044C\u044F\u043C\u0438 \u0438 \u0437\u043D\u0430\u043A\u043E\u043C\u044B\u043C\u0438, \u043E \u043C\u0438\u043B\u044B\u0445 \u043A\u043E\u0442\u044F\u0442\u043A\u0430\u0445 \u0438 \u043A\u0440\u043E\u043B\u044C\u0447\u0430\u0442\u043A\u0430\u0445, \u043E \u0434\u0435\u043B\u044C\u0444\u0438\u043D\u0447\u0438\u043A\u0430\u0445, \u0432\u0438\u0441\u043A\u0430\u0440\u0435 \u0438 \u0431\u0430\u0442\u0442\u0445\u0451\u0440\u0442\u0430\u0445. \u042F \u043D\u0435 \u0431\u0443\u0434\u0443 \u0436\u0435\u0441\u0442\u0438\u0442\u044C, \u0434\u0440\u0443\u0437\u044C\u044F, \u043F\u0440\u043E\u0447\u0442\u0438\u0442\u0435!</p><p>\u0418\u0442\u0430\u043A, \u043D\u0430\u0447\u043D\u0435\u043C. \u041C\u0435\u043D\u044F \u0437\u043E\u0432\u0443\u0442 \u0414\u0435\u043D\u0438\u0441, \u044F \u0436\u0438\u0432\u0443 \u0432 \u041D\u043E\u0432\u043E\u043A\u043E\u0441\u0438\u043D\u043E, \u0440\u0430\u0431\u043E\u0442\u0430\u044E \u0432 \u0416\u0443\u043B\u0435\u0431\u0438\u043D\u043E, \u0432 \u0441\u0432\u043E\u0431\u043E\u0434\u043D\u043E\u0435 \u043E\u0442 \u0440\u0430\u0431\u043E\u0442\u044B \u0432\u0440\u0435\u043C\u044F \u043F\u0440\u0435\u0434\u043F\u043E\u0447\u0438\u0442\u0430\u044E \u043F\u0440\u043E\u0432\u043E\u0434\u0438\u0442\u044C \u0432\u0440\u0435\u043C\u044F \u0437\u0430 \u0447\u0442\u0435\u043D\u0438\u0435\u043C, \u043F\u043E\u0437\u043D\u0430\u0432\u0430\u043D\u0438\u0435\u043C \u043E\u043A\u0440\u0443\u0436\u0430\u044E\u0449\u0435\u0433\u043E \u043C\u0438\u0440\u0430, \u0432 \u0434\u0438\u0441\u043A\u0443\u0441\u0441\u0438\u044F\u0445 \u0441 \u0434\u0440\u0443\u0437\u044C\u044F\u043C\u0438 \u0438\u043B\u0438 \u0432 \u0440\u0430\u0437\u043C\u044B\u0448\u043B\u0435\u043D\u0438\u044F\u0445. \u041D\u0430 \u0432\u044B\u0445\u043E\u0434\u043D\u044B\u0445 \u0438\u0433\u0440\u0430\u044E \u0441 \u0440\u0435\u0431\u044F\u0442\u0430\u043C\u0438 \u0432 \u0444\u0443\u0442\u0431\u043E\u043B, \u043F\u0440\u043E\u0433\u0443\u043B\u0438\u0432\u0430\u044E\u0441\u044C \u0441 \u0434\u0440\u0443\u0437\u044C\u044F\u043C\u0438 \u043F\u043E \u0443\u043B\u0438\u0446\u0430\u043C \u0433\u043E\u0440\u043E\u0434\u0430. \u042D\u0442\u043E \u044F \u0432 \u043F\u043E\u0432\u0441\u0435\u0434\u043D\u0435\u0432\u043D\u043E\u0439 \u0436\u0438\u0437\u043D\u0438. \u041D\u043E \u0432\u043E\u0442 \u044F \u0437\u0430\u0445\u043E\u0436\u0443 \u0432 \u043A\u043E\u043D\u0442\u0430\u043A\u0442 \u0438 \u0441\u0442\u0430\u043D\u043E\u0432\u043B\u044E\u0441\u044C \u0441\u043E\u0432\u0441\u0435\u043C \u0434\u0440\u0443\u0433\u0438\u043C \u0447\u0435\u043B\u043E\u0432\u0435\u043A\u043E\u043C! \u0416\u0435\u0441\u0442\u043E\u043A\u0438\u0439, \u043E\u0433\u0440\u0430\u043D\u0438\u0447\u0435\u043D\u043D\u044B\u0439, \u0441\u0430\u043C\u043E\u0432\u043B\u044E\u0431\u043B\u0435\u043D\u043D\u044B\u0439 \u0442\u0438\u043F \u0441 \u043D\u0438\u0437\u043A\u043E\u0439 \u0441\u0430\u043C\u043E\u043E\u0446\u0435\u043D\u043A\u043E\u0439, \u043A\u043E\u0442\u043E\u0440\u044B\u0439 \u0442\u0430\u043A \u0438 \u043D\u043E\u0440\u043E\u0432\u0438\u0442 \u0432\u044B\u0435\u0431\u043D\u0443\u0442\u044C\u0441\u044F \u0441\u0432\u043E\u0438\u043C\u0438 \u043E\u0434\u043D\u043E\u0431\u043E\u043A\u0438\u043C\u0438 \u0432\u0437\u0433\u043B\u044F\u0434\u0430\u043C\u0438 \u0438 \u0432\u043E\u0437\u0432\u044B\u0441\u0438\u0442\u044C\u0441\u044F \u043D\u0430\u0434 \u043D\u0435\u043D\u0430\u0432\u0438\u0434\u0438\u043C\u043E\u0439 \u0438\u043C \u0442\u043E\u043B\u043F\u043E\u0439 \u0434\u0435\u0433\u0435\u043D\u0435\u0440\u0430\u0442\u043E\u0432 \u0432\u043E\u043A\u0440\u0443\u0433. \u0422\u0430\u043A \u043B\u0438 \u044D\u0442\u043E? \u041F\u043E\u0441\u0442\u0430\u0440\u0430\u044E\u0441\u044C \u043A\u043E\u0440\u0440\u0435\u043A\u0442\u043D\u043E \u043E\u0442\u0432\u0435\u0442\u0438\u0442\u044C \u043D\u0430 \u044D\u0442\u0438 \u043E\u0431\u0432\u0438\u043D\u0435\u043D\u0438\u044F \u0438 \u043E\u0431\u044A\u044F\u0441\u043D\u0438\u0442\u044C \u0441\u0432\u043E\u0435, \u0434\u043B\u044F \u043C\u043D\u043E\u0433\u0438\u0445 \u043F\u0440\u043E\u0442\u0438\u0432\u043E\u0440\u0435\u0447\u0438\u0432\u043E\u0435, \u043F\u043E\u0432\u0435\u0434\u0435\u043D\u0438\u0435.</p><p>\u0414\u0435\u043B\u043E \u0432 \u0442\u043E\u043C, \u0447\u0442\u043E \u044F \u0435\u0449\u0435 \u0432 \u0440\u0430\u043D\u043D\u0435\u043C \u0432\u043E\u0437\u0440\u0430\u0441\u0442\u0435 \u0432\u0437\u044F\u043B\u0441\u044F \u0437\u0430 \u043D\u0435\u043F\u0440\u043E\u0441\u0442\u0443\u044E \u0437\u0430\u0434\u0430\u0447\u0443 - \u0432\u044B\u0441\u0442\u0440\u043E\u0438\u0442\u044C \u043C\u0430\u043A\u0441\u0438\u043C\u0430\u043B\u044C\u043D\u043E \u0448\u0438\u0440\u043E\u043A\u0443\u044E, \u0432\u0441\u0435\u043E\u0445\u0432\u0430\u0442\u044B\u0432\u0430\u044E\u0449\u0443\u044E \u0438 \u043D\u0435\u043F\u0440\u043E\u0442\u0438\u0432\u043E\u0440\u0435\u0447\u0438\u0432\u0443\u044E \u043A\u0430\u0440\u0442\u0438\u043D\u0443 \u043E\u043A\u0440\u0443\u0436\u0430\u044E\u0449\u0435\u0433\u043E \u043C\u0438\u0440\u0430, \u043E\u043F\u0438\u0440\u0430\u044F\u0441\u044C \u043D\u0430 \u043A\u0430\u043A \u043C\u043E\u0436\u043D\u043E \u0431\u043E\u043B\u044C\u0448\u0435\u0435 \u043A\u043E\u043B\u0438\u0447\u0435\u0441\u0442\u0432\u043E \u0438\u0441\u0442\u043E\u0447\u043D\u0438\u043A\u043E\u0432 \u0438\u043D\u0444\u043E\u0440\u043C\u0430\u0446\u0438\u0438 \u0438 \u0441\u0443\u043C\u043C\u0438\u0440\u0443\u044F \u0435\u0435 \u043C\u0430\u043A\u0441\u0438\u043C\u0430\u043B\u044C\u043D\u043E \u0435\u043C\u043A\u043E, \u043D\u0435 \u0432\u044B\u0431\u0440\u0430\u0441\u044B\u0432\u0430\u044F \u043D\u0438\u0447\u0435\u0433\u043E \u0437\u0430 \u0431\u043E\u0440\u0442 \u0430\u0431\u0441\u0442\u0440\u0430\u043A\u0442\u043D\u044B\u0445 \u0432\u044B\u0432\u043E\u0434\u043E\u0432. \u0413\u0440\u0443\u0431\u043E \u0433\u043E\u0432\u043E\u0440\u044F, \u044F \u043E\u0447\u0435\u043D\u044C \u0431\u0435\u0440\u0435\u0436\u043D\u043E \u0441\u043E\u0431\u0438\u0440\u0430\u044E \u0432 \u043E\u0434\u043D\u0443 \u0441\u0445\u0435\u043C\u0443 \u0432\u0435\u0441\u044C \u0441\u0432\u043E\u0439 \u043E\u043F\u044B\u0442, \u0432\u0441\u0435 \u0441\u0432\u043E\u0438 \u0437\u043D\u0430\u043D\u0438\u044F, \u043D\u043E \u043D\u0435 \u0444\u043E\u0440\u043C\u0443\u043B\u0438\u0440\u0443\u044E \u0432\u0441\u044E \u0441\u0445\u0435\u043C\u0443 \u0432 \u0447\u0435\u0442\u043A\u043E\u0439 \u0441\u043B\u043E\u0432\u0435\u0441\u043D\u043E\u0439 \u0444\u043E\u0440\u043C\u0435. \u042D\u0442\u0438\u0445 \u0444\u043E\u0440\u043C \u043F\u043E\u043B\u043D\u043E. \u041E\u0434\u043D\u0438 \u0432\u044B\u0441\u0442\u0440\u0430\u0438\u0432\u0430\u044E\u0442 \u0441\u0432\u043E\u044E \u043A\u0430\u0440\u0442\u0438\u043D\u0443 \u043C\u0438\u0440\u0430 \u043E\u0442 \u0411\u043E\u0433\u0430, \u0434\u0440\u0443\u0433\u0438\u0435 \u0432\u0435\u0440\u044F\u0442 \u0432 \u0441\u043B\u0443\u0447\u0430\u0439\u043D\u0443\u044E \u044D\u0432\u043E\u043B\u044E\u0446\u0438\u044E \u0432\u0441\u0435\u0433\u043E \u0438 \u0432\u0441\u044F \u0438\u0437 \u0431\u043E\u043B\u044C\u0448\u043E\u0433\u043E \u0432\u0437\u0440\u044B\u0432\u0430, \u0442\u0440\u0435\u0442\u044C\u0438 \u0432\u043E\u043E\u0431\u0449\u0435 \u043D\u0435 \u043F\u044B\u0442\u0430\u044E\u0442\u0441\u044F \u0432\u044B\u044F\u0441\u043D\u044F\u0442\u044C, \u043A\u0430\u043A \u0443\u0441\u0442\u0440\u043E\u0435\u043D \u043C\u0438\u0440, \u0430 \u043F\u0440\u043E\u0441\u0442\u043E \u0438\u0437\u0443\u0447\u0430\u0442\u044C \u043B\u0438\u0448\u044C \u0441\u043B\u0435\u0434\u0441\u0442\u0432\u0438\u044F \u044D\u0442\u043E\u0433\u043E \u0443\u0441\u0442\u0440\u043E\u0439\u0441\u0442\u0432\u0430. \u041A\u0430\u043A \u0432\u043E\u0434\u0438\u0442\u0435\u043B\u0438, \u043A\u043E\u0442\u043E\u0440\u044B\u0435 \u043D\u0435 \u0445\u043E\u0442\u044F\u0442 \u0437\u043D\u0430\u0442\u044C, \u043A\u0430\u043A \u0442\u0430\u043C \u043F\u043E\u0434 \u043A\u0430\u043F\u043E\u0442\u043E\u043C \u0431\u0435\u043D\u0437\u0438\u043D \u043F\u0440\u0435\u0432\u0440\u0430\u0449\u0430\u0435\u0442\u0441\u044F \u0432\u043E \u0432\u0440\u0430\u0449\u0435\u043D\u0438\u0435 \u043A\u043E\u043B\u0435\u0441, \u0448\u0443\u043C \u0438 \u0441\u0432\u0435\u0442 \u0444\u0430\u0440 \u0441 \u0442\u0435\u043F\u043B\u043E\u043C \u0438\u0437 \u043F\u0435\u0447\u043A\u0438. \u042F \u0445\u043E\u0447\u0443 \u044D\u0442\u043E \u0437\u043D\u0430\u0442\u044C \u0438 \u043C\u043D\u043E\u0433\u043E\u0435 \u0434\u0440\u0443\u0433\u043E\u0435, \u043F\u0440\u0438 \u044D\u0442\u043E\u043C \u043D\u0435 \u0437\u0430\u0442\u044B\u043A\u0430\u044F &quot;\u0434\u044B\u0440\u044B&quot; \u0432 \u043F\u043E\u0437\u043D\u0430\u043D\u0438\u0438 \u043D\u0438\u043A\u0430\u043A\u0438\u043C\u0438 \u0437\u0430\u0442\u044B\u0447\u043A\u0430\u043C\u0438, \u0431\u0443\u0434\u044C \u0442\u043E \u043C\u0438\u0441\u0442\u0438\u0447\u0435\u0441\u043A\u0430\u044F \u0432\u0435\u0440\u0430 \u0438\u043B\u0438 \u043D\u0435\u043A\u0438\u0435 &quot;\u0441\u043B\u0438\u0448\u043A\u043E\u043C \u0441\u043B\u043E\u0436\u043D\u044B\u0435 \u0444\u0438\u0437\u0438\u0447\u0435\u0441\u043A\u0438\u0435 \u0443\u0440\u0430\u0432\u043D\u0435\u043D\u0438\u044F&quot;.</p><p>\u0427\u0442\u043E \u0436\u0435 \u044F \u043A\u043E\u043D\u043A\u0440\u0435\u0442\u043D\u043E \u043F\u0440\u0435\u0434\u043F\u0440\u0438\u043D\u0438\u043C\u0430\u044E \u0434\u043B\u044F \u0444\u043E\u0440\u043C\u0438\u0440\u043E\u0432\u0430\u043D\u0438\u044F \u043D\u0430\u0438\u0431\u043E\u043B\u0435\u0435 \u0435\u043C\u043A\u043E\u0439 \u0438 \u044F\u0441\u043D\u043E\u0439 \u043A\u0430\u0440\u0442\u0438\u043D\u044B \u043C\u0438\u0440\u0430? \u042F \u0432\u043D\u0438\u043C\u0430\u0442\u0435\u043B\u044C\u043D\u043E \u0441\u043B\u0435\u0436\u0443 \u0437\u0430 \u0441\u043E\u0431\u043E\u0439, \u0437\u0430 \u043E\u043A\u0440\u0443\u0436\u0430\u044E\u0449\u0438\u043C\u0438, \u0437\u0430 \u0438\u043D\u0444\u043E\u0440\u043C\u0430\u0446\u0438\u043E\u043D\u043D\u044B\u043C\u0438 \u043F\u043E\u0442\u043E\u043A\u0430\u043C\u0438 \u0432 \u0421\u041C\u0418 \u0438 \u0441\u0440\u0435\u0434\u0438 \u043C\u0430\u043B\u043E\u0437\u043D\u0430\u043A\u043E\u043C\u044B\u0445 \u043B\u044E\u0434\u0435\u0439. \u042F \u0440\u0435\u0430\u043B\u044C\u043D\u043E \u0434\u043E\u0445\u0440\u0435\u043D\u0430 \u0447\u0438\u0442\u0430\u044E \u0438 \u043F\u043E\u0447\u0442\u0438 \u0442\u043E\u043B\u044C\u043A\u043E \u043D\u0430\u0443\u0447\u043D\u0443\u044E \u043B\u0438\u0442\u0435\u0440\u0430\u0442\u0443\u0440\u0443. \u0418, \u0447\u0442\u043E \u0441\u0430\u043C\u043E\u0435 \u0432\u0430\u0436\u043D\u043E\u0435, \u0432\u0441\u0435 \u043F\u043E\u043B\u0443\u0447\u0435\u043D\u043D\u044B\u0435 \u0434\u0430\u043D\u043D\u044B\u0435, \u044F \u043F\u044B\u0442\u0430\u044E\u0441\u044C \u043F\u043E\u043C\u0435\u0441\u0442\u0438\u0442\u044C \u043D\u0435 \u043A\u0430\u0436\u0434\u043E\u0435 \u043D\u0430 \u0441\u0432\u043E\u044E \u043E\u0442\u0434\u0435\u043B\u044C\u043D\u0443\u044E \u043F\u043E\u043B\u043E\u0447\u043A\u0443, \u0430 \u0441\u0432\u044F\u0437\u0430\u0442\u044C \u0432\u0441\u0435 \u0432\u043C\u0435\u0441\u0442\u0435, \u043D\u0435 \u0442\u043E\u043B\u044C\u043A\u043E \u043D\u0430\u0439\u0442\u0438 \u043F\u0440\u0438\u0447\u0438\u043D\u0443 \u0443 \u0441\u043B\u0435\u0434\u0441\u0442\u0432\u0438\u044F, \u043D\u043E \u0438 \u043F\u043E\u043F\u044B\u0442\u0430\u0442\u044C\u0441\u044F \u0437\u0430\u043C\u0435\u0447\u0430\u0442\u044C, \u043A\u0430\u043A \u0441\u0432\u044F\u0437\u0430\u043D\u044B \u0441\u043E\u0432\u0441\u0435\u043C \u0443\u0434\u0430\u043B\u0435\u043D\u043D\u044B\u0435, \u043A\u0430\u0437\u0430\u043B\u043E\u0441\u044C \u0431\u044B, \u0441\u043E\u0431\u044B\u0442\u0438\u044F \u0434\u0440\u0443\u0433 \u0441 \u0434\u0440\u0443\u0433\u043E\u043C. \u041D\u0430\u043F\u0440\u0438\u043C\u0435\u0440, \u043F\u0440\u043E\u0441\u043C\u043E\u0442\u0440 \u043A\u0430\u043A\u043E\u0433\u043E-\u0442\u043E \u0440\u0435\u043A\u043B\u0430\u043C\u043D\u043E\u0433\u043E \u0440\u043E\u043B\u0438\u043A\u0430 \u043F\u043E \u0442\u0435\u043B\u0435\u043A\u0443 \u0432\u043D\u0435\u0437\u0430\u043F\u043D\u043E \u043E\u0431\u043D\u0430\u0440\u0443\u0436\u0438\u0432\u0430\u0435\u0442, \u0447\u0442\u043E \u0442\u0430 \u043B\u043E\u0436\u044C, \u0447\u0442\u043E \u0442\u0430\u043C \u0433\u043E\u0432\u043E\u0440\u0438\u0442\u0441\u044F \u0441 \u0445\u0438\u043C\u0438\u0447\u0435\u0441\u043A\u043E\u0439 \u0442\u043E\u0447\u043A\u0438 \u0437\u0440\u0435\u043D\u0438\u044F, \u043A \u0442\u043E\u043C\u0443 \u0436\u0435 \u043E\u0431\u0443\u0441\u043B\u043E\u0432\u043B\u0435\u043D\u0430 \u043F\u0440\u0438\u043C\u0435\u043D\u0435\u043D\u0438\u0435\u043C \u043D\u0435\u043A\u043E\u0442\u043E\u0440\u044B\u0445 \u043F\u0440\u0438\u0435\u043C\u043E\u0432 \u0438\u0437 \u043A\u0443\u0440\u0441\u0430 \u043C\u0430\u0440\u043A\u0435\u0442\u0438\u043D\u0433\u0430, \u043F\u0440\u043E \u043A\u043E\u0442\u043E\u0440\u044B\u0435 \u043C\u043D\u0435 \u0440\u0430\u0441\u0441\u043A\u0430\u0437\u044B\u0432\u0430\u043B\u0430 \u0410\u043D\u044F, \u0430 \u044F \u0438 \u0441\u0430\u043C \u0447\u0438\u0442\u0430\u043B \u043E \u043F\u043E\u0434\u043E\u0431\u043D\u044B\u0445 \u043C\u0435\u0445\u0430\u043D\u0438\u0437\u043C\u0430\u0445 \u0432\u043E\u0437\u0434\u0435\u0439\u0441\u0442\u0432\u0438\u044F \u0432 \u043A\u0443\u0440\u0441\u0435 \u041D\u041B\u041F \u0438 \u043F\u0441\u0438\u0445\u043E\u043B\u043E\u0433\u0438\u0438 \u043C\u0430\u0441\u0441\u043E\u0432\u043E\u0433\u043E \u043F\u043E\u0432\u0435\u0434\u0435\u043D\u0438\u044F, \u043A\u043E\u0442\u043E\u0440\u044B\u0435 \u043A\u043E\u0433\u0434\u0430-\u0442\u043E \u043E\u0447\u0435\u043D\u044C \u0432\u043D\u0438\u043C\u0430\u0442\u0435\u043B\u044C\u043D\u043E \u0438\u0437\u0443\u0447\u0438\u043B. \u0415\u0449\u0435 \u043E\u0434\u043D\u043E\u0439 \u0432\u0430\u0436\u043D\u043E\u0439 \u043E\u0441\u043E\u0431\u0435\u043D\u043D\u043E\u0441\u0442\u044C\u044E \u043C\u043E\u0435\u0433\u043E \u043C\u044B\u0448\u043B\u0435\u043D\u0438\u044F \u044F\u0432\u043B\u044F\u0435\u0442\u0441\u044F \u0442\u043E, \u0447\u0442\u043E \u044F \u0441\u0442\u0430\u0440\u0430\u044E\u0441\u044C \u0432\u043A\u043B\u044E\u0447\u0438\u0442\u044C \u043C\u0430\u043A\u0441\u0438\u043C\u0430\u043B\u044C\u043D\u044B\u0439 \u0434\u0438\u0430\u043F\u0430\u0437\u043E\u043D \u043E\u0449\u0443\u0449\u0435\u043D\u0438\u0439, \u044D\u043C\u043E\u0446\u0438\u0439, \u0441\u043E\u0441\u0442\u043E\u044F\u043D\u0438\u0439 \u0438 \u043E\u0442\u043D\u043E\u0448\u0435\u043D\u0438\u0439 \u0432 \u041E\u0414\u041D\u041E\u041C \u041C\u0413\u041D\u041E\u0412\u041D\u0415\u041D\u0418\u0418. \u041D\u0443 \u0432\u0441\u043F\u043E\u043C\u043D\u0438\u0442\u0435, \u043A\u0430\u043A \u043F\u0440\u0435\u043A\u0440\u0430\u0441\u043D\u043E \u0431\u044B\u043B\u043E \u043E\u0434\u043D\u0430\u0436\u0434\u044B \u043F\u0440\u0438\u0434\u044F \u0441 \u0434\u043E\u043B\u0433\u043E\u0439 \u0437\u0438\u043C\u043D\u0435\u0439 \u043F\u0440\u043E\u0433\u0443\u043B\u043A\u0438, \u043F\u043E\u043F\u0438\u0442\u044C \u0433\u043E\u0440\u044F\u0447\u0435\u0433\u043E \u0447\u0430\u044E \u0438 \u043E\u043A\u0430\u0437\u0430\u0442\u044C\u0441\u044F \u0432 \u0442\u0435\u043F\u043B\u0435, \u0441 \u043A\u0430\u043A\u0438\u043C \u043E\u0449\u0443\u0449\u0435\u043D\u0438\u0435\u043C \u0432\u044B \u0434\u0443\u043C\u0430\u043B\u0438, \u0447\u0442\u043E \u0432\u043E\u0442 \u0447\u0435\u0440\u0435\u0437 \u043F\u043E\u043B\u0433\u043E\u0434\u0430 \u0431\u0443\u0434\u0435\u0442 \u043F\u043E\u043D\u0430\u0441\u0442\u043E\u044F\u0449\u0435\u043C\u0443 \u0422\u0415\u041F\u041B\u041E, \u043A\u0430\u043A \u0432\u044B \u043C\u0435\u0447\u0442\u0430\u043B\u0438 \u043E \u043B\u0435\u0442\u0435, \u043E \u043C\u043E\u0440\u0435... \u0422\u0430\u043A \u0432\u043E\u0442, \u043A\u043E\u0433\u0434\u0430 \u044F \u043D\u0430 \u043C\u043E\u0440\u0435, \u044F \u0432\u0441\u0435\u043C\u0438 \u0441\u0438\u043B\u0430\u043C\u0438 \u0441\u0442\u0430\u0440\u0430\u044E\u0441\u044C \u043F\u043E\u043C\u043D\u0438\u0442\u044C \u0438 \u0434\u0435\u0440\u0436\u0430\u0442\u044C \u0432 \u0433\u043E\u043B\u043E\u0432\u0435 \u0432\u043E\u0442 \u0442\u0435 \u0434\u0430\u043B\u0435\u043A\u0438\u0435 \u043E\u0449\u0443\u0449\u0435\u043D\u0438\u044F, \u043A\u043E\u0433\u0434\u0430 \u044F \u043C\u0435\u0447\u0442\u0430\u043B \u043E\u0431 \u044D\u0442\u0438\u0445 \u043C\u0433\u043D\u043E\u0432\u0435\u043D\u0438\u044F\u0445, \u0438 \u0433\u043E\u0432\u043E\u0440\u0438\u043B, \u0447\u0442\u043E \u043C\u043D\u0435 \u043A\u0440\u043E\u043C\u0435 \u043C\u043E\u0440\u044F \u0438 \u0441\u043E\u043B\u043D\u0446\u0435 \u0431\u043E\u043B\u044C\u0448\u0435 \u043D\u0443 \u041D\u0418\u0427\u0415\u0413\u041E \u043D\u0435 \u043D\u0443\u0436\u043D\u043E. \u0418 \u0434\u0435\u0439\u0441\u0442\u0432\u0438\u0442\u0435\u043B\u044C\u043D\u043E \u043E\u0449\u0443\u0449\u0430\u044E \u0432\u0434\u0440\u0443\u0433, \u0447\u0442\u043E \u0434\u0430\u0436\u0435 \u043F\u0438\u0442\u044C \u043D\u0435 \u0445\u043E\u0447\u0435\u0442\u0441\u044F \u0438 \u0435\u0441\u0442\u044C, \u043D\u0435 \u043D\u0430\u0434\u043E \u043D\u0438 \u043C\u0443\u0437\u044B\u043A\u0438, \u043D\u0438 \u0443\u0434\u043E\u0431\u0441\u0442\u0432\u0430, \u043D\u0438 \u0434\u0430\u0436\u0435 \u0434\u0435\u043D\u0435\u0433 \u043D\u0438 \u043A\u043E\u043F\u0435\u0439\u043A\u0438, \u0432\u0435\u0434\u044C \u044F \u043E\u0441\u0443\u0449\u0435\u0441\u0442\u0432\u0438\u043B \u043C\u0435\u0447\u0442\u0443. \u0410 \u043A\u0430\u043A \u0442\u043E\u043B\u044C\u043A\u043E \u0437\u0430\u0431\u0443\u0434\u0435\u0448\u044C \u043F\u0440\u043E \u0442\u043E\u0442 \u0445\u043E\u043B\u043E\u0434\u043D\u044B\u0439 \u0437\u0438\u043C\u043D\u0438\u0439 \u0434\u0435\u043D\u044C, \u0442\u0430\u043A \u0441\u0440\u0430\u0437\u0443 \u0432\u0441\u0435 \u0447\u0442\u043E-\u0442\u043E \u043D\u0435 \u0442\u0430\u043A, \u043E\u043F\u044F\u0442\u044C \u043E \u0447\u0435\u043C-\u0442\u043E \u043C\u0435\u0447\u0442\u0430\u0442\u044C \u043F\u0440\u0438\u0445\u043E\u0434\u0438\u0442\u0441\u044F. \u041B\u0435\u0442\u043E\u043C \u044F \u043A\u0440\u0438\u0447\u0443 \u043E\u0442 \u0441\u0447\u0430\u0441\u0442\u044C\u044F, \u0447\u0442\u043E \u0436\u0430\u0440\u043A\u043E, \u0437\u0438\u043C\u043E\u0439 \u044F \u0440\u0430\u0434\u0443\u044E\u0441\u044C \u043A\u0430\u0436\u0434\u043E\u0439 \u0441\u043D\u0435\u0436\u0438\u043D\u043A\u0435, \u044F \u043E\u0447\u0435\u043D\u044C-\u043E\u0447\u0435\u043D\u044C \u0441\u0442\u0430\u0440\u0430\u044E\u0441\u044C \u043F\u043E\u043C\u043D\u0438\u0442\u044C \u0432\u0441\u0435, \u0434\u0435\u0440\u0436\u0430\u0442\u044C \u0432 \u0433\u043E\u043B\u043E\u0432\u0435 \u0432\u0441\u0435 \u0438 \u0441\u0440\u0430\u0437\u0443, \u0432\u0435\u0441\u044C \u0438\u0437\u0432\u0435\u0441\u0442\u043D\u044B\u0439 \u043C\u043D\u0435 \u043C\u0438\u0440 \u0446\u0435\u043B\u0438\u043A\u043E\u043C.</p><p>\u0422\u0430\u043A\u043E\u0439 \u043F\u043E\u0434\u0445\u043E\u0434 \u043C\u043D\u0435 \u043A\u0430\u0436\u0435\u0442\u0441\u044F \u0447\u0435\u0441\u0442\u043D\u044B\u043C, \u0432\u0435\u0434\u044C \u044F \u0432\u043D\u0438\u043C\u0430\u0442\u0435\u043B\u044C\u043D\u043E \u0441\u043B\u0435\u0436\u0443, \u0447\u0442\u043E\u0431\u044B \u043D\u0435 \u0431\u044B\u043B\u043E \u043D\u0438\u043A\u0430\u043A\u0438\u0445 \u043F\u0440\u043E\u0442\u0438\u0432\u043E\u0440\u0435\u0447\u0438\u0439, \u0447\u0442\u043E\u0431\u044B \u044F \u043D\u044B\u043D\u0435\u0448\u043D\u0438\u0439 \u0438 \u044F \u043F\u0440\u043E\u0448\u043B\u044B\u0439 \u0431\u044B\u043B\u0438 \u0447\u0435\u0441\u0442\u043D\u044B \u0438 \u043E\u0442\u043A\u0440\u044B\u0442\u044B \u0434\u0440\u0443\u0433 \u0441 \u0434\u0440\u0443\u0433\u043E\u043C, \u0430 \u043D\u0435 \u0441\u043A\u0440\u044B\u0432\u0430\u043B\u0438 \u0447\u0435\u0433\u043E-\u0442\u043E, \u0433\u043E\u0432\u043E\u0440\u044F &quot;\u043E\u0445, \u043A\u0430\u043A \u044F \u043C\u0435\u0447\u0442\u0430\u044E \u043E \u043F\u0440\u043E\u0441\u0442\u043E\u0439 \u043A\u0440\u043E\u0432\u0430\u0442\u0438, \u043F\u0443\u0441\u0442\u044C \u0434\u0430\u0436\u0435 \u0438\u0437 \u0434\u043E\u0441\u043E\u043A, \u043B\u0438\u0448\u044C \u0431\u044B \u043B\u0435\u0447\u044C!&quot; \u0432 \u043F\u044B\u043B\u0443 \u0443\u0441\u0442\u0430\u043B\u043E\u0441\u0442\u0438 \u0432 \u043F\u043E\u0445\u043E\u0434\u0435, \u0430 \u043F\u043E\u0442\u043E\u043C \u0432\u043E\u0437\u043C\u0443\u0449\u0430\u044F\u0441\u044C \u043A\u0440\u043E\u0432\u0430\u0442\u0438 \u0438\u0437 \u0434\u043E\u0441\u043E\u043A \u0447\u0435\u0440\u0435\u0437 \u043F\u043E\u043B\u0433\u043E\u0434\u0430 \u043F\u043E\u0441\u043B\u0435 \u043F\u043E\u0445\u043E\u0434\u0430 \u0438 \u043D\u0435 \u0431\u0443\u0434\u0443\u0447\u0438 \u0443\u0441\u0442\u0430\u0432\u0448\u0438\u043C.</p><p>\u042F \u0431\u044B \u0445\u043E\u0442\u0435\u043B \u0438\u0437\u0432\u0438\u043D\u0438\u0442\u044C\u0441\u044F \u0437\u0430 \u0442\u043E, \u0447\u0442\u043E \u043C\u043E\u0436\u0435\u0442 \u0431\u044B\u0442\u044C \u043A\u043E\u0433\u043E-\u0442\u043E \u0437\u0430\u0434\u0435\u0432\u0430\u044E \u0438\u043D\u043E\u0433\u0434\u0430 \u0442\u0443\u0442 \u0432 \u043A\u043E\u043D\u0442\u0430\u043A\u0442\u0435 \u0441\u0432\u043E\u0438\u043C\u0438 \u0441\u043E\u043E\u0431\u0449\u0435\u043D\u0438\u044F\u043C\u0438 \u0438 \u043A\u043E\u043C\u043C\u0435\u043D\u0442\u0430\u0440\u0438\u044F\u043C\u0438 \u0441 \u0437\u0430\u043C\u0435\u0442\u043A\u0430\u043C\u0438, \u043F\u0440\u043E\u0441\u0442\u043E \u044F \u043D\u0435\u043C\u043D\u043E\u0433\u043E \u0437\u0430\u0438\u0433\u0440\u0430\u043B\u0441\u044F \u0432 \u0441\u0432\u043E\u0438\u0445 \u0441\u0442\u0430\u0440\u0430\u043D\u0438\u044F\u0445 \u0436\u0438\u0442\u044C \u0447\u0435\u0441\u0442\u043D\u043E \u0438 \u0441\u0442\u0430\u043B \u0441\u043B\u0438\u0448\u043A\u043E\u043C \u043C\u043D\u043E\u0433\u043E \u0442\u0440\u0435\u0431\u043E\u0432\u0430\u0442\u044C \u043E\u0442 \u0434\u0440\u0443\u0433\u0438\u0445. \u0421\u043A\u043E\u0440\u0435\u0435 \u0432\u0441\u0435\u0433\u043E \u043A \u0441\u0442\u0430\u0440\u043E\u0441\u0442\u0438 \u044F \u0432\u043E\u043E\u0431\u0449\u0435 \u0441\u0442\u0430\u043D\u0443 \u044D\u0434\u0430\u043A\u0438\u043C \u0441\u0432\u043E\u0440\u043B\u0438\u0432\u044B\u043C \u0434\u0435\u0434\u043E\u043C, \u0445\u043E\u0442\u044F \u044F \u0431\u0443\u0434\u0443 \u0441\u0442\u0430\u0440\u0430\u0442\u044C\u0441\u044F \u043D\u0435 \u0437\u0430\u0433\u043E\u043D\u044F\u0442\u044C\u0441\u044F \u0432 \u0441\u0432\u043E\u0435\u043C \u043B\u0438\u0447\u043D\u043E\u043C \u043C\u0438\u0440\u043A\u0435.</p><p>\u041F\u043E\u044F\u0441\u043D\u044E \u0441\u0432\u043E\u0439 \u0432\u0437\u0433\u043B\u044F\u0434 \u043D\u0430 \u043F\u0440\u0438\u043C\u0435\u0440\u0435 \u044D\u0442\u043E\u0439 \u0442\u0435\u043C\u044B \u0441 \u0440\u0435\u0437\u043D\u0435\u0439 \u0434\u0435\u043B\u044C\u0444\u0438\u043D\u043E\u0432 \u0432 \u043A\u0430\u043A\u043E\u0439-\u0442\u043E \u0441\u043A\u0430\u043D\u0434\u0438\u043D\u0430\u0432\u0441\u043A\u043E\u0439 \u0431\u0443\u0445\u0442\u0435. \u0425\u043E\u0442\u044F \u044D\u0442\u043E \u043B\u0438\u0448\u044C \u043F\u0440\u0438\u043C\u0435\u0440 \u0431\u043E\u043B\u044C\u0448\u043E\u0439 \u0433\u043E\u0440\u044B \u0441\u0432\u044F\u0437\u0430\u043D\u043D\u043E\u0439 \u0438\u043D\u0444\u043E\u0440\u043C\u0430\u0446\u0438\u0438, \u0432\u0435\u0434\u044C \u0432\u0441\u0435 \u044D\u0442\u0438 \u0443\u043C\u0438\u043B\u0438\u0442\u0435\u043B\u044C\u043D\u044B\u0435 \u043A\u0430\u0440\u0442\u0438\u043D\u043E\u0447\u043A\u0438 \u0441 \u043A\u043E\u0442\u044F\u0442\u043A\u0430\u043C\u0438 \u0438 \u043F\u0451\u0441\u0438\u043A\u0430\u043C\u0438, \u0444\u043E\u0442\u043E\u0433\u0440\u0430\u0444\u0438\u0438 \u043C\u0443\u0436\u0438\u043A\u043E\u0432 \u0432 \u043A\u043E\u0436\u0430\u043D\u0430\u0445 \u0438\u043B\u0438 \u0443\u044E\u0442\u043D\u044B\u0445 \u0438\u043D\u0442\u0435\u0440\u044C\u0435\u0440\u043E\u0432 \u0441 \u043E\u0444\u043E\u0440\u043C\u043B\u0435\u043D\u0438\u0435\u043C \u0438\u0437 \u043E\u0432\u0435\u0447\u044C\u0438\u0445 \u0448\u043A\u0443\u0440, \u044D\u0442\u0438 \u0432\u043E\u0437\u043C\u0443\u0449\u0435\u043D\u0438\u044F \u043F\u043E \u043F\u043E\u0432\u043E\u0434\u0443 \u043F\u0440\u0430\u0437\u0434\u043D\u0438\u043A\u0430 \u041A\u0443\u0440\u0431\u0430\u043D-\u0431\u0430\u0439\u0440\u0430\u043C, \u0430 \u0442\u0430\u043A\u0436\u0435 \u0432\u043E\u0437\u043C\u0443\u0449\u0435\u043D\u0438\u044F \u043E \u0432\u043E\u0437\u043C\u0443\u0449\u0435\u043D\u0438\u044F\u0445 \u043F\u043E \u043F\u043E\u0432\u043E\u0434\u0443 \u044D\u0442\u043E\u0433\u043E \u043F\u0440\u0430\u0437\u0434\u043D\u0438\u043A\u0430, \u044D\u0442\u0438 \u043C\u0435\u0447\u0442\u044B \u0434\u0430\u043C \u043E \u0448\u0443\u0431\u043A\u0435 \u0438\u043B\u0438 \u0434\u0443\u0431\u043B\u0435\u043D\u043A\u0435 \u0438 \u043F\u0430\u0440\u0430\u043B\u043B\u0435\u043B\u044C\u043D\u044B\u0435 \u0441 \u043D\u0438\u043C\u0438 \u043C\u0438\u0444\u044B \u043E \u0442\u043E\u043C, \u0447\u0442\u043E \u044D\u0442\u043E \u0441\u0430\u043C\u0430\u044F \u043B\u0443\u0447\u0448\u0430\u044F \u043E\u0434\u0435\u0436\u0434\u0430 \u043D\u0430 \u0437\u0438\u043C\u0443 \u043D\u0430 \u0432\u0441\u0435 \u0432\u0440\u0435\u043C\u0435\u043D\u0430, \u044D\u0442\u0438 \u043A\u0440\u0438\u043A\u0438 \u0432\u0435\u0433\u0430\u043D\u043E\u0432 \u0438 \u0432\u0435\u0433\u0435\u0442\u0430\u0440\u0438\u0430\u043D\u0446\u0435\u0432 \u043E \u0436\u0435\u0441\u0442\u043E\u043A\u0438\u0445 \u043B\u044E\u0434\u044F\u0445, \u0443\u0431\u0438\u0432\u0430\u044E\u0449\u0438\u0445 \u0436\u0438\u0432\u043E\u0442\u043D\u044B\u0445 \u043D\u0430 \u0435\u0434\u0443, \u0438 \u0438\u0445 \u043F\u0440\u0435\u0434\u043B\u043E\u0436\u0435\u043D\u0438\u044F \u0443\u0431\u0438\u0432\u0430\u0442\u044C \u043B\u044E\u0434\u0435\u0439 \u0437\u0430 \u044D\u0442\u043E, \u0434\u0430 \u043C\u0430\u043B\u043E \u043B\u0438 \u0435\u0449\u0435 \u043F\u0435\u0440\u0435\u0447\u0438\u0441\u043B\u044F\u0442\u044C - \u0432\u0441\u0435 \u044D\u0442\u043E \u0447\u0430\u0441\u0442\u0438 \u043E\u0434\u043D\u043E\u0439 \u0438 \u0442\u043E\u0439 \u0436\u0435 \u0438\u0441\u0442\u043E\u0440\u0438\u0438, \u043A\u043E\u0442\u043E\u0440\u0443\u044E \u044F \u043F\u044B\u0442\u0430\u044E\u0441\u044C \u0443\u0434\u0435\u0440\u0436\u0430\u0442\u044C \u0432 \u0433\u043E\u043B\u043E\u0432\u0435 \u0426\u0415\u041B\u0418\u041A\u041E\u041C, \u0430 \u043D\u0435 \u0440\u0430\u0441\u0441\u043C\u0430\u0442\u0440\u0438\u0432\u0430\u0442\u044C \u0435\u0435 \u043E\u0442\u0434\u0435\u043B\u044C\u043D\u044B\u0435 \u043D\u0435 \u043E\u0441\u043E\u0431\u0435\u043D\u043D\u043E-\u0442\u043E \u0438 \u0432\u0430\u0436\u043D\u044B\u0435 \u043A\u0443\u0441\u043E\u0447\u043A\u0438. \u042D\u0442\u0430 \u0442\u0435\u043C\u0430 \u0432 \u043C\u043E\u0435\u0439 \u0433\u043E\u043B\u043E\u0432\u0435 \u043D\u0430\u0437\u044B\u0432\u0430\u0435\u0442\u0441\u044F \u043F\u0440\u043E\u0441\u0442\u043E \u041E\u0422\u041D\u041E\u0428\u0415\u041D\u0418\u0415 \u0427\u0415\u041B\u041E\u0412\u0415\u041A\u0410 \u041A \u0416\u0418\u0412\u041E\u0422\u041D\u041E\u041C\u0423 \u041C\u0418\u0420\u0423. \u0418 \u044F \u043F\u044B\u0442\u0430\u044E\u0441\u044C \u0432\u043D\u0443\u0442\u0440\u0438 \u044D\u0442\u043E\u0439 \u0442\u0435\u043C\u044B \u0432\u044B\u0441\u0442\u0440\u043E\u0438\u0442\u044C \u0446\u0435\u043B\u043E\u0441\u0442\u043D\u0443\u044E \u0438 \u043D\u0435\u043F\u0440\u043E\u0442\u0438\u0432\u043E\u0440\u0435\u0447\u0438\u0432\u0443\u044E \u043A\u0430\u0440\u0442\u0438\u043D\u0443. \u0418 \u0432 \u043D\u0435\u0439 \u043E\u0434\u043D\u043E\u0432\u0440\u0435\u043C\u0435\u043D\u043D\u043E \u043D\u0443 \u043D\u0438\u043A\u0430\u043A \u043D\u0435 \u043F\u043E\u043B\u0443\u0447\u0430\u0435\u0442\u0441\u044F \u0443\u043C\u0435\u0441\u0442\u0438\u0442\u044C \u0433\u043B\u0443\u0431\u043E\u0447\u0430\u0439\u0448\u0438\u0439 \u0432\u043E\u0441\u0442\u043E\u0440\u0433 \u043E\u0442 \u043C\u0438\u043B\u0435\u043D\u044C\u043A\u0438\u0445 \u043A\u043E\u0442\u044F\u0442\u043E\u043A \u0438 \u0449\u0435\u043D\u044F\u0442, \u0441\u043E\u043F\u0440\u044F\u0436\u0435\u043D\u043D\u043E\u0435 \u0441 \u044F\u0440\u043E\u0441\u0442\u043D\u043E\u0439 \u0437\u0430\u0449\u0438\u0442\u043E\u0439 \u0438\u0445 \u0438 \u0434\u0440\u0443\u0433\u0438\u0445 \u043F\u0443\u0448\u0438\u0441\u0442\u0438\u043A\u043E\u0432 \u043E\u0442 \u043B\u044E\u0431\u044B\u0445 \u0434\u0430\u0436\u0435 \u0441\u043B\u043E\u0432\u0435\u0441\u043D\u044B\u0445 \u043D\u0430\u043F\u0430\u0434\u043E\u043A \u0438 \u043D\u0435\u043F\u0440\u0435\u043E\u0434\u043E\u043B\u0438\u043C\u0430\u044F \u043F\u043E\u0442\u0440\u0435\u0431\u043D\u043E\u0441\u0442\u044C \u0432 \u0448\u0443\u0431\u0435 \u0438\u0437 \u043D\u0443\u0442\u0440\u0438\u0438. \u0422\u0430\u043A\u0436\u0435 \u043D\u0435 \u043F\u043E\u043C\u0435\u0449\u0430\u0435\u0442\u0441\u044F \u0438 \u043B\u044E\u0431\u043E\u0432\u044C \u043A \u0445\u043E\u0440\u043E\u0448\u043E \u043F\u0440\u043E\u0436\u0430\u0440\u0435\u043D\u043D\u043E\u043C\u0443 \u0441\u0442\u0435\u0439\u043A\u0443 \u0438\u043B\u0438 \u043F\u0435\u043B\u044C\u043C\u0435\u0448\u043A\u0430\u043C \u0432\u043C\u0435\u0441\u0442\u0435 \u0441 \u043E\u0441\u0443\u0436\u0434\u0435\u043D\u0438\u0435\u043C \u0442\u0440\u0430\u0434\u0438\u0446\u0438\u043E\u043D\u043D\u043E\u0433\u043E \u0438 \u0434\u043E\u0441\u0442\u0430\u0442\u043E\u0447\u043D\u043E \u043E\u0431\u044B\u0434\u0435\u043D\u043D\u043E\u0433\u043E \u0440\u0438\u0442\u0443\u0430\u043B\u0430 \u043F\u0440\u0438\u043D\u043E\u0448\u0435\u043D\u0438\u044F \u0432 \u0436\u0435\u0440\u0442\u0432\u0443 \u0431\u0430\u0440\u0430\u0448\u043A\u0430 \u043D\u0430 \u041A\u0443\u0440\u0431\u0430\u043D-\u0411\u0430\u0439\u0440\u0430\u043C \u043C\u0443\u0441\u0443\u043B\u044C\u043C\u0430\u043D\u0430\u043C\u0438. \u041A\u0430\u043A \u043C\u043E\u0436\u043D\u043E \u043E\u0434\u043D\u043E\u0432\u0440\u0435\u043C\u0435\u043D\u043D\u043E \u043F\u043E\u043E\u0449\u0440\u044F\u0442\u044C \u0443\u0431\u0438\u0439\u0441\u0442\u0432\u043E \u0436\u0438\u0432\u043E\u0442\u043D\u044B\u0445 \u0440\u0430\u0434\u0438 \u043F\u0438\u0449\u0438 \u0438 \u0442\u0443\u0442 \u0436\u0435 \u044D\u0442\u043E \u043E\u0441\u0443\u0436\u0434\u0430\u0442\u044C. \u041A\u043E\u0433\u0434\u0430 \u043D\u0430\u0447\u0438\u043D\u0430\u044E\u0442 \u0433\u043E\u0432\u043E\u0440\u0438\u0442\u044C \u043E \u0442\u043E\u043C, \u0447\u0442\u043E \u0445\u043E\u0442\u044F\u0442 \u043F\u0440\u043E\u0441\u0442\u043E \u044D\u0442\u043E\u0433\u043E \u043D\u0435 \u0432\u0438\u0434\u0435\u0442\u044C, \u0442\u043E \u043C\u0435\u043D\u044F \u044D\u0442\u043E \u043F\u0440\u043E\u0441\u0442\u043E \u043F\u0443\u0433\u0430\u0435\u0442! \u041E\u0442 \u0442\u043E\u0433\u043E, \u0447\u0442\u043E \u043B\u0438\u0447\u043D\u043E \u0442\u044B \u044D\u0442\u043E\u0433\u043E \u043D\u0435 \u0432\u0438\u0434\u0438\u0448\u044C, \u043D\u0438\u0447\u0435\u0433\u043E \u043D\u0435 \u0438\u0437\u043C\u0435\u043D\u0438\u0442\u0441\u044F \u0438 \u043D\u0430 \u0431\u043E\u0439\u043D\u0435 \u0432 \u0446\u0435\u0445\u0443 \u0437\u0430\u0431\u043E\u044F \u043B\u044E\u0434\u0438 \u043F\u043E \u043F\u0440\u0435\u0436\u043D\u0435\u043C\u0443 \u0431\u0443\u0434\u0443\u0442 \u0440\u0443\u0431\u0438\u0442\u044C \u0433\u043E\u043B\u043E\u0432\u044B \u0441\u043E\u0442\u043D\u044F\u043C \u043A\u043E\u0440\u043E\u0432\u043E\u043A \u0438 \u0442\u0430\u043A\u0438\u0445 \u0436\u0435 \u0431\u0430\u0440\u0430\u0448\u043A\u043E\u0432 \u043A\u0430\u0436\u0434\u044B\u0439 \u0434\u0435\u043D\u044C.</p><p>\u042F \u043D\u0430\u0445\u043E\u0436\u0443 \u043D\u0435\u0447\u0435\u0441\u0442\u043D\u044B\u043C, \u0435\u0441\u043B\u0438 \u0431\u044B \u0432\u0447\u0435\u0440\u0430 \u0433\u043E\u0432\u043E\u0440\u0438\u043B \u043E \u0442\u043E\u043C, \u0447\u0442\u043E \u044F \u043B\u044E\u0431\u043B\u044E \u043C\u0430\u043B\u0435\u043D\u044C\u043A\u0438\u0445 \u0431\u0435\u043B\u044F\u043A\u043E\u0432 \u0438 \u0447\u0442\u043E \u043D\u0430\u0434\u043E \u043A\u0440\u0438\u0447\u0430\u0442\u044C \u0438 \u0431\u043E\u0440\u043E\u0442\u044C\u0441\u044F \u0441 \u0442\u044E\u043B\u0435\u043D\u044C\u0438\u043C \u043F\u0440\u043E\u043C\u044B\u0441\u043B\u043E\u043C \u0432 \u042F\u043A\u0443\u0442\u0438\u0438, \u0430 \u043D\u0430 \u0441\u043B\u0435\u0434\u0443\u044E\u0449\u0438\u0439 \u0434\u0435\u043D\u044C \u0432\u044B\u0431\u0438\u0440\u0430\u043B \u0431\u044B \u0441\u0435\u0431\u0435 \u0448\u0443\u0431\u0443 \u0438\u0437 \u043D\u0430\u0442\u0443\u0440\u0430\u043B\u044C\u043D\u043E\u0433\u043E \u043C\u0435\u0445\u0430. \u0418\u043B\u0438, \u043D\u0430\u043E\u0431\u043E\u0440\u043E\u0442, \u043E\u0441\u0443\u0436\u0434\u0430\u043B \u0431\u044B \u043A\u0440\u043E\u0432\u0430\u0432\u044B\u0435 \u0440\u0438\u0442\u0443\u0430\u043B\u044B \u043A\u0430\u043A\u043E\u0439-\u0442\u043E \u0433\u0440\u0443\u043F\u043F\u044B \u043B\u044E\u0434\u0435\u0439, \u0430 \u043F\u043E\u0442\u043E\u043C \u0431\u044B \u0441\u043B\u043E\u043F\u0430\u043B \u0432\u043A\u0443\u0441\u043D\u044B\u0439 \u0431\u0430\u0440\u0430\u043D\u0438\u0439 \u0448\u0430\u0448\u043B\u044B\u043A \u0432 \u043A\u0430\u0444\u0435\u0448\u043A\u0435 \u0432 \u0411\u043B\u0430\u0433\u043E\u0432\u0435\u0449\u0435\u043D\u043A\u0435.</p><p>\u041D\u0430\u0445\u043E\u0436\u0443 \u043D\u0435\u0447\u0435\u0441\u0442\u043D\u044B\u043C, \u043D\u043E \u043D\u0435 \u043C\u043E\u0433\u0443 \u043F\u043E\u043A\u0430 \u0434\u043E\u0445\u043E\u0434\u0447\u0438\u0432\u043E \u043E\u0431\u044A\u044F\u0441\u043D\u0438\u0442\u044C \u043A\u043E\u0440\u043D\u0438 \u044D\u0442\u0438\u0445 \u0437\u0430\u0431\u043B\u0443\u0436\u0434\u0435\u043D\u0438\u0439 \u0438 \u0441\u0430\u043C\u043E\u043E\u0431\u043C\u0430\u043D\u0430, \u043D\u0430\u0445\u043E\u0436\u0443 \u043E\u0448\u0438\u0431\u043E\u0447\u043D\u044B\u043C, \u043D\u043E \u043D\u0435 \u043C\u043E\u0433\u0443 \u043F\u043E\u043A\u0430 \u0441\u043E\u0432\u0435\u0440\u0448\u0435\u043D\u043D\u043E \u044F\u0441\u043D\u043E \u043E\u0431\u044A\u044F\u0441\u043D\u0438\u0442\u044C, \u043A\u0430\u043A \u0436\u0435 \u043E\u0431\u043E\u0439\u0442\u0438\u0441\u044C \u0431\u0435\u0437 \u043E\u0448\u0438\u0431\u043E\u043A. \u0417\u0434\u0435\u0441\u044C \u0438 &quot;\u0444\u0430\u0448\u0438\u0437\u043C&quot; \u043F\u043E \u043E\u0442\u043D\u043E\u0448\u0435\u043D\u0438\u044E \u043A\u043E \u0432\u0441\u0435\u043C\u0443 \u041D\u0415 \u043C\u0438\u043B\u0435\u043D\u044C\u043A\u043E\u043C\u0443 \u0438 \u041D\u0415 \u043F\u0443\u0448\u0438\u0441\u0442\u0435\u043D\u044C\u043A\u043E\u043C\u0443, \u043A\u043E\u0433\u0434\u0430 \u0432\u0441\u0435 \u0436\u0438\u0432\u043E\u0442\u043D\u044B\u0435 \u0441 \u043C\u0438\u043B\u044B\u043C\u0438 \u043C\u043E\u0440\u0434\u0430\u043C\u0438 \u043F\u043E\u043B\u0443\u0447\u0430\u044E\u0442 \u043D\u0430\u043C\u043D\u043E\u0433\u043E \u0431\u043E\u043B\u044C\u0448\u0435 \u043F\u0440\u0430\u0432 \u0432 \u0433\u043B\u0430\u0437\u0430\u0445 \u043B\u044E\u0434\u0435\u0439. \u0417\u0434\u0435\u0441\u044C \u0438 \u0434\u0432\u043E\u0439\u043D\u044B\u0435 \u0441\u0442\u0430\u043D\u0434\u0430\u0440\u0442\u044B \u043A \u0443\u0431\u0438\u0439\u0441\u0442\u0432\u0443, \u043A\u043E\u0442\u043E\u0440\u043E\u0435 \u0432\u0438\u0434\u043D\u043E \u0438 \u043D\u0435 \u0432\u0438\u0434\u043D\u043E. \u0417\u0434\u0435\u0441\u044C \u0438 \u043A\u0440\u0430\u0439\u043D\u0435 \u0440\u0430\u0437\u043C\u044B\u0442\u0430\u044F \u0433\u0440\u0430\u043D\u0438\u0446\u0430 \u043C\u0435\u0436\u0434\u0443 \u0436\u0438\u0432\u043E\u0442\u043D\u044B\u043C\u0438, \u043A\u043E\u0442\u043E\u0440\u044B\u0445 \u0436\u0430\u043B\u043A\u043E \u0438 \u043A\u043E\u0442\u043E\u0440\u044B\u0445 \u043D\u0435 \u0436\u0430\u043B\u043A\u043E \u0441\u043E\u0432\u0441\u0435\u043C. \u0422\u0443\u0442 \u0436\u0435 \u0440\u044F\u0434\u043E\u043C \u0438 \u0441\u0430\u043C\u043E\u0437\u0430\u0431\u0432\u0435\u043D\u043D\u043E\u0435 \u043F\u043E\u0447\u0438\u0442\u0430\u043D\u0438\u0435 \u0441\u043E\u0431\u0441\u0442\u0432\u0435\u043D\u043D\u044B\u0445 \u0442\u0440\u0430\u0434\u0438\u0446\u0438\u0439 \u0441 \u0433\u043B\u0443\u0431\u043E\u043A\u0438\u043C \u0441\u043C\u044B\u0441\u043B\u043E\u043C \u0438 \u0437\u0430\u0434\u0430\u0447\u0430\u043C\u0438 \u0441 \u043E\u0434\u043D\u043E\u0432\u0440\u0435\u043C\u0435\u043D\u043D\u044B\u043C \u043F\u043E\u0432\u0435\u0440\u0445\u043D\u043E\u0441\u0442\u043D\u044B\u043C \u043F\u043E\u0440\u0438\u0446\u0430\u043D\u0438\u0435\u043C \u0447\u0443\u0436\u0438\u0445 \u0431\u0435\u0437 \u0440\u0430\u0437\u0431\u043E\u0440\u0430 \u0433\u043B\u0443\u0431\u0438\u043D\u043D\u043E\u0433\u043E \u0441\u043C\u044B\u0441\u043B\u0430.</p><p>\u042F \u043D\u0435 \u043A\u0440\u0438\u0447\u0443, \u0447\u0442\u043E \u0432\u0441\u0435 \u043F\u043E\u043D\u044F\u043B. \u041D\u0430\u043F\u0440\u043E\u0442\u0438\u0432, \u0434\u043B\u044F \u043C\u0435\u043D\u044F \u043D\u0435\u044F\u0441\u043D\u043E\u0433\u043E \u0438 \u0441\u043B\u043E\u0436\u043D\u043E\u0433\u043E \u0441\u0442\u0430\u043D\u043E\u0432\u0438\u0442\u0441\u044F \u0441 \u043A\u0430\u0436\u0434\u044B\u043C \u0434\u043D\u0435\u043C \u0432\u0441\u0435 \u0431\u043E\u043B\u044C\u0448\u0435! \u042F \u043F\u0440\u043E\u0441\u0442\u043E \u0433\u043E\u0432\u043E\u0440\u044E \u043E \u0442\u043E\u043C, \u0447\u0442\u043E \u043C\u043D\u0435 \u0441\u0442\u0430\u043D\u043E\u0432\u0438\u0442\u0441\u044F \u044F\u0441\u043D\u043E \u0441 \u043C\u043E\u0435\u0439 \u0442\u043E\u0447\u043A\u0438, \u0441 \u043F\u043E\u0437\u0438\u0446\u0438\u0438 \u041D\u0435\u043F\u0440\u043E\u0442\u0438\u0432\u043E\u0440\u0435\u0447\u0438\u0432\u043E\u0441\u0442\u0438 \u0438 \u0426\u0435\u043B\u043E\u0441\u0442\u043D\u043E\u0441\u0442\u0438, \u0438\u0437 \u043F\u043E\u043F\u044B\u0442\u043E\u043A \u0441\u0444\u043E\u0440\u043C\u0438\u0440\u043E\u0432\u0430\u0442\u044C \u0432\u0441\u0435-\u0442\u0430\u043A\u0438 \u043D\u0435\u0440\u0430\u0437\u0440\u044B\u0432\u043D\u0443\u044E \u043A\u0430\u0440\u0442\u0438\u043D\u043A\u0443 \u043E\u043A\u0440\u0443\u0436\u0430\u044E\u0449\u0435\u0433\u043E \u043C\u0438\u0440\u0430, \u043F\u0443\u0441\u043A\u0430\u0439 \u0434\u0430\u0436\u0435 \u044D\u0442\u0430 \u0437\u0430\u0434\u0430\u0447\u0430 \u0438 \u043D\u0435\u0432\u044B\u043F\u043E\u043B\u043D\u0438\u043C\u0430, \u043D\u043E \u044F \u0441\u0447\u0438\u0442\u0430\u044E \u0435\u0435 \u0434\u043E\u0441\u0442\u043E\u0439\u043D\u043E\u0439 \u0434\u043B\u044F \u0442\u043E\u0433\u043E, \u0447\u0442\u043E\u0431\u044B \u0437\u0430\u043D\u0438\u043C\u0430\u0442\u044C\u0441\u044F \u044D\u0442\u0438\u043C \u0432\u0441\u044E \u0436\u0438\u0437\u043D\u044C.</p><p>\u041F\u0440\u043E\u0441\u0442\u0438\u0442\u0435 \u043C\u0435\u043D\u044F, \u0435\u0441\u043B\u0438 \u044F \u043C\u0430\u043B\u043E \u0434\u0430\u044E \u0437\u0434\u0435\u0441\u044C \u0438\u043D\u0444\u043E\u0440\u043C\u0430\u0446\u0438\u0438, \u0430 \u0442\u043E\u043B\u044C\u043A\u043E \u0433\u0430\u0436\u0443 \u0432 \u0432\u0430\u0448\u0438\u0445 \u043A\u043E\u043C\u043C\u0435\u043D\u0442\u0430\u0440\u0438\u044F\u0445 \u0441\u0432\u043E\u0438\u043C\u0438 \u0432\u044B\u043F\u0430\u0434\u0430\u043C\u0438 - \u044D\u0442\u043E \u0441\u043B\u0435\u0434\u0441\u0442\u0432\u0438\u0435 \u043C\u043E\u0435\u0439 \u0442\u0430\u043A\u043E\u0439 \u043F\u0440\u043E\u0441\u0442\u043E\u0439 \u043C\u044B\u0441\u043B\u0438, \u0447\u0442\u043E \u0438\u0434\u0435\u0438, \u043A\u043E\u0442\u043E\u0440\u044B\u0435 \u0447\u0435\u043B\u043E\u0432\u0435\u043A \u043F\u043E\u043D\u044F\u043B \u0441\u0430\u043C\u043E\u0441\u0442\u043E\u044F\u0442\u0435\u043B\u044C\u043D\u043E, \u0431\u0443\u0434\u0443\u0447\u0438 \u043B\u0438\u0448\u044C \u043A\u0440\u0430\u0435\u0448\u043A\u043E\u043C \u0437\u0430\u0446\u0435\u043F\u043B\u0435\u043D\u043D\u044B\u043C \u043A\u0430\u043A\u043E\u0439-\u0442\u043E \u0444\u0440\u0430\u0437\u043E\u0439, \u043A\u0443\u0434\u0430 \u0446\u0435\u043D\u043D\u0435\u0435 \u0434\u043B\u044F \u043D\u0435\u0433\u043E, \u0447\u0435\u043C \u043F\u0440\u043E\u0441\u0442\u043E \u0440\u0430\u0437\u0436\u0435\u0432\u0430\u043D\u043D\u044B\u0435 \u043F\u043E\u0440\u0446\u0438\u0438 \u0438\u043D\u0444\u044B. \u042F \u043D\u0435 \u0437\u043E\u0432\u0443 \u0432\u0430\u0441 \u043D\u0438\u043A\u0443\u0434\u0430, \u043F\u0440\u043E\u0441\u0442\u043E \u043F\u0440\u043E\u0448\u0443 \u0432\u043D\u0438\u043C\u0430\u0442\u0435\u043B\u044C\u043D\u043E \u0441\u043C\u043E\u0442\u0440\u0435\u0442\u044C \u043F\u043E\u0434 \u043D\u043E\u0433\u0438 \u0438 \u0434\u0443\u043C\u0430\u0442\u044C \u0441\u0430\u043C\u0438\u043C, \u043A\u0443\u0434\u0430 \u0438 \u043A\u0430\u043A \u0438\u0434\u0442\u0438.</p><p>\u041D\u0430 \u043F\u043E\u0441\u043B\u0435\u0434\u043E\u043A \u043F\u0440\u0438\u0432\u0435\u0434\u0443 \u043D\u0435\u043C\u043D\u043E\u0433\u043E \u0438\u043D\u0444\u044B \u043F\u0440\u043E \u0442\u0435\u0445 &quot;\u0434\u0435\u043B\u044C\u0444\u0438\u043D\u0447\u0438\u043A\u043E\u0432&quot; \u0432 \u0414\u0430\u043D\u0438\u0438. &quot;\u043F\u0440\u043E\u0431\u043B\u0435\u043C\u0430&quot;: <a href="http://www.lookatme.ru/flows/obschestvo/posts/57469-ubiystvo-delfinov-v-" class="external-link" target="_blank" rel="noopener noreferrer">http://www.lookatme.ru/flows/obschestvo/posts/57469-ubiystvo-delfinov-v-</a>...</p><p>\u0418 \u0442\u043E, \u0431\u0435\u0437 \u0437\u043D\u0430\u043D\u0438\u044F \u0447\u0435\u0433\u043E \u043D\u0435 \u0441\u0442\u043E\u0438\u0442 \u043D\u0438\u0447\u0435\u0433\u043E \u043F\u0440\u043E \u044D\u0442\u043E \u0437\u0430\u044F\u0432\u043B\u044F\u0442\u044C: &quot;\u0411\u043E\u043B\u044C\u0448\u0443\u044E \u0447\u0430\u0441\u0442\u044C \u0442\u0440\u0430\u0434\u0438\u0446\u0438\u043E\u043D\u043D\u043E\u0433\u043E \u0440\u0430\u0446\u0438\u043E\u043D\u0430 \u0444\u0430\u0440\u0435\u0440\u0446\u0435\u0432 \u0441\u043E\u0441\u0442\u0430\u0432\u043B\u044F\u0435\u0442 \u043C\u044F\u0441\u043E. \u0418\u0437-\u0437\u0430 \u0436\u0435\u0441\u0442\u043A\u043E\u0433\u043E \u043A\u043B\u0438\u043C\u0430\u0442\u0430 \u043D\u0430 \u043E\u0441\u0442\u0440\u043E\u0432\u0430\u0445 \u043F\u0440\u0430\u043A\u0442\u0438\u0447\u0435\u0441\u043A\u0438 \u043D\u0435\u0432\u043E\u0437\u043C\u043E\u0436\u043D\u043E \u0432\u044B\u0440\u0430\u0449\u0438\u0432\u0430\u0442\u044C \u043E\u0432\u043E\u0449\u0438 \u0438 \u0437\u0435\u0440\u043D\u043E\u0432\u044B\u0435. \u0412 \u0437\u0438\u043C\u043D\u0438\u0435 \u043C\u0435\u0441\u044F\u0446\u044B \u0435\u0434\u0438\u043D\u0441\u0442\u0432\u0435\u043D\u043D\u044B\u043C \u0441\u043F\u043E\u0441\u043E\u0431\u043E\u043C \u0432\u044B\u0436\u0438\u0432\u0430\u043D\u0438\u044F \u0434\u043B\u044F \u043E\u0441\u0442\u0440\u043E\u0432\u0438\u0442\u044F\u043D \u0431\u044B\u043B\u043E \u043F\u043E\u0442\u0440\u0435\u0431\u043B\u0435\u043D\u0438\u0435 \u0441\u043E\u043B\u0451\u043D\u043E\u0439 \u0438 \u0432\u044F\u043B\u0435\u043D\u043E\u0439 \u043F\u0438\u0449\u0438, \u0432\u043A\u043B\u044E\u0447\u0430\u044F \u043C\u044F\u0441\u043E \u0438 \u0436\u0438\u0440 \u0433\u0440\u0438\u043D\u0434, \u0431\u0430\u0440\u0430\u043D\u0438\u043D\u0443, \u043C\u044F\u0441\u043E \u043C\u043E\u0440\u0441\u043A\u0438\u0445 \u043F\u0442\u0438\u0446 \u0438 \u0440\u044B\u0431\u0443. \u0412 \u0442\u0435\u0447\u0435\u043D\u0438\u0435 \u0432\u0435\u043A\u043E\u0432 \u0433\u0440\u0438\u043D\u0434\u044B \u0441\u043E\u0441\u0442\u0430\u0432\u043B\u044F\u043B\u0438 \u0432\u0430\u0436\u043D\u044B\u0439 \u0438\u0441\u0442\u043E\u0447\u043D\u0438\u043A \u043F\u0438\u0449\u0438 \u0438 \u0432\u0438\u0442\u0430\u043C\u0438\u043D\u043E\u0432 \u0434\u043B\u044F \u0438\u0437\u043E\u043B\u0438\u0440\u043E\u0432\u0430\u043D\u043D\u043E\u0433\u043E \u043D\u0430\u0441\u0435\u043B\u0435\u043D\u0438\u044F \u0441\u0435\u0432\u0435\u0440\u043E\u0430\u0442\u043B\u0430\u043D\u0442\u0438\u0447\u0435\u0441\u043A\u043E\u0433\u043E \u0430\u0440\u0445\u0438\u043F\u0435\u043B\u0430\u0433\u0430.</p><p>\u041C\u044F\u0441\u043E \u0438 \u0436\u0438\u0440 \u0433\u0440\u0438\u043D\u0434 \u0445\u0440\u0430\u043D\u044F\u0442, \u043F\u0440\u0438\u0433\u043E\u0442\u0430\u0432\u043B\u0438\u0432\u0430\u044E\u0442 \u0438 \u0441\u044A\u0435\u0434\u0430\u044E\u0442 \u043D\u0435\u043F\u043E\u0441\u0440\u0435\u0434\u0441\u0442\u0432\u0435\u043D\u043D\u043E \u0432 \u0441\u0435\u043C\u044C\u044F\u0445, \u043A\u0438\u0442\u043E\u0432\u043E\u0435 \u043C\u044F\u0441\u043E \u043D\u0435 \u043F\u0440\u043E\u0434\u0430\u0451\u0442\u0441\u044F \u0432 \u043C\u0430\u0433\u0430\u0437\u0438\u043D\u0430\u0445. \u0425\u043E\u0442\u044F \u0433\u043B\u0430\u0432\u043D\u044B\u043C \u044D\u043A\u0441\u043F\u043E\u0440\u0442\u0438\u0440\u0443\u0435\u043C\u044B\u043C \u0442\u043E\u0432\u0430\u0440\u043E\u043C \u0424\u0430\u0440\u0435\u0440\u0441\u043A\u0438\u0445 \u043E\u0441\u0442\u0440\u043E\u0432\u043E\u0432 \u044F\u0432\u043B\u044F\u0435\u0442\u0441\u044F \u0440\u044B\u0431\u0430, \u043C\u044F\u0441\u043E \u0438 \u0436\u0438\u0440 \u0433\u0440\u0438\u043D\u0434 \u043D\u0435 \u044D\u043A\u0441\u043F\u043E\u0440\u0442\u0438\u0440\u0443\u0435\u0442\u0441\u044F. \u0415\u0436\u0435\u0433\u043E\u0434\u043D\u0430\u044F \u0434\u043E\u0431\u044B\u0447\u0430 \u0433\u0440\u0438\u043D\u0434, \u0441\u043E\u0441\u0442\u0430\u0432\u043B\u044F\u044E\u0449\u0430\u044F 956 \u0433\u043E\u043B\u043E\u0432[12] (1990\u20141999) \u043F\u0440\u0438\u043C\u0435\u0440\u043D\u043E \u0441\u043E\u043E\u0442\u0432\u0435\u0442\u0441\u0442\u0432\u0443\u0435\u0442 500 \u0442\u043E\u043D\u043D\u0430\u043C \u043C\u044F\u0441\u0430 \u0438 \u0436\u0438\u0440\u0430, \u0447\u0442\u043E \u0441\u043E\u0441\u0442\u0430\u0432\u043B\u044F\u0435\u0442 \u043E\u043A\u043E\u043B\u043E 30 % \u043F\u0440\u043E\u0438\u0437\u0432\u043E\u0434\u0438\u043C\u043E\u0433\u043E \u043D\u0430 \u043E\u0441\u0442\u0440\u043E\u0432\u0430\u0445 \u043C\u044F\u0441\u0430&quot; \u0418 &quot;\u0414\u0430 \u043D\u0435\u0442, \u043E\u043D\u0438 \u043A\u0430\u043A \u0440\u0430\u0437 \u0438\u0445 \u0435\u0434\u044F\u0442. \u0418 \u043D\u0435 \u043F\u0440\u043E\u0441\u0442\u043E \u0435\u0434\u044F\u0442. \u041E\u043D\u0438 \u0435\u0434\u044F\u0442 \u0438\u0445 \u0443\u0436\u0435 \u0431\u043E\u043B\u044C\u0448\u0435 1000 \u043B\u0435\u0442. \u041C\u0430\u043B\u043E \u0442\u043E\u0433\u043E, \u0443 \u0442\u0430\u043A\u043E\u0439 &quot;\u0434\u0435\u0439\u0441\u0442\u0432\u0430&quot; \u0435\u0441\u0442\u044C \u0438 \u0435\u0449\u0435 \u043E\u0434\u043D\u043E \u0440\u0430\u0446\u0438\u043E\u043D\u0430\u043B\u044C\u043D\u043E\u0435 \u043E\u0431\u044A\u044F\u0441\u043D\u0435\u043D\u0438\u0435 - \u044D\u0442\u043E \u0434\u0435\u043B\u0430\u0435\u0442\u0441\u044F \u0440\u0430\u0437 \u0432 \u0433\u043E\u0434, \u0441\u043F\u0435\u0446\u0438\u0430\u043B\u044C\u043D\u043E \u0434\u043B\u044F \u0440\u0435\u0433\u0443\u043B\u0438\u0440\u043E\u0432\u0430\u043D\u0438\u044F \u043F\u043E\u0433\u043E\u043B\u043E\u0432\u044C\u044F \u044D\u0442\u0438\u0445 \u0434\u0435\u043B\u044C\u0444\u0438\u043D\u043E\u0432. \u0414\u0435\u043B\u043E \u0432 \u0442\u043E\u043C, \u0447\u0442\u043E &quot;\u043E\u0441\u0442\u0440\u043E\u0432\u0438\u0442\u044F\u043D\u0435&quot; \u0435\u0434\u044F\u0442 \u0432 \u043E\u0441\u043D\u043E\u0432\u043D\u043E\u043C \u0440\u044B\u0431\u0443. \u0422\u0430\u043A \u0441\u043E\u0432\u043F\u0430\u043B\u043E, \u0447\u0442\u043E \u0438 \u0434\u0435\u043B\u044C\u0444\u0438\u043D\u044B \u0435\u0434\u044F\u0442 \u0432 \u043E\u0441\u043D\u043E\u0432\u043D\u043E\u043C \u0440\u044B\u0431\u0443. \u041F\u0440\u0438 \u0443\u0432\u0435\u043B\u0438\u0447\u0435\u043D\u0438\u0438 \u043A\u043E\u043B\u0438\u0447\u0435\u0441\u0442\u0432\u0430 \u0434\u0435\u043B\u044C\u0444\u0438\u043D\u043E\u0432 \u0441\u043D\u0438\u0436\u0430\u0435\u0442\u0441\u044F \u043A\u043E\u043B\u0438\u0447\u0435\u0441\u0442\u0432\u043E \u0440\u044B\u0431\u044B. \u0422\u043E \u0435\u0441\u0442\u044C - \u043D\u0435 \u0443\u0431\u0438\u0432\u0430\u044F \u0434\u0435\u043B\u044C\u0444\u0438\u043D\u043E\u0432, \u0436\u0438\u0442\u0435\u043B\u0438 \u043E\u0441\u0442\u0440\u043E\u0432\u043E\u0432 \u043E\u0441\u0442\u0430\u043D\u0443\u0442\u0441\u044F \u0438 \u0431\u0435\u0437 \u0434\u0435\u043B\u044C\u0444\u0438\u043D\u044C\u0435\u0433\u043E \u043C\u044F\u0441\u0430, \u0438 \u0431\u0435\u0437 \u0440\u044B\u0431\u044B. \u041F\u0440\u043E\u0449\u0435 \u0433\u043E\u0432\u043E\u0440\u044F, \u043F\u043E\u043C\u0440\u0443\u0442 \u0441 \u0433\u043E\u043B\u043E\u0434\u0430. \u0412\u043E\u043F\u0440\u043E\u0441 \u0432 \u0442\u043E\u043C, \u043A\u0430\u043A\u0438\u043C \u0441\u043F\u043E\u0441\u043E\u0431\u043E\u043C \u044D\u0442\u043E \u0434\u0435\u043B\u0430\u0435\u0442\u0441\u044F... \u0410 \u043A\u0430\u043A\u0438\u043C \u0441\u043F\u043E\u0441\u043E\u0431\u043E\u043C \u0432\u044B \u043F\u0440\u0435\u0434\u043B\u0430\u0433\u0430\u0435\u0442\u0435 \u043B\u043E\u0432\u0438\u0442\u044C \u0434\u0435\u043B\u044C\u0444\u0438\u043D\u043E\u0432? \u0421\u0442\u0440\u0435\u043B\u044F\u0442\u044C \u0438\u0437 \u0440\u0443\u0436\u044C\u044F? \u0422\u0440\u0430\u0432\u0438\u0442\u044C \u044F\u0434\u043E\u043C? \u041A\u0430\u043F\u043A\u0430\u043D\u044B \u0441\u0442\u0430\u0432\u0438\u0442\u044C? \u0422\u0443\u0442 \u0438 \u0432\u0430\u0440\u0438\u0430\u043D\u0442\u043E\u0432-\u0442\u043E \u043E\u0441\u043E\u0431\u043E \u043D\u0435\u043C\u043D\u043E\u0433\u043E...\u041C\u0435\u043B\u043A\u0430\u044F \u0440\u044B\u0431\u0430 \u043B\u043E\u0432\u0438\u0442\u0441\u044F \u043D\u0430 \u0443\u0434\u043E\u0447\u043A\u0443 \u0438\u043B\u0438 \u0441\u0435\u0442\u044C\u044E, \u043A\u0440\u0443\u043F\u043D\u0430\u044F - \u0433\u0430\u0440\u043F\u0443\u043D\u0430\u043C\u0438. \u042D\u0442\u0438 \u043A\u0440\u044E\u043A\u0438 - \u0442\u0435 \u0436\u0435 \u0433\u0430\u0440\u043F\u0443\u043D\u044B. \u0418 \u043D\u0438\u043A\u0430\u043A\u0438\u0435 \u044D\u0442\u043E \u043D\u0435 &quot;\u043F\u043E\u0434\u0440\u043E\u0441\u0442\u043A\u0438&quot;, \u0438 \u043D\u0438\u043A\u0430\u043A\u043E\u0435 \u044D\u0442\u043E \u043D\u0435 \u0445\u0432\u0430\u0441\u0442\u043E\u0432\u0441\u0442\u0432\u043E. \u042D\u0442\u0438 \u0434\u0435\u043B\u044C\u0444\u0438\u043D\u044B \u043D\u0435 \u0441\u0430\u043C\u0438 \u043F\u0440\u0438\u043F\u043B\u044B\u043B\u0438 \u043A \u0431\u0435\u0440\u0435\u0433\u0443 - \u0438\u0445 \u0437\u0430\u0433\u043E\u043D\u044F\u043B\u0438 \u0441\u043F\u0435\u0446\u0438\u0430\u043B\u044C\u043D\u044B\u0435 \u0441\u0443\u0434\u0430, \u043A\u043E\u0442\u043E\u0440\u044B\u0435 \u0432\u0438\u0434\u043D\u044B \u043D\u0430 \u043D\u0435\u043A\u043E\u0442\u043E\u0440\u044B\u0445 \u0444\u043E\u0442\u043E\u0433\u0440\u0430\u0444\u0438\u044F\u0445. \u042D\u0442\u043E \u043E\u0444\u0438\u0446\u0438\u0430\u043B\u044C\u043D\u043E \u0440\u0430\u0437\u0440\u0435\u0448\u0435\u043D\u043D\u043E\u0435 \u043C\u0435\u0440\u043E\u043F\u0440\u0438\u044F\u0442\u0438\u0435, \u0446\u0435\u043B\u044C \u043A\u043E\u0442\u043E\u0440\u043E\u0433\u043E, \u0432 \u043A\u043E\u043D\u0435\u0447\u043D\u043E\u043C \u0441\u0447\u0435\u0442\u0435 - \u0432\u044B\u0436\u0438\u0432\u0430\u043D\u0438\u0435 \u0441\u0430\u043C\u0438\u0445 \u043E\u0441\u0442\u0440\u043E\u0432\u0438\u0442\u044F\u043D. \u041A\u0441\u0442\u0430\u0442\u0438, \u0442\u0443\u0442 \u043A\u0442\u043E-\u0442\u043E \u0441\u043E\u0432\u0435\u0440\u0448\u0435\u043D\u043D\u043E \u043F\u0440\u0430\u0432\u0438\u043B\u044C\u043D\u043E \u043D\u0430\u043F\u0438\u0441\u0430\u043B - \u0442\u043E \u0436\u0435 \u0441\u0430\u043C\u043E\u0435 \u043F\u0440\u043E\u0438\u0441\u0445\u043E\u0434\u0438\u0442 \u0438 \u0432 \u042F\u043F\u043E\u043D\u0438\u0438. \u0418 \u0446\u0435\u043B\u044C \u0442\u0430\u043C \u0442\u043E\u0436\u0435 - \u0434\u0430\u043B\u0435\u043A\u043E \u043D\u0435 \u0440\u0430\u0437\u0432\u043B\u0435\u0447\u044C\u0441\u044F. \u0410 \u0432\u043E\u0442 \u0441\u0430\u043C\u043E \u0437\u0440\u0435\u043B\u0438\u0449\u0435...\u0414\u0430, \u0443\u0436\u0430\u0441\u043D\u043E. \u041F\u043E\u0442\u043E\u043C\u0443 \u043A\u0430\u043A \u0434\u0435\u043B\u044C\u0444\u0438\u043D \u0432 \u043D\u0430\u0448\u0435\u043C, \u0440\u043E\u0441\u0441\u0438\u0439\u0441\u043A\u043E\u043C (\u0438 \u043D\u0435 \u0442\u043E\u043B\u044C\u043A\u043E) \u0432\u043E\u0441\u043F\u0440\u0438\u044F\u0442\u0438\u0438 - \u043D\u0435\u043A\u043E\u0435 \u0434\u043E\u0431\u0440\u043E\u0435, \u0431\u0435\u0437\u043E\u0431\u0438\u0434\u043D\u043E\u0435, \u0443\u043C\u043D\u043E\u0435 \u0441\u0443\u0449\u0435\u0441\u0442\u0432\u043E, \u0434\u0440\u0443\u0433 \u0447\u0435\u043B\u043E\u0432\u0435\u043A\u0430, \u043F\u043E\u0447\u0442\u0438 \u0441\u043E\u0431\u0430\u043A\u0430. \u041D\u043E \u0432\u0435\u0434\u044C \u0438 \u0441\u043E\u0431\u0430\u043A \u043A\u043E\u0435-\u0433\u0434\u0435 \u0435\u0434\u044F\u0442. \u0410 \u0441 \u0434\u0440\u0443\u0433\u043E\u0439 \u0441\u0442\u043E\u0440\u043E\u043D\u044B - \u043A\u043E\u0440\u043E\u0432\u044B \u0442\u043E\u0436\u0435 \u0431\u0435\u0437\u043E\u0431\u0438\u0434\u043D\u044B\u0435 \u0438 \u0434\u043E\u0431\u0440\u044B\u0435. \u041C\u043E\u0436\u0435\u0442 \u0431\u044B\u0442\u044C \u0433\u0434\u0435-\u0442\u043E \u0434\u0430\u0436\u0435 \u0443\u043C\u043D\u044B\u0435. \u041D\u043E \u0438\u0445 \u0432 \u0420\u043E\u0441\u0441\u0438\u0438 \u0443\u0431\u0438\u0432\u0430\u0435\u0442\u0441\u044F \u043A\u0443\u0434\u0430 \u0431\u043E\u043B\u044C\u0448\u0435, \u0447\u0435\u043C \u0434\u0435\u043B\u044C\u0444\u0438\u043D\u043E\u0432 \u043D\u0430 \u0424\u0430\u0440\u0435\u0440\u0430\u0445. \u0410 \u0432\u043E \u043C\u043D\u043E\u0433\u0438\u0445 \u0448\u0442\u0430\u0442\u0430\u0445 \u0418\u043D\u0434\u0438\u0438 \u0437\u0430 \u0442\u043E \u0436\u0435 \u0443\u0431\u0438\u0439\u0441\u0442\u0432\u043E \u043A\u043E\u0440\u043E\u0432\u044B \u043C\u043E\u0436\u043D\u043E \u0438 \u043D\u0430 \u043D\u0430\u0440\u0430\u0445 \u043F\u043E\u0447\u0430\u043B\u0438\u0442\u044C\u0441\u044F ) \u0412 \u043E\u0431\u0449\u0435\u043C, \u0443\u0432\u0430\u0436\u0430\u0439\u0442\u0435 \u0447\u0443\u0436\u0438\u0435 \u0442\u0440\u0430\u0434\u0438\u0446\u0438\u0438, \u043A\u0430\u043A\u0438\u043C\u0438 \u0431\u044B \u0441\u0442\u0440\u0430\u043D\u043D\u044B\u043C\u0438 \u043E\u043D\u0438 \u0432\u0430\u043C \u043D\u0435 \u043A\u0430\u0437\u0430\u043B\u0438\u0441\u044C. \u041F\u043E\u0442\u043E\u043C\u0443 \u043A\u0430\u043A \u0438 \u043D\u0430\u0448\u0438 \u0441 \u0432\u0430\u043C\u0438 \u043E\u0431\u044B\u0447\u0430\u0438 \u0434\u0430\u043B\u0435\u043A\u043E \u043D\u0435 \u0432\u0441\u0435\u0433\u0434\u0430 \u0438 \u043D\u0435 \u0432\u0441\u0435\u043C \u043F\u043E \u0434\u0443\u0448\u0435.&quot; \u0413\u043B\u0430\u0432\u043D\u043E\u0435, \u0447\u0442\u043E \u044F \u0434\u043B\u044F \u0441\u0435\u0431\u044F \u043E\u043F\u0440\u0435\u0434\u0435\u043B\u0438\u043B \u0442\u043E\u0447\u043D\u043E, \u0442\u0430\u043A \u044D\u0442\u043E \u0442\u043E, \u0447\u0442\u043E \u041D\u0418 \u041E\u0414\u041D\u041E \u0416\u0418\u0412\u041E\u0422\u041D\u041E\u0415 \u041D\u0415 \u041C\u041E\u0416\u0415\u0422 \u0411\u042B\u0422\u042C \u0412\u0410\u0416\u041D\u0415\u0415 \u0427\u0415\u041B\u041E\u0412\u0415\u041A\u0410. \u0414\u0430\u0436\u0435 \u0442\u044B\u0441\u044F\u0447\u0430 \u0434\u0435\u043B\u044C\u0444\u0438\u043D\u043E\u0432 \u043C\u0435\u043D\u0435\u0435 \u0432\u0430\u0436\u043D\u0430, \u0447\u0435\u043C \u0436\u0438\u0437\u043D\u044C \u043E\u0434\u043D\u043E\u0433\u043E \u0435\u0434\u0438\u043D\u0441\u0442\u0432\u0435\u043D\u043D\u043E\u0433\u043E \u0447\u0435\u043B\u043E\u0432\u0435\u043A\u0430. \u041E\u0431\u043E \u0432\u0441\u0435\u043C \u043E\u0441\u0442\u0430\u043B\u044C\u043D\u043E\u043C \u043C\u043E\u0436\u043D\u043E \u0441\u043F\u043E\u0440\u0438\u0442\u044C \u0431\u0435\u0441\u043A\u043E\u043D\u0435\u0447\u043D\u043E, \u043A\u0442\u043E \u0443\u043C\u043D\u0435\u0435 \u0438 \u043A\u0442\u043E \u043C\u0438\u043B\u0435\u0435 - \u0431\u0435\u043B\u044F\u0447\u043E\u043A, \u0434\u0435\u043B\u044C\u0444\u0438\u043D \u0438\u043B\u0438 \u043F\u0430\u043D\u0434\u0430.</p><p>\u041B\u044E\u0431\u0438\u0442\u0435 \u043B\u044E\u0434\u0435\u0439 - \u044D\u0442\u043E \u0433\u043B\u0430\u0432\u043D\u043E\u0435, \u043E\u0441\u0442\u0430\u043B\u044C\u043D\u043E\u0435 \u043C\u044B \u0443\u0436\u0435 \u0432\u043C\u0435\u0441\u0442\u0435 \u0441\u043C\u043E\u0436\u0435\u043C \u0441\u0434\u0435\u043B\u0430\u0442\u044C \u0445\u043E\u0440\u043E\u0448\u043E \u0438 \u043F\u0440\u0430\u0432\u0438\u043B\u044C\u043D\u043E. \u042F \u043B\u044E\u0431\u043B\u044E \u0432\u0430\u0441!</p><p><a href="https://vk.com/wall129086_5444" class="external-link" target="_blank" rel="noopener noreferrer">2010-12-08</a></p>',16),s=[a];function u(n,_,l,c,i,q){return e(),o("div",null,s)}var h=t(r,[["render",u]]);export{v as __pageData,h as default};
