import{_ as e,c as p,o as t,d as a}from"./app.d5474651.js";const m=JSON.parse('{"title":"\u041E\u0431 \u043E\u0431\u0449\u0435\u043C \u043C\u0435\u0441\u0442\u0435","description":"","frontmatter":{"title":"\u041E\u0431 \u043E\u0431\u0449\u0435\u043C \u043C\u0435\u0441\u0442\u0435","tags":["writingArchive"],"date":"2011-06-08T00:00:00.000Z"},"headers":[{"level":3,"title":"\u0414\u0440\u0443\u0437\u044C\u044F!","slug":"\u0434\u0440\u0443\u0437\u044C\u044F","link":"#\u0434\u0440\u0443\u0437\u044C\u044F","children":[]}],"relativePath":"art/writing/archive/place.md"}'),r={name:"art/writing/archive/place.md"},c=a('<h3 id="\u0434\u0440\u0443\u0437\u044C\u044F" tabindex="-1">\u0414\u0440\u0443\u0437\u044C\u044F! <a class="header-anchor" href="#\u0434\u0440\u0443\u0437\u044C\u044F" aria-hidden="true">#</a></h3><p>\u0425\u043E\u0447\u0443 \u0440\u0430\u0441\u0441\u043A\u0430\u0437\u0430\u0442\u044C \u0432\u0430\u043C \u043E \u041C\u0435\u0441\u0442\u0430\u0445. \u0414\u0443\u043C\u0430\u044E \u043C\u043D\u043E\u0433\u0438\u0435 \u0443\u0437\u043D\u0430\u044E\u0442 \u044D\u0442\u0443 \u0438\u0441\u0442\u043E\u0440\u0438\u044E \u0438 \u0432 \u0441\u0432\u043E\u0435\u0439 \u0436\u0438\u0437\u043D\u0438. \u041C\u043D\u043E\u0433\u0438\u0435 \u043D\u0430\u0447\u043D\u0443\u0442 \u043A\u0438\u0432\u0430\u0442\u044C \u0432 \u043C\u043E\u043D\u0438\u0442\u043E\u0440 \u0443\u0436\u0435 \u0441 \u043F\u0435\u0440\u0432\u044B\u0445 \u0441\u0442\u0440\u043E\u043A, \u0443\u0432\u0435\u0440\u0435\u043D.</p><p>\u0423 \u043C\u0435\u043D\u044F, \u043D\u0435\u0442, \u0443 \u043D\u0430\u0441 \u0434\u0430\u0432\u043D\u043E \u0438, \u043A\u0430\u043A \u0431\u0443\u0434\u0442\u043E \u0431\u044B, \u0432\u0441\u0435\u0433\u0434\u0430 \u0431\u044B\u043B\u0438 \u041C\u0435\u0441\u0442\u0430. \u041C\u0435\u0441\u0442\u0430, \u043A\u0443\u0434\u0430 \u043C\u043E\u0436\u043D\u043E \u043D\u0435 \u0437\u0430\u0434\u0443\u043C\u044B\u0432\u0430\u044F\u0441\u044C \u043F\u043E\u0439\u0442\u0438 \u0438\u043B\u0438 \u043F\u043E\u0435\u0445\u0430\u0442\u044C, \u043A\u043E\u0433\u0434\u0430 \u043D\u0435\u0447\u0435\u0433\u043E \u0434\u0435\u043B\u0430\u0442\u044C, \u043A\u043E\u0433\u0434\u0430 \u0435\u0441\u0442\u044C \u0441\u0432\u043E\u0431\u043E\u0434\u043D\u043E\u0435 \u0432\u0440\u0435\u043C\u044F \u0438 \u0445\u043E\u0447\u0435\u0442\u0441\u044F \u043F\u0440\u043E\u0432\u0435\u0441\u0442\u0438 \u0435\u0433\u043E \u0432\u043C\u0435\u0441\u0442\u0435 \u0441 \u043A\u0435\u043C-\u0442\u043E, \u043D\u043E \u043D\u0435 \u0437\u043D\u0430\u0435\u0448\u044C \u0442\u043E\u0447\u043D\u043E \u0441 \u043A\u0435\u043C. \u0421 \u043A\u0435\u043C-\u0442\u043E \u0431\u043B\u0438\u0437\u043A\u0438\u043C, \u0437\u043D\u0430\u043A\u043E\u043C\u044B\u043C \u0438\u043B\u0438 \u043D\u0430\u043E\u0431\u043E\u0440\u043E\u0442 \u0441 \u043D\u043E\u0432\u044B\u043C \u0438 \u0441\u043E\u0432\u0435\u0440\u0448\u0435\u043D\u043D\u043E \u043D\u0435\u0438\u0437\u0432\u0435\u0441\u0442\u043D\u044B\u043C \u0447\u0435\u043B\u043E\u0432\u0435\u043A\u043E\u043C. \u041D\u043E \u0447\u0442\u043E\u0431\u044B \u044D\u0442\u043E \u0431\u044B\u043B \u0442\u0432\u043E\u0439 \u0447\u0435\u043B\u043E\u0432\u0435\u043A. \u041D\u0435 \u0431\u0430\u0431\u0443\u043B\u044F \u0432 \u043E\u0447\u0435\u0440\u0435\u0434\u0438, \u043D\u0435 \u0440\u0430\u0437\u0433\u043E\u0432\u043E\u0440\u0447\u0438\u0432\u044B\u0439 \u0442\u0430\u043A\u0441\u0438\u0441\u0442, \u043D\u0435 \u0431\u0430\u0440\u043C\u0435\u043D, \u043D\u0435 \u043A\u0443\u0447\u0430 \u0442\u043E\u0440\u0433\u0443\u044E\u0449\u0438\u0445 \u043B\u0438\u0446\u0430\u043C\u0438 \u0442\u0435\u043B\u043E\u0447\u0435\u043A \u0432 \u044E\u0431\u043A\u0430\u0445 \u0438 \u043F\u0430\u0440\u043D\u0435\u0439 \u0432 \u043E\u0431\u043B\u0435\u0433\u0430\u044E\u0449\u0438\u0445 \u0448\u043C\u043E\u0442\u043A\u0430\u0445 \u043F\u043E\u0434 \u0433\u0440\u043E\u043C\u043A\u0443\u044E \u043C\u0443\u0437\u044B\u043A\u0443, \u0430 \u043F\u0440\u043E\u0441\u0442\u043E \u043B\u044E\u0434\u0438, \u043A\u043E\u0442\u043E\u0440\u044B\u043C \u0442\u0430\u043A\u0436\u0435 \u043D\u0435\u0447\u0435\u0433\u043E \u0434\u0435\u043B\u0430\u0442\u044C \u0438 \u0445\u043E\u0447\u0435\u0442\u0441\u044F \u043F\u0440\u043E\u0432\u0435\u0441\u0442\u0438 \u0435\u0433\u043E \u0432\u043C\u0435\u0441\u0442\u0435 \u0441 \u0442\u043E\u0431\u043E\u0439 \u0438\u043B\u0438 \u0442\u0432\u043E\u0438\u043C \u0434\u0440\u0443\u0433\u043E\u043C \u043A\u0430\u043A\u0438\u043C-\u043D\u0438\u0431\u0443\u0434\u044C, \u043D\u0430\u043F\u0440\u0438\u043C\u0435\u0440.</p><p>\u0421\u0430\u043C\u044B\u043C \u043F\u0435\u0440\u0432\u044B\u043C \u0442\u0430\u043A\u0438\u043C \u041C\u0435\u0441\u0442\u043E\u043C \u0431\u044B\u043B\u0430, \u043A\u043E\u043D\u0435\u0447\u043D\u043E \u0436\u0435, \u0448\u043A\u043E\u043B\u0430 \u0438 \u043F\u0440\u043E\u0434\u043B\u0435\u043D\u043A\u0430, \u0433\u0434\u0435 \u0442\u044B \u0432\u0441\u0435\u0433\u0434\u0430 \u0432 \u043A\u0443\u0447\u0435 \u0442\u0430\u043A\u0438\u0445 \u0436\u0435 \u043A\u0430\u043A \u0442\u044B \u043B\u044E\u0434\u0435\u0439 \u0432\u0441\u0435\u0433\u0434\u0430 \u043C\u043E\u0433 \u043D\u0430\u0439\u0442\u0438 \u0447\u0435\u043B\u043E\u0432\u0435\u043A\u0430, \u0433\u043E\u0442\u043E\u0432\u043E\u0433\u043E \u0440\u0430\u0437\u0434\u0435\u043B\u0438\u0442\u044C \u043B\u044E\u0431\u0443\u044E \u0442\u0432\u043E\u044E \u0437\u0430\u0442\u0435\u044E \u0438\u043B\u0438 \u043F\u0440\u0435\u0434\u043B\u043E\u0436\u0438\u0442\u044C \u0441\u0432\u043E\u044E. \u0417\u043E\u043B\u043E\u0442\u044B\u0435 \u0432\u0440\u0435\u043C\u0435\u043D\u0430 \u0441\u043E\u0432\u043C\u0435\u0449\u0435\u043D\u0438\u044F \u043E\u0431\u044F\u0437\u0430\u043D\u043D\u043E\u0441\u0442\u0435\u0439 \u0438 \u043F\u043E\u0442\u0440\u0435\u0431\u043D\u043E\u0441\u0442\u0435\u0439, \u043D\u0443 \u0432\u044B \u043F\u043E\u043D\u044F\u043B\u0438. \u041F\u043E\u0442\u043E\u043C, \u0441\u0442\u0430\u0432 \u043F\u043E\u0441\u0442\u0430\u0440\u0448\u0435 \u0438 \u043F\u043E\u0431\u0440\u0443\u0442\u0430\u043B\u044C\u043D\u0435\u0435, \u043C\u044B \u0441\u0442\u0430\u043B\u0438 \u0441\u043E\u0431\u0438\u0440\u0430\u0442\u044C\u0441\u044F \u043F\u0435\u0440\u0435\u0434 \u0448\u043A\u043E\u043B\u043E\u0439, \xAB\u043D\u0430 \u0436\u0435\u0440\u0434\u043E\u0447\u043A\u0435\xBB. \u0420\u0430\u0441\u0441\u0430\u0436\u0438\u0432\u0430\u043B\u0438\u0441\u044C \u043D\u0430 \u043D\u0435\u0432\u044B\u0441\u043E\u043A\u043E\u043C \u0437\u0430\u0431\u043E\u0440\u0447\u0438\u043A\u0435 \u043F\u0435\u0440\u0435\u0434 \u0432\u0445\u043E\u0434\u043E\u043C \u0438 \u0441\u0438\u0434\u0435\u043B\u0438 \u0442\u0430\u043C \u0434\u043E \u0441\u0430\u043C\u043E\u0439 \u0442\u0435\u043C\u043D\u043E\u0442\u044B. \u041A\u0442\u043E-\u0442\u043E \u043F\u0440\u0438\u0435\u0437\u0436\u0430\u043B \u043D\u0430 \u0432\u0435\u043B\u0438\u043A\u0435, \u043A\u0442\u043E-\u0442\u043E \u043D\u0430 \u0440\u043E\u043B\u0438\u043A\u0430\u0445, \u043A\u0442\u043E-\u0442\u043E \u043F\u0440\u043E\u0441\u0442\u043E \u0442\u0430\u043A \u0441\u0442\u043E\u044F\u043B, \u0433\u0440\u044B\u0437 \u0441\u0435\u043C\u043A\u0438, \u043D\u043E \u0432\u0441\u0435\u0445 \u043E\u0431\u044A\u0435\u0434\u0438\u043D\u044F\u043B\u043E \u043E\u0434\u043D\u043E \u0441\u043E\u0441\u0442\u043E\u044F\u043D\u0438\u0435, \u0434\u043E\u0431\u0440\u043E\u0435 \u0441\u0430\u043C\u043E\u043E\u0442\u0432\u0435\u0440\u0436\u0435\u043D\u043D\u043E\u0435 \u0431\u0435\u0437\u0434\u0435\u043B\u044C\u0435, \u043A\u043E\u0442\u043E\u0440\u044B\u043C \u0442\u0430\u043A \u0445\u043E\u0447\u0435\u0442\u0441\u044F \u043F\u043E\u0434\u0435\u043B\u0438\u0442\u044C\u0441\u044F \u0441\u043E \u0432\u0441\u0435\u043C\u0438 \u043E\u043A\u0440\u0443\u0436\u0430\u044E\u0449\u0438\u043C\u0438.</p><p>\u041F\u043E\u0442\u043E\u043C \u0431\u044B\u043B\u0438 \xAB\u043A\u0430\u0447\u0435\u043B\u044C\u043A\u0438\xBB \u043D\u0435\u043F\u043E\u0434\u0430\u043B\u0435\u043A\u0443 \u043E\u0442 \u0448\u043A\u043E\u043B\u044B, \u0433\u0434\u0435 \u0443\u0436\u0435 \u043C\u043E\u0436\u043D\u043E \u0431\u044B\u043B\u043E \u043E\u0441\u043E\u0431\u0435\u043D\u043D\u043E \u043C\u0430\u0441\u043A\u0443\u043B\u0438\u043D\u043D\u043E \u0432\u044B\u043F\u0438\u0442\u044C \u0431\u0443\u0442\u044B\u043B\u043E\u0447\u043A\u0443 \u043F\u0438\u0432\u0430, \u043F\u043E\u0433\u0440\u044B\u0437\u0442\u044C \u0441\u0435\u043C\u043E\u043A, \u043F\u043E\u043A\u0430\u0434\u0440\u0438\u0442\u044C \u0434\u0435\u0432\u0447\u043E\u043D\u043E\u043A \u0438\u0437 \u043F\u0430\u0440\u0430\u043B\u043B\u0435\u043B\u044C\u043D\u044B\u0445 \u043A\u043B\u0430\u0441\u0441\u043E\u0432, \u043E\u0431\u0441\u0443\u0434\u0438\u0442\u044C \u0441 \u043F\u0430\u0446\u0430\u043D\u0430\u043C\u0438 \u0442\u0430\u0447\u043A\u0438, \u043E\u0442\u043A\u043E\u0441\u044B \u043E\u0442 \u0430\u0440\u043C\u0438\u0438, \u043F\u043B\u0430\u043D\u044B \u043D\u0430 \u0431\u0443\u0434\u0443\u0449\u0435\u0435, \u0437\u0430\u0431\u0440\u0430\u0442\u044C\u0441\u044F \u043D\u0430 \u043A\u0440\u044B\u0448\u0443 \u0442\u0440\u0430\u043D\u0441\u0444\u043E\u0440\u043C\u0430\u0442\u043E\u0440\u043D\u043E\u0439 \u0431\u0443\u0434\u043A\u0438 \u0438\u043B\u0438 \u043C\u0438\u0440\u043D\u043E \u043F\u043E\u043A\u0430\u0447\u0430\u0442\u044C\u0441\u044F \u043D\u0430 \u043A\u0430\u0447\u0435\u043B\u044F\u0445, \u0433\u043B\u0430\u0437\u0435\u044F \u043D\u0430 \u043D\u0435\u0431\u043E, \u0431\u0443\u0434\u0442\u043E \u0431\u044B \u043E\u0434\u0438\u043D\u043E\u043A\u043E, \u043D\u043E \u0432\u0435\u0434\u044C \u0440\u044F\u0434\u043E\u043C \u0441\u043E \u0432\u0441\u0435\u043C\u0438!</p><p>\u0428\u043A\u043E\u043B\u0430 \u043A\u043E\u043D\u0447\u0438\u043B\u0430\u0441\u044C \u0438 \u043D\u043E\u0432\u044B\u043C \u041C\u0435\u0441\u0442\u043E\u043C \u0441\u0442\u0430\u043B \u0443\u043D\u0438\u0432\u0435\u0440\u0441\u0438\u0442\u0435\u0442. \u0412\u0435\u0441\u044C \u043F\u0435\u0440\u0432\u044B\u0439 \u0438 \u043F\u043E\u0447\u0442\u0438 \u0432\u0435\u0441\u044C \u0432\u0442\u043E\u0440\u043E\u0439 \u043A\u0443\u0440\u0441 \u044F \u0443\u0432\u0435\u0440\u0435\u043D\u043D\u043E \u0432\u044B\u0445\u043E\u0434\u0438\u043B \u0438\u0437 \u0443\u043D\u0438\u0432\u0435\u0440\u0430 \u043F\u043E\u0441\u043B\u0435 \u043F\u0430\u0440 \u0438 \u0437\u043D\u0430\u043B, \u0447\u0442\u043E \u0442\u0443\u0442 \u0436\u0435 \u0432\u0441\u0442\u0440\u0435\u0447\u0443 \u043A\u043E\u0433\u043E-\u043D\u0438\u0431\u0443\u0434\u044C \u0438\u0437 \u0437\u043D\u0430\u043A\u043E\u043C\u044B\u0445, \u043F\u0440\u0438\u0447\u0435\u043C \u043E\u043D \u043D\u0435 \u0431\u0443\u0434\u0435\u0442 \u0434\u0435\u043B\u043E\u0432\u0438\u0442\u043E \u043E\u0437\u0438\u0440\u0430\u0442\u044C\u0441\u044F \u0438 \u0441\u043C\u0430\u0442\u044B\u0432\u0430\u0442\u044C\u0441\u044F \u043F\u043E \u043A\u0430\u043A\u0438\u043C-\u0442\u043E \u043D\u0435 \u043E\u0447\u0435\u043D\u044C-\u0442\u043E \u0432\u0430\u0436\u043D\u044B\u043C \u0434\u0435\u043B\u0430\u043C, \u0430 \u0442\u0430\u043A\u0436\u0435 \u0443\u0432\u0435\u0440\u0435\u043D\u043D\u043E \u0431\u0443\u0434\u0435\u0442 \u0441 \u0442\u043E\u0431\u043E\u0439 \u0441\u0442\u043E\u044F\u0442\u044C, \u0431\u043E\u043B\u0442\u0430\u0442\u044C, \u0432\u0441\u0442\u0440\u0435\u0447\u0430\u0442\u044C \u0435\u0449\u0435 \u0437\u043D\u0430\u043A\u043E\u043C\u044B\u0445 \u0438\u043B\u0438 \u043D\u0435 \u043E\u0447\u0435\u043D\u044C \u043B\u044E\u0434\u0435\u0439. \u041C\u043E\u0436\u0435\u0442 \u0432\u044B \u0441\u044F\u0434\u0435\u0442\u0435 \u043D\u0430 \u0441\u043A\u0430\u043C\u0435\u0439\u043A\u0443 \u0441 \u043A\u043E\u0444\u0435\u0439\u043A\u043E\u043C, \u043C\u043E\u0436\u0435\u0442 \u0431\u044B\u0442\u044C \u043F\u043E\u0439\u0434\u0435\u0442\u0435 \u0432 \u043F\u0430\u0440\u043A \u043D\u0435\u043F\u043E\u0434\u0430\u043B\u0435\u043A\u0443, \u043C\u043E\u0436\u0435\u0442 \u0431\u044B\u0442\u044C \u043F\u0440\u043E\u0441\u0442\u043E \u0431\u0443\u0434\u0435\u0442\u0435 \u0441\u0442\u043E\u044F\u0442\u044C \u043F\u0440\u044F\u043C\u043E \u043F\u0435\u0440\u0435\u0434 \u0432\u0445\u043E\u0434\u043E\u043C - \u043A\u043E\u043D\u043A\u0440\u0435\u0442\u043D\u043E\u0435 \u0434\u0435\u043B\u043E \u043D\u0435 \u0438\u043C\u0435\u0435\u0442 \u0437\u043D\u0430\u0447\u0435\u043D\u0438\u044F. \u041C\u0435\u0441\u0442\u043E \u043D\u0430\u0434\u0435\u043B\u044F\u0435\u0442 \u0441\u043C\u044B\u0441\u043B\u043E\u043C \u0432\u0440\u0435\u043C\u044F, \u043A\u043E\u0442\u043E\u0440\u043E\u0435 \u0442\u044B \u043F\u0440\u043E\u0432\u043E\u0434\u0438\u0448\u044C \u0442\u0430\u043C, \u0430 \u043D\u0435 \u0434\u0435\u0439\u0441\u0442\u0432\u0438\u044F.</p><p>\u0412 \u041C\u0435\u0441\u0442\u0435 \u043C\u043E\u0436\u043D\u043E \u0434\u0430\u0436\u0435 \u043F\u043E\u0441\u0438\u0434\u0435\u0442\u044C \u0441\u043E\u0432\u0435\u0440\u0448\u0435\u043D\u043D\u043E \u043E\u0434\u043D\u043E\u043C\u0443, \u0447\u0438\u0442\u0430\u0442\u044C \u043A\u043D\u0438\u0433\u0443, \u0433\u043B\u0430\u0437\u0435\u0442\u044C \u043D\u0430 \u043F\u0440\u043E\u0445\u043E\u0436\u0438\u0445, \u0441\u043B\u0443\u0448\u0430\u0442\u044C \u043F\u043B\u0435\u0435\u0440 \u0438\u043B\u0438 \u043F\u0435\u0440\u0435\u043F\u0438\u0441\u044B\u0432\u0430\u0442\u044C \u043A\u0430\u043A\u0443\u044E-\u043D\u0438\u0431\u0443\u0434\u044C \u043B\u0435\u043A\u0446\u0438\u044E - \u0432\u0441\u0435 \u0447\u0442\u043E \u0443\u0433\u043E\u0434\u043D\u043E \u043D\u0430\u0434\u0435\u043B\u044F\u043B\u043E\u0441\u044C \u0441\u043C\u044B\u0441\u043B\u043E\u043C \u0438 \u0431\u044B\u043B\u043E \u0432\u043E\u0437\u043D\u0430\u0433\u0440\u0430\u0436\u0434\u0435\u043D\u043E \u0442\u0435\u043F\u043B\u044B\u043C \u043E\u0431\u0449\u0435\u043D\u0438\u0435\u043C \u0438 \u043F\u0440\u0438\u044F\u0442\u043D\u043E\u0439 \u043A\u043E\u043C\u043F\u0430\u043D\u0438\u0435\u0439. \u041C\u0430\u0433\u0438\u044F \u0434\u0430 \u0438 \u0442\u043E\u043B\u044C\u043A\u043E. \u041F\u043E\u0442\u043E\u043C \u044F \u043F\u043E\u0431\u044B\u0432\u0430\u043B \u0432 \u0421\u041E\u0426 \u0410\u043D\u0430\u043F\u0435 \u0438 \u044D\u0442\u0430 \u041C\u0430\u0433\u0438\u044F \u0437\u0430\u0438\u0441\u043A\u0440\u0438\u043B\u0430\u0441\u044C \u0438 \u0437\u0430\u0441\u0438\u044F\u043B\u0430 \u0441\u043E\u0432\u0435\u0440\u0448\u0435\u043D\u043D\u043E \u043D\u043E\u0432\u044B\u043C\u0438 \u043A\u0440\u0430\u0441\u043A\u0430\u043C\u0438! \u0410\u043D\u0430\u043F\u0430 - \u044D\u0442\u043E \u043E\u0442\u0434\u0435\u043B\u044C\u043D\u0430\u044F \u0442\u0435\u043C\u0430, \u041C\u0435\u0441\u0442\u043E \u041C\u0435\u0441\u0442, \u041C\u0435\u0441\u0442\u043E \u0436\u0438\u0437\u043D\u0438, \u043F\u043E\u0436\u0430\u043B\u0443\u0439. \u042D\u0442\u043E \u043E\u0442\u0434\u0435\u043B\u044C\u043D\u0430\u044F \u0442\u0435\u043C\u0430, \u0434\u0443\u043C\u0430\u044E, \u0442\u0435\u043C, \u043A\u0442\u043E \u0442\u0430\u043C \u0431\u044B\u0432\u0430\u043B, \u0440\u0430\u0441\u0441\u043A\u0430\u0437\u044B\u0432\u0430\u0442\u044C \u043D\u0435 \u043D\u0443\u0436\u043D\u043E, \u0430 \u0442\u0435\u043C, \u043A\u0442\u043E \u043D\u0435 \u0431\u044B\u043B - \u043E\u043D\u0438 \u044D\u0442\u043E \u0442\u043E\u0436\u0435 \u043F\u043E\u0439\u043C\u0443\u0442, \u0434\u043E\u0441\u0442\u0430\u0442\u043E\u0447\u043D\u043E \u043D\u0430\u043F\u0438\u0441\u0430\u0442\u044C \u0441\u043B\u043E\u0432\u0430 \u041B\u0435\u0442\u043E, \u041C\u043E\u0440\u0435, \u0421\u0432\u043E\u0431\u043E\u0434\u0430, \u041C\u043E\u043B\u043E\u0434\u043E\u0441\u0442\u044C. \u0425\u043E\u0442\u044F, \u0437\u043D\u0430\u044E, \u0447\u0442\u043E \u043D\u0435 \u0442\u043E\u043B\u044C\u043A\u043E \u043D\u0430 \u043C\u043E\u0440\u0435 \u0435\u0441\u0442\u044C \u0442\u0430\u043A\u0438\u0435 \u0441\u0442\u0443\u0434\u0435\u043D\u0447\u0435\u0441\u043A\u0438\u0435 \u043B\u0430\u0433\u0435\u0440\u044F, \u0433\u0434\u0435 \u0432\u0440\u0435\u043C\u044F \u0442\u0435\u0447\u0435\u0442 \u0442\u0430\u043A\u0438\u043C \u0436\u0435 \u0433\u0443\u0441\u0442\u044B\u043C \u0441\u043B\u0430\u0434\u043A\u0438\u043C \u043A\u0438\u0441\u0435\u043B\u0435\u043C, \u0442\u044B \u043D\u0438 \u0441 \u043A\u0435\u043C \u043D\u0435 \u043F\u0440\u043E\u0449\u0430\u0435\u0448\u044C\u0441\u044F \u0438 \u043D\u0435 \u0437\u0434\u043E\u0440\u043E\u0432\u0430\u0435\u0448\u044C\u0441\u044F (\u0434\u0430\u0436\u0435 \u0441 \u0442\u043E\u043B\u044C\u043A\u043E \u0447\u0442\u043E \u043F\u0440\u0438\u0435\u0445\u0430\u0432\u0448\u0438\u043C\u0438!) \u0438 \u0432\u0441\u0435 \u043B\u044E\u0434\u0438 \u0432\u043E\u043A\u0440\u0443\u0433 \u0437\u0430\u0432\u0435\u0434\u043E\u043C\u043E \u0443\u0436\u0435 \u0442\u0432\u043E\u0438 \u0434\u0440\u0443\u0437\u044C\u044F \u0438 \u043D\u0438\u043A\u0430\u043A \u0438\u043D\u0430\u0447\u0435. \u0410\u043D\u0430\u043F\u0430 \u043F\u043E\u0434\u0430\u0440\u0438\u043B\u0430 \u043C\u043D\u0435 \u043C\u043E\u0440\u0435. \u041C\u043E\u0440\u0435 \u043B\u044E\u0434\u0435\u0439, \u043C\u043E\u0440\u0435 \u0434\u0440\u0443\u0436\u0431\u044B. \u041F\u043B\u0435\u0445\u0430\u043D\u043E\u0432\u0441\u043A\u0430\u044F \u0430\u043A\u0430\u0434\u0435\u043C\u0438\u044F \u0441\u0442\u0430\u043B\u0430 \u0434\u043B\u044F \u043C\u0435\u043D\u044F \u0432\u0442\u043E\u0440\u043E\u0439 \u0430\u043B\u044C\u043C\u0430\u043C\u0430\u0442\u0435\u0440, \u043F\u0443\u0441\u043A\u0430\u0439 \u0434\u0430\u0436\u0435 \u044F \u043F\u043B\u0435\u0432\u0430\u043B \u043D\u0430 \u044D\u0442\u0438 \u0432\u0430\u0448\u0438 \u0433\u043E\u0432\u043D\u043E\u043C\u0435\u043D\u0435\u0434\u0436\u043C\u0435\u043D\u0442\u044B \u0438 \u0433\u043E\u0432\u043D\u043E\u0440\u0435\u043A\u043B\u0430\u043C\u0443, \u043A\u043E\u0442\u043E\u0440\u043E\u0439, \u043E \u0411\u043E\u0436\u0435!, \u0443\u0447\u0430\u0442 \u0432\u0430\u0441 \u043F\u043E \u043F\u044F\u0442\u044C \u043B\u0435\u0442 \u043F\u043E\u0434\u0440\u044F\u0434.</p><p>\u041F\u043E\u0441\u043B\u0435 \u0443\u043D\u0438\u0432\u0435\u0440\u0430, \u043F\u043E\u0441\u043B\u0435 \u0440\u0430\u0431\u043E\u0442\u044B, \u0434\u0430 \u0434\u0430\u0436\u0435 \u0432\u043E \u0432\u0440\u0435\u043C\u044F - \u0432\u0441\u0435\u0433\u0434\u0430 \u043C\u043E\u0436\u043D\u043E \u0431\u044B\u043B\u043E \u043F\u043E\u0434\u044A\u0435\u0445\u0430\u0442\u044C \u043D\u0430 \u041C\u0435\u0441\u0442\u043E, \u043A\u043E \u0432\u0445\u043E\u0434\u0443 \u0432 \u0420\u042D\u0410 \u0438 \u0432\u0441\u0442\u0440\u0435\u0442\u0438\u0442\u044C \u0442\u0430\u043C \u0434\u0440\u0443\u0437\u0435\u0439, \u0437\u043D\u0430\u043A\u043E\u043C\u044B\u0445 \u0438\u043B\u0438 \u043F\u0440\u043E\u0441\u0442\u043E \u0443\u0436\u0435 \u0443\u0432\u0438\u0434\u0435\u043D\u043D\u044B\u0445 \u043E\u0434\u043D\u0430\u0436\u0434\u044B \u043B\u044E\u0434\u0435\u0439 \u0438, \u043C\u0435\u043B\u044C\u043A\u043E\u043C \u043F\u043E\u0437\u0434\u043E\u0440\u043E\u0432\u0430\u0432\u0448\u0438\u0441\u044C, \u043D\u044B\u0440\u043D\u0443\u0442\u044C \u0432 \u0442\u0435\u043F\u043B\u043E\u0435 \u0438 \u043F\u0440\u0438\u044F\u0442\u043D\u043E\u0435 \u043E\u0431\u0449\u0435\u043D\u0438\u0435 \u0438 \u043F\u0435\u0440\u043F\u043B\u0435\u0442\u0435\u043D\u0438\u0435 \u0441\u0443\u0434\u0435\u0431 \u043B\u044E\u0434\u0435\u0439. \u041A\u0442\u043E-\u0442\u043E \u043F\u0440\u0435\u0434\u043B\u043E\u0436\u0438\u0442 \u043F\u043E\u0439\u0442\u0438 \u0437\u0430 \u0435\u0434\u043E\u0439 \u0432 \u043C\u0430\u0433\u0430\u0437\u0438\u043D \u0438\u043B\u0438 \u043F\u0440\u043E\u0433\u0443\u043B\u044F\u0442\u044C\u0441\u044F \u0434\u043E \u043C\u0430\u043A\u0434\u043E\u043D\u0430\u043B\u044C\u0434\u0441\u0430, \u043A\u0442\u043E-\u0442\u043E \u0434\u0435\u043B\u043E\u0432\u0438\u0442\u043E \u0431\u0443\u0434\u0435\u0442 \u043B\u0438\u0441\u0442\u0430\u0442\u044C \u043A\u0430\u043A\u0438\u0435-\u043D\u0438\u0431\u0443\u0434\u044C \u043B\u0438\u0441\u0442\u044B \u0441 \u043F\u0435\u0447\u0430\u0442\u043D\u044B\u043C \u0442\u0435\u043A\u0441\u0442\u043E\u043C, \u0433\u0440\u043E\u043C\u043A\u043E \u0441\u043C\u0435\u044F\u0442\u044C\u0441\u044F, \u043A\u043E\u0433\u043E-\u0442\u043E \u0442\u043E\u043B\u043A\u0430\u0442\u044C, \u043E\u0431\u043D\u0438\u043C\u0430\u0442\u044C, \u0437\u043B\u0438\u0442\u044C \u0438\u043B\u0438 \u0441\u043C\u0435\u0448\u0438\u0442\u044C. \u0414\u0430 \u0434\u0430\u0436\u0435 \u043F\u0440\u043E\u0441\u0442\u043E \u043C\u043E\u043B\u0447\u0430\u043D\u0438\u0435 \u0432 \u041C\u0435\u0441\u0442\u0435 \u0438 \u0432\u043C\u0435\u0441\u0442\u0435 \u0441 \u0434\u0440\u0443\u0437\u044C\u044F\u043C\u0438 \u0441\u0442\u0430\u043D\u043E\u0432\u0438\u0442\u0441\u044F \u0432\u0434\u0440\u0443\u0433 \u0433\u043B\u0443\u0431\u043E\u043A\u0438\u043C \u0438 \u043E\u0441\u043C\u044B\u0441\u043B\u0435\u043D\u043D\u044B\u043C!</p><p>\u041D\u043E \u0432\u043E\u0442 \u0440\u0435\u0431\u044F\u0442\u0430 \u0437\u0430\u043A\u043E\u043D\u0447\u0438\u043B\u0438 \u0441\u0432\u043E\u0438 \u0412\u0423\u0417\u044B, \u043E\u0431\u0440\u0435\u043B\u0438 \u0434\u0435\u043B\u043E\u0432\u0438\u0442\u044B\u0435 \u043B\u0438\u0446\u0430, \u0441\u0435\u0440\u044C\u0435\u0437\u043D\u044B\u0435 \u0448\u043C\u043E\u0442\u043A\u0438 \u0438 \u0412\u0430\u0436\u043D\u044B\u0435 \u0414\u0435\u043B\u0430, \u043A\u043E\u0442\u043E\u0440\u044B\u0435 \u0441\u0442\u0430\u043B\u0438 \u0432\u0430\u0436\u043D\u0435\u0435 \u0432\u0441\u0442\u0440\u0435\u0447, \u0432\u0430\u0436\u043D\u0435\u0435 \u0431\u0435\u0437\u0434\u0435\u043B\u044C\u044F \u0438 \u0442\u0430\u043A\u043E\u0433\u043E \u043F\u0440\u043E\u0441\u0442\u043E\u0433\u043E \u0438 \u043B\u0435\u0433\u043A\u043E\u0433\u043E \u043E\u0431\u0449\u0435\u043D\u0438\u044F \u0441 \u0431\u043B\u0438\u0437\u043A\u0438\u043C\u0438 \u0438 \u043D\u0435 \u043E\u0447\u0435\u043D\u044C \u043B\u044E\u0434\u044C\u043C\u0438. \u041D\u043E \u043D\u0435 \u0432\u0441\u0435 \u0442\u0430\u043A \u043F\u043B\u043E\u0445\u043E, \u043D\u0435\u0442!</p><p>\u041A\u0442\u043E \u0431\u044B\u0432\u0430\u043B, \u0442\u043E\u0442 \u0432\u0441\u043F\u043E\u043C\u043D\u0438\u0442, \u043A\u0430\u043A\u0438\u043C \u043F\u0440\u0435\u043A\u0440\u0430\u0441\u043D\u044B\u043C \u041C\u0435\u0441\u0442\u043E\u043C \u0441\u0442\u0430\u043B\u043E \u043A\u043E\u0433\u0434\u0430-\u0442\u043E \u0436\u0438\u043B\u0438\u0449\u0435 \u0410\u043D\u0434\u0440\u0435\u044F \u041D\u043E\u0432\u0438\u043A\u043E\u0432\u0430, \u043A\u043E\u0442\u043E\u0440\u044B\u0439 \u043E\u043F\u0440\u043E\u043C\u0435\u0442\u0447\u0438\u0432\u043E \u0442\u0430\u043A \u0441\u043D\u044F\u043B \u043A\u0432\u0430\u0440\u0442\u0438\u0440\u0443 \u043F\u0440\u044F\u043C\u043E \u0440\u044F\u0434\u043E\u043C \u0441 \u043F\u043B\u0435\u0445\u043E\u0439 \u0438 \u043F\u043E\u0437\u0432\u0430\u043B \u0442\u0443\u0434\u0430 \u0432 \u043C\u043D\u043E\u0433\u043E\u043B\u044E\u0434\u043D\u043E\u0435 \u043F\u0440\u0430\u0437\u0434\u043D\u043E\u0432\u0430\u043D\u0438\u0435 \u043F\u0435\u0440\u0432\u043E\u0433\u043E \u0441\u0435\u043D\u0442\u044F\u0431\u0440\u044F \u0438 \u0441\u0431\u043E\u0440\u0430 \u0430\u043D\u0430\u043F\u0441\u043A\u0438\u0445 \u041B\u0438\u0447\u043D\u043E\u0441\u0442\u0435\u0439 \u0442\u0443\u043F\u043E \u0412\u0421\u0415\u0425 \u0438 \u0421\u0420\u0410\u0417\u0423. \u042F \u043F\u043E\u043C\u043D\u044E \u043A\u0430\u043A \u0433\u0440\u043E\u043C\u043A\u043E \u043A\u0440\u0438\u0447\u0430\u043B &quot;\u0412\u0441\u0435 \u043A \u041D\u043E\u0432\u0438\u043A\u043E\u043C\u0443!&quot; \u0438 \u0442\u043E\u043B\u043A\u0430\u043B \u0432 \u0442\u0443 \u0441\u0442\u043E\u0440\u043E\u043D\u0443 \u0442\u043E\u043B\u043F\u0443 \u0443\u0436\u0435 \u0441\u043E\u0432\u0441\u0435\u043C \u043D\u0435\u0442\u0440\u0435\u0437\u0432\u044B\u0445 \u043B\u044E\u0434\u0435\u0439. \u0427\u0435\u043B\u043E\u0432\u0435\u043A 100 \u0442\u0430\u043C \u0437\u0430 \u044D\u0442\u0443 \u043D\u043E\u0447\u044C \u043F\u043E\u0431\u044B\u0432\u0430\u043B\u043E, \u043F\u0440\u0438\u0447\u0435\u043C \u043E\u0434\u043D\u043E\u0432\u0440\u0435\u043C\u0435\u043D\u043D\u043E \u0442\u0430 \u0434\u0432\u0443\u0448\u043A\u0430 \u0437\u0430\u043F\u043E\u043B\u043D\u044F\u043B\u0430\u0441\u044C \u0447\u0443\u0442\u044C \u043B\u0438 \u043D\u0435 \u043F\u044F\u0442\u044C\u044E \u0434\u0435\u0441\u044F\u0442\u043A\u0430\u043C\u0438 \u043B\u044E\u0434\u0435\u0439, \u043A\u043E\u0442\u043E\u0440\u044B\u0435 \u0437\u0430\u043F\u043E\u043B\u043D\u0438\u043B\u0438 \u043D\u0435 \u0442\u043E\u043B\u044C\u043A\u043E \u0432\u0441\u0435 \u043A\u043E\u043C\u043D\u0430\u0442\u044B \u043A\u0432\u0430\u0440\u0442\u0438\u0440\u044B, \u043D\u043E \u0441\u0438\u0434\u0435\u043B\u0438 \u043D\u0430 \u043F\u043E\u0434\u043E\u043A\u043E\u043D\u043D\u0438\u043A\u0430\u0445, \u043D\u0430 \u0441\u0442\u043E\u043B\u0430\u0445, \u043F\u043B\u043E\u0442\u043D\u043E \u0441\u0442\u043E\u044F\u043B\u0438 \u0432\u0434\u043E\u043B\u044C \u0441\u0442\u0435\u043D \u043A\u043E\u0440\u0440\u0438\u0434\u043E\u0440\u0430, \u0432 \u0434\u0432\u0435\u0440\u044F\u0445, \u043D\u0430 \u043B\u0435\u0441\u0442\u043D\u0438\u0447\u043D\u043E\u0439 \u043A\u043B\u0435\u0442\u043A\u0435, \u0432\u043D\u0438\u0437\u0443 \u043F\u043E\u0434\u044A\u0435\u0437\u0434\u0430, \u043D\u0430 \u0443\u043B\u0438\u0446\u0435 \u0438 \u0440\u0430\u0441\u0442\u044F\u043D\u0443\u043B\u0438\u0441\u044C \u043F\u043E \u0432\u0441\u0435\u043C\u0443 \u043F\u0443\u0442\u0438 \u043E\u0442 \u043F\u043B\u0435\u0448\u043A\u0438! \u0422\u0430 \u043A\u0432\u0430\u0442\u0440\u0438\u0440\u043A\u0430 \u0434\u043B\u044F \u043C\u043D\u043E\u0433\u0438\u0445 \u0441\u0442\u0430\u043B\u0430 \u0442\u0430\u043A\u0438\u043C \u0436\u0435 \u041C\u0435\u0441\u0442\u043E\u043C, \u0446\u0435\u043D\u0442\u0440\u043E\u043C \u043F\u0440\u0438\u0442\u044F\u0436\u0435\u043D\u0438\u044F, \u0442\u043E\u0447\u043A\u043E\u0439 \u0432\u0441\u0442\u0440\u0435\u0447\u0438 \u0438 \u0441\u0438\u043C\u0432\u043E\u043B\u043E\u043C \u0421\u0432\u043E\u0431\u043E\u0434\u044B \u0438 \u043E\u0442\u043A\u0440\u044B\u0442\u043E\u0441\u0442\u0438 \u043A \u0441\u043E\u0432\u043C\u0435\u0441\u0442\u043D\u043E\u043C\u0443 \u0431\u0435\u0437\u0434\u0435\u043B\u044C\u044E.</p><p>\u042D\u0442\u043E\u0442 \u0443\u0440\u043E\u043A \u0431\u044B\u043B \u043C\u043D\u043E\u044E \u0443\u0441\u0432\u043E\u0435\u043D \u0438 \u044F \u0438\u0437\u043E \u0432\u0441\u0435\u0445 \u0441\u0438\u043B \u0441\u0442\u0430\u0440\u0430\u043B\u0441\u044F \u0445\u0440\u0430\u043D\u0438\u0442\u044C \u044D\u0442\u0443 \u0446\u0435\u043D\u043D\u043E\u0441\u0442\u044C. \u041D\u0435 \u043A\u0430\u043A\u0443\u044E-\u0442\u043E \u043A\u043E\u043D\u043A\u0440\u0435\u0442\u043D\u0443\u044E \u043A\u0432\u0430\u0440\u0442\u0438\u0440\u0443, \u0442\u043E\u0447\u043A\u0443, \u0441\u043A\u0430\u043C\u0435\u0439\u043A\u0443, \u043F\u0430\u0440\u043A, \u0430 \u043D\u0430\u0448\u0443 \u043E\u0431\u0449\u0443\u044E \u0421\u041F\u041E\u0421\u041E\u0411\u041D\u041E\u0421\u0422\u042C \u0436\u0438\u0442\u044C \u0441 \u041C\u0435\u0441\u0442\u043E\u043C \u0432 \u0434\u0443\u0448\u0435 \u0438 \u043C\u0435\u0441\u0442\u043E\u043C \u0434\u043B\u044F \u044D\u0442\u043E\u0439 \u043D\u0430\u0441\u0442\u043E\u044F\u0449\u0435\u0439 \u0441\u0432\u043E\u0431\u043E\u0434\u044B \u043E\u0442\u043E \u0432\u0441\u0435\u0433\u043E, \u0441\u0432\u043E\u0431\u043E\u0434\u044B \u0440\u0430\u0434\u0438 \u0434\u0440\u0443\u0437\u0435\u0439 \u0438 \u0431\u043B\u0438\u0437\u043A\u0438\u0445.</p><p>\u0410\u0432\u0430\u0442\u0430\u0440\u043E\u0448\u043D\u0430\u044F, \u043A\u0432\u0430\u0440\u0442\u0438\u0440\u0430, \u043A\u043E\u0442\u043E\u0440\u0443\u044E \u044F \u0441\u043D\u044F\u043B \u043D\u0430 \u041F\u0440\u043E\u043B\u0435\u0442\u0430\u0440\u043A\u0435, \u0431\u044B\u043B\u0430 \u043C\u043E\u0438\u043C \u043C\u0430\u043D\u0438\u0444\u0435\u0441\u0442\u043E\u043C \u044D\u0442\u043E\u0439 \u043E\u0442\u043A\u0440\u044B\u0442\u043E\u0441\u0442\u0438 \u0438 \u0441\u0432\u043E\u0431\u043E\u0434\u044B. \u0414\u0432\u0435\u0440\u0438 \u0431\u044B\u043B\u0438 \u043E\u0442\u043A\u0440\u044B\u0442\u044B \u0434\u043B\u044F \u0432\u0441\u0435\u0445 \u0438 \u0432\u0441\u0435\u0433\u0434\u0430, \u0432 \u0441\u043A\u0440\u043E\u043C\u043D\u0443\u044E \u043E\u0434\u043D\u0443\u0448\u043A\u0443 \u043D\u0430\u0431\u0438\u0432\u0430\u043B\u043E\u0441\u044C, \u0431\u044B\u0432\u0430\u043B\u043E, \u0447\u0435\u043B\u043E\u0432\u0435\u043A 15-20! \u041D\u0435 \u0432 \u043A\u043E\u043B\u0438\u0447\u0435\u0441\u0442\u0432\u0435 \u0434\u0435\u043B\u043E, \u0438 \u043D\u0435\u0431\u043E\u043B\u044C\u0448\u0438\u0435 \u043A\u043E\u043C\u043F\u0430\u043D\u0438\u0438 \u043F\u0440\u0435\u043A\u0440\u0430\u0441\u043D\u043E \u043F\u0440\u043E\u0432\u043E\u0434\u0438\u043B\u0438 \u0432\u0440\u0435\u043C\u044F, \u043D\u043E \u043D\u0435\u0431\u043E\u043B\u044C\u0448\u0438\u0435 \u0442\u0435\u043F\u043B\u044B\u0435 \u043A\u043E\u043C\u043F\u0430\u043D\u0438\u0438 \u0442\u0443\u0442 \u0436\u0435 \u043F\u043E\u0434\u0442\u044F\u0433\u0438\u0432\u0430\u043B\u0438 \u0435\u0449\u0435 \u0434\u043E\u0431\u0440\u043E\u0434\u0443\u0448\u043D\u044B\u0445 \u0431\u0435\u0437\u0434\u0435\u043B\u044C\u043D\u0438\u043A\u043E\u0432.</p><p>\u0411\u0435\u0437\u0434\u0435\u043B\u044C\u043D\u0438\u043A\u0438, \u0434\u0430. \u041D\u043E \u043D\u0435 \u044D\u0442\u043E \u043B\u0438 \u0438 \u0435\u0441\u0442\u044C \u043D\u0430\u0441\u0442\u043E\u044F\u0449\u0430\u044F \u0421\u0432\u043E\u0431\u043E\u0434\u0430? \u0421\u0432\u043E\u0431\u043E\u0434\u0430 \u043E\u0442 \u043F\u043E\u0442\u0440\u0435\u0431\u043D\u043E\u0441\u0442\u0435\u0439, \u043E\u0442 \u043F\u0440\u0430\u0432 \u0438 \u043E\u0431\u044F\u0437\u0430\u043D\u043D\u043E\u0441\u0442\u0435\u0439, \u043E\u0442 \u0434\u0435\u043B \u0438 \u043E\u0434\u0438\u043D\u043E\u0447\u0435\u0441\u0442\u0432\u0430, \u043E\u0442 \u043F\u043E\u043A\u0443\u043F\u043E\u043A \u0438 \u043F\u0440\u043E\u0434\u0430\u0436, \u043E\u0442 \u0434\u043E\u0440\u043E\u0433\u0438 \u0438 \u043E\u0436\u0438\u0434\u0430\u043D\u0438\u044F! \u0414\u0430 \u0438 \u043A\u0443\u0434\u0430 \u0432\u0430\u0436\u043D\u0435\u0435 \u043D\u0435 \u0441\u0432\u043E\u0431\u043E\u0434\u0430 \u043E\u0442, \u0430 \u0441\u0432\u043E\u0431\u043E\u0434\u0430 \u0414\u041B\u042F. \u0421\u0432\u043E\u0431\u043E\u0434\u0430 \u043B\u044E\u0434\u0435\u0439 \u0434\u043B\u044F \u043B\u044E\u0434\u0435\u0439.</p><p>\u041C\u043E\u0436\u0435\u0442 \u0431\u044B\u0442\u044C \u0432\u0430\u043C \u043F\u043E\u043A\u0430\u0436\u0435\u0442\u0441\u044F, \u0447\u0442\u043E \u044F \u043F\u0438\u0448\u0443 \u044D\u0442\u0438 \u0441\u0442\u0440\u043E\u043A\u0438 \u0441 \u0441\u043E\u0436\u0430\u043B\u0435\u043D\u0438\u0435\u043C \u0438 \u0432\u0437\u0434\u043E\u0445\u0430\u043C\u0438 \u043E\u0431 \u0443\u0448\u0435\u0434\u0448\u0435\u043C \u043F\u0440\u043E\u0448\u043B\u043E\u043C. \u041E\u0442\u0447\u0430\u0441\u0442\u0438 \u044D\u0442\u043E \u0442\u0430\u043A, \u0432\u0435\u0434\u044C \u043F\u0440\u043E\u0448\u043B\u043E\u0435 \u043D\u0435 \u0432\u0435\u0440\u043D\u0443\u0442\u044C. \u041D\u043E \u0432\u043E\u0442 \u0431\u0443\u0434\u0443\u0449\u0435\u0435 \u0438 \u043D\u0430\u0441\u0442\u043E\u044F\u0449\u0435\u0435 \u043F\u043E\u0440\u0430 \u0432\u0437\u044F\u0442\u044C \u0432 \u0440\u0443\u043A\u0438, \u043D\u0435\u043C\u043D\u043E\u0433\u043E \u043F\u043E\u0434\u0443\u043C\u0430\u0442\u044C \u0438 \u043F\u043E\u043D\u044F\u0442\u044C, \u0447\u0442\u043E \u0436\u0435 \u0442\u0430\u043A\u043E\u0433\u043E \u043A\u0440\u0443\u0442\u043E\u0433\u043E \u0438 \u043F\u0440\u0435\u043A\u0440\u0430\u0441\u043D\u043E\u0433\u043E \u0432 \u0442\u0430\u043A\u043E\u043C \u0434\u043E\u043B\u0433\u043E\u0436\u0434\u0430\u043D\u043D\u043E\u043C \u043B\u0435\u0442\u0435? \u041D\u0435 \u0443\u0436 \u0442\u043E \u0432\u044B \u0432\u0441\u0435 \u0436\u0434\u0430\u043B\u0438 \u0432\u043E\u0437\u043C\u043E\u0436\u043D\u043E\u0441\u0442\u0438 \u0432\u043A\u043B\u044E\u0447\u0438\u0442\u044C \u0432 \u043E\u0444\u0438\u0441\u0430\u0445 \u043A\u043E\u043D\u0434\u0438\u0446\u0438\u043E\u043D\u0435\u0440\u044B \u043F\u043E\u044F\u0434\u0440\u0435\u043D\u0435\u0435 \u0438 \u0432\u044B\u0433\u0443\u043B\u044F\u0442\u044C \u043F\u043E \u0443\u043B\u0438\u0446\u0435 \u0441\u0432\u043E\u0439 \u043B\u0435\u0442\u043D\u0438\u0439 \u0433\u0430\u0434\u0435\u0440\u043E\u0431? \u041D\u0435\u0443\u0436\u0435\u043B\u0438 \u0432\u044B \u0436\u0434\u0430\u043B\u0438 \u0435\u0433\u043E \u0434\u043B\u044F \u0442\u043E\u0433\u043E, \u0447\u0442\u043E\u0431\u044B \u0442\u0430\u043A\u0436\u0435 \u0441\u0438\u0434\u0435\u0442\u044C \u0434\u043E\u043C\u0430 \u043F\u0435\u0440\u0435\u0434 \u043C\u043E\u043D\u0438\u0442\u043E\u0440\u043E\u043C, \u043D\u043E \u0442\u043E\u043B\u044C\u043A\u043E \u0441 \u0440\u0430\u0441\u043F\u0430\u0445\u043D\u0443\u0442\u044B\u043C \u043E\u043A\u043D\u043E\u043C? \u041D\u0435\u0443\u0436\u0435\u043B\u0438 \u0432\u044B \u0434\u0435\u0439\u0441\u0442\u0432\u0438\u0442\u0435\u043B\u044C\u043D\u043E \u0445\u043E\u0442\u0435\u043B\u0438 \u043F\u0440\u043E\u0441\u0442\u043E \u043F\u043E\u0441\u043C\u043E\u0442\u0440\u0435\u0442\u044C \u043D\u0430 \u0432\u0441\u0435 \u0442\u0435 \u0436\u0435 \u0437\u0434\u0430\u043D\u0438\u044F \u0432 \u0446\u0435\u043D\u0442\u0440\u0435 \u0433\u043E\u0440\u043E\u0434\u0430, \u043D\u043E \u0442\u043E\u043B\u044C\u043A\u043E \u0441\u0443\u0445\u0438\u043C\u0438 \u0438 \u0447\u0438\u0441\u0442\u044B\u043C\u0438 \u043E\u0442 \u0441\u043D\u0435\u0433\u0430?</p><p>\u041D\u0430\u0439\u0434\u0438\u0442\u0435 \u0432\u0440\u0435\u043C\u044F. \u041D\u0430\u0439\u0434\u0438\u0442\u0435 \u041C\u0435\u0441\u0442\u043E. \u0410 \u0434\u0430\u043B\u044C\u0448\u0435 \u0443\u0436\u0435 \u043D\u0438\u043A\u0442\u043E \u043D\u0435 \u0441\u043C\u043E\u0436\u0435\u0442 \u0441\u043A\u0430\u0437\u0430\u0442\u044C, \u0447\u0442\u043E \u0432\u0430\u0441 \u0436\u0434\u0435\u0442. \u0417\u0430\u0441\u044B\u043F\u0430\u043D\u043D\u044B\u0439 \u0448\u043A\u0443\u0440\u043A\u0430\u043C\u0438 \u0441\u0435\u043C\u0435\u0447\u0435\u043A \u0442\u0440\u043E\u0442\u0443\u0430\u0440 \u0432\u043E\u043A\u0440\u0443\u0433 \u0441\u043A\u0430\u043C\u0435\u0439\u043A\u0438 \u0438\u043B\u0438 \u0432\u0441\u0442\u0440\u0435\u0447\u0430 \u043B\u044E\u0431\u0432\u0438 \u0432\u0441\u0435\u0439 \u0436\u0438\u0437\u043D\u0438, \u0444\u0438\u043B\u043E\u0441\u043E\u0444\u0441\u043A\u0430\u044F \u0431\u0430\u0442\u0430\u043B\u0438\u044F \u0441 \u043D\u0435\u0437\u043D\u0430\u043A\u043E\u043C\u0446\u0435\u043C \u0438\u043B\u0438 \u0432\u0441\u0442\u0440\u0435\u0447\u0430 \u0441\u0442\u0430\u0440\u043E\u0433\u043E \u0434\u0440\u0443\u0433\u0430, \u043F\u043E \u043A\u043E\u0442\u043E\u0440\u043E\u043C\u0443 \u0442\u0430\u043A \u0441\u043A\u0443\u0447\u0430\u043B\u043E\u0441\u044C, \u0432\u044F\u043B\u0430\u044F \u0431\u0435\u0441\u0435\u0434\u0430 \u0441 \u0431\u044B\u0432\u0448\u0438\u043C \u043D\u0435\u0434\u0440\u0443\u0433\u043E\u043C \u0438\u043B\u0438 \u043F\u0440\u043E\u0433\u0443\u043B\u043A\u0430 \u043F\u043E \u043D\u043E\u0447\u043D\u043E\u043C\u0443 \u0433\u043E\u0440\u043E\u0434\u0443 \u0434\u043E \u0441\u0430\u043C\u043E\u0433\u043E \u0443\u0442\u0440\u0430.</p><p>\u041A\u043E\u043D\u0435\u0447\u043D\u043E, \u0432 \u043F\u0440\u0435\u0434\u0435\u043B\u0435 \u043A\u0430\u0436\u0434\u044B\u0439 \u0438\u0437 \u043D\u0430\u0441 \u0441\u0430\u043C \u043F\u043E \u0441\u0435\u0431\u0435 \u0434\u043E\u043B\u0436\u0435\u043D \u0441\u0442\u0430\u0442\u044C \u0442\u0430\u043A\u0438\u043C \u041C\u0435\u0441\u0442\u043E\u043C, \u0438\u0434\u0442\u0438 \u043F\u043E \u0436\u0438\u0437\u043D\u0438 \u0441\u0432\u043E\u0431\u043E\u0434\u043D\u043E, \u0432\u0441\u0442\u0440\u0435\u0447\u0430\u044F \u0442\u0430\u043A\u0438\u0445 \u0436\u0435 \u0441\u0432\u043E\u0431\u043E\u0434\u043D\u044B\u0445 \u043B\u044E\u0434\u0435\u0439 \u043F\u043E\u0432\u0441\u044E\u0434\u0443, \u0432\u0441\u0442\u0440\u0435\u0432\u0430\u044F \u0432 \u043B\u044E\u0431\u044B\u0435 \u0440\u0430\u0437\u0433\u043E\u0432\u043E\u0440\u044B, \u0437\u0430\u0442\u0435\u0438 \u0438 \u044D\u043A\u0441\u043F\u0435\u0440\u0438\u043C\u0435\u043D\u0442\u044B. \u0418 \u043C\u043D\u043E\u0433\u0438\u0435 \u0438\u0437 \u043D\u0430\u0441 \u0442\u0430\u043A\u0438\u043C\u0438 \u0441\u0442\u0430\u043B\u0438. \u041D\u043E, \u0441\u043E\u0433\u043B\u0430\u0441\u0438\u0442\u0435\u0441\u044C, \u0438\u043D\u043E\u0433\u0434\u0430, \u0431\u044B\u0432\u0430\u0435\u0442, \u0442\u0430\u043A \u043D\u0430\u0445\u043B\u044B\u043D\u0435\u0442 \u0441\u043E\u0441\u043A\u0443\u0447 \u043F\u043E \u0441\u0442\u0430\u0440\u044B\u043C \u0434\u043E\u0431\u0440\u044B\u043C \u0434\u0440\u0443\u0437\u044C\u044F\u043C, \u0430 \u043C\u043E\u0436\u0435\u0442 \u0438 \u043F\u0440\u043E\u0441\u0442\u043E \u043F\u043E \u0438\u043D\u0442\u0435\u0440\u0435\u0441\u043D\u043E\u0439 \u0438 \u043B\u0435\u0433\u043A\u043E\u0439 \u0431\u0435\u0441\u0435\u0434\u0435, \u043D\u0430 \u043A\u043E\u0442\u043E\u0440\u0443\u044E \u0442\u044B \u0441\u043E\u0431\u0435\u0441\u0435\u0434\u043D\u0438\u043A\u0430 \u0434\u0430\u0436\u0435 \u0438 \u043D\u0435 \u0437\u0432\u0430\u043B, \u0430 \u043F\u0440\u043E\u0441\u0442\u043E \u0432\u0441\u0442\u0440\u0435\u0442\u0438\u043B. \u0411\u044B\u0432\u0430\u043B\u043E \u043B\u0438 \u0432\u0430\u043C \u043D\u0430\u0441\u0442\u043E\u043B\u044C\u043A\u043E \u043E\u0445\u043E\u0442\u0430 \u0432\u043E\u0442 \u0442\u0430\u043A \u043B\u0435\u0433\u043A\u043E \u043E\u0431\u043C\u0435\u043D\u044F\u0442\u044C\u0441\u044F \u043C\u044B\u0441\u043B\u044F\u043C\u0438 \u0441 \u043A\u0435\u043C-\u0442\u043E, \u0447\u0442\u043E \u0432\u044B \u0433\u043E\u0442\u043E\u0432\u044B \u0431\u044B \u0431\u044B\u043B\u0438 \u0443\u0437\u043D\u0430\u0442\u044C \u0432 \u0442\u043E\u043B\u043F\u0435 \u0434\u0430\u0436\u0435 \u043D\u0435\u0437\u043D\u0430\u043A\u043E\u043C\u043E\u0433\u043E \u0447\u0435\u043B\u043E\u0432\u0435\u043A\u0430 \u043A\u0430\u043A \u0441\u0442\u0430\u0440\u043E\u0433\u043E \u0434\u0440\u0443\u0433\u0430? \u0423 \u043C\u0435\u043D\u044F \u0442\u0430\u043A\u043E\u0435 \u043F\u043E\u0441\u0442\u043E\u044F\u043D\u043D\u043E! \u042F \u0441\u043A\u0443\u0447\u0430\u044E \u043F\u043E \u0432\u0430\u043C, \u0434\u0440\u0443\u0437\u044C\u044F!</p><p>\u042D\u0442\u0430 \u043C\u043E\u044F \u0441\u0442\u0440\u0430\u043D\u0438\u0447\u043A\u0430 - \u044D\u0442\u043E \u043C\u043E\u0451 \u0437\u0438\u043C\u043D\u0435\u0435 \u043C\u0435\u0441\u0442\u043E, \u044F \u0437\u0434\u0435\u0441\u044C \u0432\u0441\u0442\u0440\u0435\u0447\u0430\u044E \u043C\u043D\u043E\u0433\u0438\u0445 \u0438 \u043B\u0435\u0433\u043A\u043E \u043C\u043E\u0433\u0443 \u043F\u043E\u0434\u0435\u043B\u0438\u0442\u044C\u0441\u044F \u0432\u0441\u0435\u043C, \u0447\u0442\u043E \u0435\u0441\u0442\u044C, \u0441 \u043A\u0430\u0436\u0434\u044B\u043C. \u041E\u0434\u043D\u0430\u043A\u043E \u0442\u0430\u043A \u0445\u043E\u0447\u0435\u0442\u0441\u044F \u0432\u0438\u0434\u0435\u0442\u044C \u0432\u0430\u0441, \u0441\u043C\u0435\u044F\u0442\u044C\u0441\u044F \u0432\u043C\u0435\u0441\u0442\u0435 \u0441 \u0432\u0430\u043C\u0438, \u0431\u0435\u0437\u0434\u0435\u043B\u044C\u043D\u0438\u0447\u0430\u0442\u044C \u0438 \u0434\u0430\u0436\u0435 \u043C\u043E\u043B\u0447\u0430\u0442\u044C \u0432\u043C\u0435\u0441\u0442\u0435 \u0438, \u0436\u0435\u043B\u0430\u0442\u0435\u043B\u044C\u043D\u043E, \u0432 \u041C\u0435\u0441\u0442\u0435.</p><p>\u041A\u043E\u0440\u043E\u0447\u0435. \u041E\u0444\u0438\u0446\u0438\u0430\u043B\u044C\u043D\u043E \u043E\u0431\u044A\u044F\u0432\u043B\u044F\u044E \u041C\u0435\u0441\u0442\u043E\u043C \u044D\u0442\u0443 \u0441\u0442\u0440\u0430\u043D\u0438\u0447\u043A\u0443, \u0411\u043E\u043B\u043E\u0442\u043D\u0443\u044E \u043F\u043B\u043E\u0449\u0430\u0434\u044C \u0432 \u0446\u0435\u043D\u0442\u0440\u0435 \u041C\u043E\u0441\u043A\u0432\u044B, \u0444\u0443\u0442\u0431\u043E\u043B\u044C\u043D\u043E\u0435 \u043F\u043E\u043B\u0435 \u0432 \u041C\u0430\u0440\u044C\u0438\u043D\u043E \u043F\u043E \u0432\u043E\u0441\u043A\u0440\u0435\u0441\u0435\u043D\u044C\u044F\u043C \u0438 \u044D\u0442\u043E \u0442\u043E\u043B\u044C\u043A\u043E \u0438\u0437 \u0442\u043E\u0433\u043E, \u0447\u0442\u043E \u0443\u0436\u0435 \u0440\u0430\u0431\u043E\u0442\u0430\u0435\u0442 \u0438 \u043F\u0435\u0440\u0435\u043F\u043B\u0435\u0442\u0430\u0435\u0442 \u0441\u0443\u0434\u044C\u0431\u044B. \u0410 \u0432\u0435\u0434\u044C \u041C\u0435\u0441\u0442\u0430 \u0434\u0435\u043B\u0430\u044E\u0442\u0441\u044F \u0438 \u0441\u043E\u0437\u0434\u0430\u044E\u0442\u0441\u044F \u0433\u0434\u0435 \u0443\u0433\u043E\u0434\u043D\u043E, \u0432\u0435\u0434\u044C \u0438\u0445 \u0434\u0435\u043B\u0430\u0435\u043C \u043C\u044B \u0441\u0430\u043C\u0438!</p><p>\u0425\u043E\u0447\u0443 \u0432\u0441\u0442\u0440\u0435\u0447\u0430\u0442\u044C \u0432\u0430\u0441 \u043D\u0435\u0432\u0437\u043D\u0430\u0447\u0430\u0439 \u0438 \u0442\u043E\u043B\u044C\u043A\u043E \u043A\u043E\u0433\u0434\u0430 \u0441\u0442\u0435\u043C\u043D\u0435\u0435\u0442 \u043F\u043E\u043D\u0438\u043C\u0430\u0442\u044C, \u0447\u0442\u043E \u043F\u0440\u043E\u0432\u0435\u043B \u0441 \u0432\u0430\u043C\u0438 \u043C\u043D\u043E\u0433\u043E \u0447\u0430\u0441\u043E\u0432 \u043F\u043E\u0434\u0440\u044F\u0434. \u041D\u0443 \u0432\u044B \u043F\u043E\u043D\u044F\u043B\u0438, \u0434\u0440\u0443\u0437\u044C\u044F! \u{1F609}</p><p><a href="https://vk.com/wall129086_7625" class="external-link" target="_blank" rel="noreferrer">2011-06-08</a></p>',20),i=[c];function n(_,s,o,l,d,h){return t(),p("div",null,i)}const u=e(r,[["render",n]]);export{m as __pageData,u as default};
