import{o as l,g as t,j as o}from"./motion.2c60df92.js";const n='{"title":"Графический дизайн","description":"","frontmatter":{"title":"Графический дизайн","tags":"collab","list":"graphic"},"relativePath":"collab/graphic/index.md","lastUpdated":1618576479151}',e={},a=[o("p",null,"Экспериментировать с Adobe Photoshop я начинал уже в школе. Повторял разные интересные туториалы, пробовал рисовать на планшете.",-1),o("p",null,"Затем Photoshop я осваивал уже как фотограф и именно через фотографию постигал принципы композиции и визуального творчества.",-1),o("p",null,"Освоил кадрирование, ретушь, цветокоррекцию. Работал с крупными форматами. Отрисовывал первые макеты сайтов тоже там. Но по-настоящему смог раскрыться в работе с графикой, когда перешел на Adobe Illustrator.",-1),o("p",null,"Печатный дизайн начинал с простых визиток и бланков. Верстал каталоги и буклеты. Разрабатывал логотипы, выполнил и несколько брендбуков для небольших компаний.",-1),o("p",null,"Потом во многом переключился на свобственные проекты. Так, например, навыки верстки в Adobe Indesign пригодилась при разработке Руководства по организации и устройству открытых мастерских OOLEY.",-1),o("p",null,"Я сам создаю логотипы и стиль для всех наших проектов. Презентации, плакаты, листовки. Блокноты, буклеты и календари. Есть опыт работы с различной сувениркой.",-1)];e.render=function(o,n,e,r,i,p){return l(),t("div",null,a)};export{n as __pageData,e as default};
