import{k as l,f as t,g as o}from"./motion.74b6f6cb.js";const e='{"title":"Графический дизайн","description":"","frontmatter":{"title":"Графический дизайн","tags":"collab","list":"graphic"},"relativePath":"collab/graphic/index.md","lastUpdated":1618576479151}',n={},r=o("p",null,"Экспериментировать с Adobe Photoshop я начинал уже в школе. Повторял разные интересные туториалы, пробовал рисовать на планшете.",-1),a=o("p",null,"Затем Photoshop я осваивал уже как фотограф и именно через фотографию постигал принципы композиции и визуального творчества.",-1),p=o("p",null,"Освоил кадрирование, ретушь, цветокоррекцию. Работал с крупными форматами. Отрисовывал первые макеты сайтов тоже там. Но по-настоящему смог раскрыться в работе с графикой, когда перешел на Adobe Illustrator.",-1),i=o("p",null,"Печатный дизайн начинал с простых визиток и бланков. Верстал каталоги и буклеты. Разрабатывал логотипы, выполнил и несколько брендбуков для небольших компаний.",-1),s=o("p",null,"Потом во многом переключился на свобственные проекты. Так, например, навыки верстки в Adobe Indesign пригодилась при разработке Руководства по организации и устройству открытых мастерских OOLEY.",-1),d=o("p",null,"Я сам создаю логотипы и стиль для всех наших проектов. Презентации, плакаты, листовки. Блокноты, буклеты и календари. Есть опыт работы с различной сувениркой.",-1);n.render=function(o,e,n,u,c,b){return l(),t("div",null,[r,a,p,i,s,d])};export default n;export{e as __pageData};
