import{_ as e,c as i,o as a,f as r}from"./chunks/framework.wcBTHMk5.js";const g=JSON.parse('{"title":"Denis Starov CV","description":"Digital designer, web developer and multimedia artist","frontmatter":{"title":"Denis Starov CV","description":"Digital designer, web developer and multimedia artist","date":"2024-01-29T00:00:00.000Z"},"headers":[],"relativePath":"cv/index.md","filePath":"cv/index.md"}'),t={name:"cv/index.md"},l=r('<p><a href="https://github.com/davay42" target="_blank" rel="noreferrer">GitHub Profile</a> | <a href="http://starovdenis.com" target="_blank" rel="noreferrer">Personal Website</a> | <a href="https://www.linkedin.com/in/denis-starov/" target="_blank" rel="noreferrer">LinkedIn profile</a></p><hr><h2 id="profile" tabindex="-1">Profile <a class="header-anchor" href="#profile" aria-label="Permalink to &quot;Profile&quot;">​</a></h2><p>I am a freelance digital and print designer and frontend developer with very wide skillset. I have more than 10 years of experience in web technologies. I build UIs with Vue and many other amazing open source libraries. I can build an interactive experience or a digital product from ground up by myself or or in collaborations with your remote team.</p><p>I am passionate about working on impactful projects. I&#39;m open for participation in creative industry and art projects, involving experiments with audio synthesis, vector graphics, web animations, interfaces and intermedia.</p><p>The resulting design, site or app can be closed source and you will get all the IP rights generated in the process. But I do have a discounted rate for open-source projects.</p><hr><h2 id="skillset" tabindex="-1">Skillset <a class="header-anchor" href="#skillset" aria-label="Permalink to &quot;Skillset&quot;">​</a></h2><h3 id="frontend-development" tabindex="-1">Frontend Development <a class="header-anchor" href="#frontend-development" aria-label="Permalink to &quot;Frontend Development&quot;">​</a></h3><ul><li>HTML5</li><li>CSS3</li><li>Vue 3</li><li>Vite build and plugins</li><li>Vitepress static site build</li><li>VueUse</li><li>UnoCSS (Tailwind)</li></ul><h3 id="web-browser-apis" tabindex="-1">Web browser APIs <a class="header-anchor" href="#web-browser-apis" aria-label="Permalink to &quot;Web browser APIs&quot;">​</a></h3><ul><li>SVG, Canvas 2D and GLSL shader graphics and animation</li><li>Web Audio API with Tone.js and Elementary.audio</li><li>Web MIDI API with WebMIDI.js</li><li>Web Bluetooth API in Chromium browsers</li><li>Fullscreen API and Intersection Observer API</li><li>File Reader API, Clipboard API, Web Share API</li><li>Local Storage API and IndexedDB for client-side data storage</li><li>Installable PWAs and desktop/mobile apps build with Tauri</li></ul><h3 id="backend-development" tabindex="-1">Backend Development <a class="header-anchor" href="#backend-development" aria-label="Permalink to &quot;Backend Development&quot;">​</a></h3><ul><li>Node <ul><li>FileSystem API, Fetch API, ES6 data parsing</li><li>package.json management</li><li>NPM package release and maintainance</li></ul></li><li>Nuxt <ul><li>SSR</li><li>SSG</li><li>Server routes</li></ul></li><li>Directus <ul><li>PostgreSQL schema</li><li>REST API design</li><li>Flow Server Actions</li></ul></li><li>Docker deployments with Coolify and Portainer</li></ul><h3 id="auxiliary-development" tabindex="-1">Auxiliary development <a class="header-anchor" href="#auxiliary-development" aria-label="Permalink to &quot;Auxiliary development&quot;">​</a></h3><ul><li>Git</li><li>GitHub actions CI/CD</li><li>GitHub pages static hosting</li><li>ESP32 prototype firmware build and upload</li></ul><h3 id="design" tabindex="-1">Design <a class="header-anchor" href="#design" aria-label="Permalink to &quot;Design&quot;">​</a></h3><ul><li>Adobe Illustrator vector design</li><li>Packaging design</li><li>Logo and brand identity design</li><li>Pre-press layout preparation</li></ul><h3 id="multimedia" tabindex="-1">Multimedia <a class="header-anchor" href="#multimedia" aria-label="Permalink to &quot;Multimedia&quot;">​</a></h3><ul><li>Music production in Logic Pro and Ableton</li><li>Live music performance with portable synth setup</li><li>Field and studio sound recording and editing</li><li>Reportage and studio photography</li><li>Drone flying and filming</li></ul><hr><h2 id="work-experience" tabindex="-1">Work experience <a class="header-anchor" href="#work-experience" aria-label="Permalink to &quot;Work experience&quot;">​</a></h2><p><strong>Interactive experience designer</strong> @ Playtronica | Freelance | 2022-2024</p><ul><li>Remote team work experience</li><li>MIDI enabled Tone.js music synthesizer and custom SVG visualization for TouchMe Experience</li><li>A Tone.js synthesizer and visualizer of musical notes played on a MIDI controller or a computer keyboard</li><li>Interactive user experience with multiple scenes and usage statistics</li><li>SVG animations, full-screen mode and other modern web tech</li><li>Lightweight UI and reactive database for the Web Synth Collection</li></ul><p><strong>Music Theory Teacher</strong> @ SkillBox | Visual Music Course | 2022-2023</p><ul><li>Online course presenter experience with a complete video production team</li><li>Deep research verified and mapped with a professional methodologist</li><li>Work with remote students and educational materials production</li></ul><p><strong>Product graphic designer</strong> @ SkillPad | Freelance | 2018-2021</p><ul><li>Adobe Illustrator vector design for print and packaging</li><li>Print shop communication and comprehensive print material research</li><li>Customer relations and product quality control</li></ul><hr><h2 id="personal-projects" tabindex="-1">Personal projects <a class="header-anchor" href="#personal-projects" aria-label="Permalink to &quot;Personal projects&quot;">​</a></h2><p><strong>Research and development</strong> @ chromatone.center | 2014 - Present</p><ul><li>Visual Music Theory research from the very basics to profound composition knowledge</li><li>Knowledge packed in Theory articles and learning experience packed in Practice web-apps for everyone</li><li>Real life tested and refined Vue + Vitepress web-app build process</li><li>Working print shop and custom LMS in development</li></ul><p><strong>Musician, composer and live music performer</strong> @ tsoop.com | 2017 - Present</p><ul><li>Live multimedia generation - I create both music and visuals right at the moment</li><li>Full yet portable electronic music performance and recording setup</li><li>5 albums, 2 singles published on all major platforms</li></ul><p><strong>Research, design, video production, crafts and copywriter</strong> @ OOLEY | 2012-2022</p><ul><li>Makerspace catalog and up-to-date map</li><li>Interviews and video reviews recording, editing and publishing</li><li>Educational events planning and conducting</li><li>Full brand design and media package</li></ul><hr><h2 id="npm-packages-maintainer" tabindex="-1">NPM Packages Maintainer <a class="header-anchor" href="#npm-packages-maintainer" aria-label="Permalink to &quot;NPM Packages Maintainer&quot;">​</a></h2><ul><li><a href="https://gun-vue.js.org" target="_blank" rel="noreferrer">Gun-Vue</a> - Toolset for easy p2p app development with Gun database and Vue 3 reactive components system</li><li><a href="https://gun-avatar.js.org" target="_blank" rel="noreferrer">Gun-Avatar</a> - Simple JS avatar generator for use with SEA Cryptographic keypairs</li><li><a href="https://www.npmjs.com/package/use-chromatone" target="_blank" rel="noreferrer">use-chromatone</a> - Interactive visual music toolkit</li></ul><hr><h2 id="education" tabindex="-1">Education <a class="header-anchor" href="#education" aria-label="Permalink to &quot;Education&quot;">​</a></h2><h3 id="bachelor-of-engineering-be" tabindex="-1">Bachelor of Engineering - BE <a class="header-anchor" href="#bachelor-of-engineering-be" aria-label="Permalink to &quot;Bachelor of Engineering - BE&quot;">​</a></h3><p>Mendeleev University of Chemical Technology of Russia 2004–2008</p><ul><li>Chemical Engineering</li><li>Technical English Translation</li><li>Glass Ceramics Technology</li></ul><h2 id="languages" tabindex="-1">Languages <a class="header-anchor" href="#languages" aria-label="Permalink to &quot;Languages&quot;">​</a></h2><ul><li><strong>Russian</strong> - Native speaker</li><li><strong>English</strong> - Upper intermediate - B2</li><li><strong>Thai</strong> - Elementary</li></ul><h2 id="let-s-build-something-together" tabindex="-1">Let&#39;s build something together <a class="header-anchor" href="#let-s-build-something-together" aria-label="Permalink to &quot;Let&#39;s build something together&quot;">​</a></h2><p>I create purposeful web-sites and internal tools for any business and personal use. It may be anything from the list:</p><ul><li>Portfolio or Catalog static site built with Vitepress,</li><li>Blog with Comments and Reactions, served by Nuxt,</li><li>Internal CRM with analytics based on Directus,</li><li>Online Shop with payments API from Stripe or other provider,</li><li>Resource Management system with e-mail or other notifications system,</li><li>Internal Knowledge Base with certain fields available as public APIs and in your employee/customer web-app,</li><li>a discussion and rating platform for any goods and services...</li></ul><p>...or any custom database, logic and interface need for your business. Let&#39;s talk!</p><h2 id="i-m-available-for-freelance-projects-and-part-fulltime-hire" tabindex="-1">I&#39;m available for freelance projects and part/fulltime hire <a class="header-anchor" href="#i-m-available-for-freelance-projects-and-part-fulltime-hire" aria-label="Permalink to &quot;I&#39;m available for freelance projects and part/fulltime hire&quot;">​</a></h2><hr><h2 id="my-project-based-work-plan" tabindex="-1">My project based work plan <a class="header-anchor" href="#my-project-based-work-plan" aria-label="Permalink to &quot;My project based work plan&quot;">​</a></h2><ol><li>You reach me out and describe the project/task/position, that you have.</li><li>I ask questions that help me figure out estimated time and efforts needed to accomplish it.</li><li>My basic rate is $30-50/hour, depending to the skillset involved. A discounted rate available in case the final code will be open-sourced.</li><li>I propose you my work schedule and payment terms.</li><li>We negotiate and approve the project conditions.</li><li>I get to work with timer and weekly progress reports</li><li>You accept the results and pay for the time spent in a next week period.</li><li>We continue this cycle until all of your business requirements are met.</li><li>We finalize the project, you pay me the last payment and I give you all of the source code, layout files and finished product.</li><li>Support and maintainance service for the product can be negotiated.</li></ol><hr><h2 id="contact-me" tabindex="-1">Contact me <a class="header-anchor" href="#contact-me" aria-label="Permalink to &quot;Contact me&quot;">​</a></h2><ul><li><strong>Email:</strong> <a href="mailto:me@starovdenis.com" target="_blank" rel="noreferrer">me@starovdenis.com</a></li><li><strong>Telegram:</strong> <a href="https://t.me/starov" target="_blank" rel="noreferrer">@starov</a></li><li><strong>LinkedIn:</strong> <a href="https://www.linkedin.com/in/denis-starov/" target="_blank" rel="noreferrer">Denis Starov</a></li></ul>',57),n=[l];function o(s,d,c,u,h,p){return a(),i("div",null,n)}const b=e(t,[["render",o]]);export{g as __pageData,b as default};
