import{_ as t,o,c as s,a as e,b as a}from"./chunks/framework.c2f1a08b.js";const b=JSON.parse('{"title":"Культурология","description":"","frontmatter":{"title":"Культурология","subtitle":"Люди не изобретают жизнь заново каждое поколение, а перенимают большую часть образцов мысли и поведения из культурной среды вокруг.","tags":"philosophy","list":"culturology"},"headers":[],"relativePath":"philosophy/culturology/index.md","filePath":"philosophy/culturology/index.md"}'),l={name:"philosophy/culturology/index.md"},n=e("p",null,"В наблюдениях, беседах и размышлениях я раз за разом обнаруживаю, насколько глубоко культура определяет жизнь человека. Мнения, идеи и представления очень частов происходят не из внутренних убеждений и осознанного выбора, а переняты через подражание моделям поведения, предлагаемым нам культурой с самого рождения.",-1),i=e("p",null,"Культуру можно определить и как актуализированную память сообщества. Исторически она в основном накапливала, закрепляля и передавала сама себя из поколения в поколение. Резкие перемены происходили в древности в основном по внешним причинам — катаклизмы, захват, геноцид. Этого история видела много. А вот тотальная трансформация всего общества за считанные десятилетия — это удивительный прорыв человечества лишь в последние пару веков.",-1),c=e("p",null,"Опыт поколений земледельцев оказался не востребован в городе, а новая жизнь в промышленном обществе потребовала выработки новых норм, традиций, этики. Долгое время этим занималось в основном государства и церкви, затем в дело включились компании и корпорации. Средством доставки новых культурных образцов до людей сотнями лет были театр и книги, затем ими стали кино и тв, теперь — платформы и блогеры.",-1),r=e("p",null,"Со временем я пришел к тому, что моим наиболее ценным вкладом в общественное достояние может стать проявленный набор образов мысли и действия, несущих в себе потенциал творческой трансформации общества. Так я пришел к открытым мастерским, как пространству для раскрытия этого потенциала. Этот же порыв стоит у истоков множества моих социокультурных проектов.",-1),d=e("h3",{id:"с-благодарностью-к-наследию-предков-я-уже-много-лет-изучаю-как-наши-идеи-и-совместные-деиствия-могут-определять-в-свою-очередь-современную-культуру-а-значит-и-наше-будущее",tabindex:"-1"},[a("С благодарностью к наследию предков я уже много лет изучаю, как наши идеи и совместные действия могут определять, в свою очередь, современную культуру, а значит и наше будущее. "),e("a",{class:"header-anchor",href:"#с-благодарностью-к-наследию-предков-я-уже-много-лет-изучаю-как-наши-идеи-и-совместные-деиствия-могут-определять-в-свою-очередь-современную-культуру-а-значит-и-наше-будущее","aria-label":'Permalink to "С благодарностью к наследию предков я уже много лет изучаю, как наши идеи и совместные действия могут определять, в свою очередь, современную культуру, а значит и наше будущее."'},"​")],-1),p=[n,i,c,r,d];function _(h,u,m,f,x,y){return o(),s("div",null,p)}const $=t(l,[["render",_]]);export{b as __pageData,$ as default};
