import{k as n,f as t,g as l,J as e}from"./motion.f7ed0388.js";const r='{"title":"Культурология","description":"","frontmatter":{"title":"Культурология","subtitle":"Люди не изобретают жизнь заново каждое поколение, а перенимают большую часть образцов мысли и поведения из культурной среды вокруг.","tags":"philosophy","list":"culturology"},"headers":[{"level":3,"title":"С благодарностью к наследию предков я уже много лет изучаю, как наши идеи и совместные действия могут определять, в свою очередь, современную культуру, а значит и наше будущее.","slug":"с-бnагодарностью-к-насnедию-предков-я-уже-много-nет-изучаю-как-наши-идеи-и-совместные-действия-могут-опредеnять-в-свою-очередь-современную-куnьтуру-а-значит-и-наше-будущее"}],"relativePath":"philosophy/culturology/index.md","lastUpdated":1619030345791}',o={},a=l("p",null,"В наблюдениях, беседах и размышлениях я раз за разом обнаруживаю, насколько глубоко культура определяет жизнь человека. Мнения, идеи и представления очень частов происходят не из внутренних убеждений и осознанного выбора, а переняты через подражание моделям поведения, предлагаемым нам культурой с самого рождения.",-1),i=l("p",null,"Культуру можно определить и как актуализированную память сообщества. Исторически она в основном накапливала, закрепляля и передавала сама себя из поколения в поколение. Резкие перемены происходили в древности в основном по внешним причинам — катаклизмы, захват, геноцид. Этого история видела много. А вот тотальная трансформация всего общества за считанные десятилетия — это удивительный прорыв человечества лишь в последние пару веков.",-1),s=l("p",null,"Опыт поколений земледельцев оказался не востребован в городе, а новая жизнь в промышленном обществе потребовала выработки новых норм, традиций, этики. Долгое время этим занималось в основном государства и церкви, затем в дело включились компании и корпорации. Средством доставки новых культурных образцов до людей сотнями лет были театр и книги, затем ими стали кино и тв, теперь — платформы и блогеры.",-1),u=l("p",null,"Со временем я пришел к тому, что моим наиболее ценным вкладом в общественное достояние может стать проявленный набор образов мысли и действия, несущих в себе потенциал творческой трансформации общества. Так я пришел к открытым мастерским, как пространству для раскрытия этого потенциала. Этот же порыв стоит у истоков множества моих социокультурных проектов.",-1),d=l("h3",{id:"с-бnагодарностью-к-насnедию-предков-я-уже-много-nет-изучаю-как-наши-идеи-и-совместные-действия-могут-опредеnять-в-свою-очередь-современную-куnьтуру-а-значит-и-наше-будущее"},[l("a",{class:"header-anchor",href:"#с-бnагодарностью-к-насnедию-предков-я-уже-много-nет-изучаю-как-наши-идеи-и-совместные-действия-могут-опредеnять-в-свою-очередь-современную-куnьтуру-а-значит-и-наше-будущее","aria-hidden":"true"},"#"),e(" С благодарностью к наследию предков я уже много лет изучаю, как наши идеи и совместные действия могут определять, в свою очередь, современную культуру, а значит и наше будущее.")],-1);o.render=function(l,e,r,o,p,h){return n(),t("div",null,[a,i,s,u,d])};export default o;export{r as __pageData};
