import{l as e,f as l,g as r,N as t}from"./motion.b5c20781.js";const a='{"title":"Эдмунд Гуссерль","description":"","frontmatter":{"title":"Эдмунд Гуссерль","tags":"philosophyTeacher"},"headers":[{"level":3,"title":"Феноменология","slug":"феноменоnогия"}],"relativePath":"philosophy/teachers/husserl.md","lastUpdated":1617903453069}',n={},i=[r("p",null,[t("Цитата из книги "),r("a",{href:"https://ru.wikipedia.org/wiki/%D0%93%D1%83%D1%81%D1%81%D0%B5%D1%80%D0%BB%D1%8C,_%D0%AD%D0%B4%D0%BC%D1%83%D0%BD%D0%B4",class:"external-link",target:"_blank",rel:"noopener noreferrer"},"Эдмунда Гуссерля"),t(" «Кризис европейских наук и трансцендентальная феноменология» (1936, полный текст рукописи был издан посмертно, в 1954 году).")],-1),r("p",null,'Начнем с общеизвестного - с различия между естественнонаучной медициной и так называемым "лечением силами природы". Последнее возникает в повседневной жизни народа из наивной эмпирии и традиции , тогда как естественнонаучная медицина основывается на воззрениях чисто теоретических наук, прежде всего анатомии и физиологии, на их представлениях о человеческой телесности. Они же, в свою очередь, основываются на всеобщих фундаментальных науках, объясняющих природу, - физике и химии.',-1),r("p",null,'Перейдем теперь от человеческой телесности к человеческой духовности - теме так называемых наук о духе. Предмет их теоретического интереса - люди как личности, их личностная жизнь и деятельность и, соответственно, ее продукты. Личностная жизнь - это когда Я и Мы живут сообщественно, в горизонте общности, а именно в различных простых и иерархизированных общностях, таких, как семья, нация, сверхнация. Слово жизнь здесь не имеет физиологического смысла: оно означает жизнь целенаправленную, создающую продукты духа, в наиболее полном же смысле - культуротворящую жизнь в единстве определенной историчности. Все это и есть тема многообразных наук о духе. При этом ясно, что существует различие между бурным цветением и загниванием, или, можно сказать, между здоровьем и болезнью, также и для общностей, народов, государств. Поэтому очевиден вопрос: почему здесь не родилась научная медицина - медицина наций и сверхнациональных общностей? Европейские нации больны, говорят нам, сама Европа в кризисе. В естественных средствах лечения нет недостатка. Мы прямо-таки тонем в потоке наивных и экзальтированных реформаторских идей. Но почему столь высокоразвитые науки о духе пасуют перед тем, что успешно осуществляют в своей сфере науки естественные? Тот, кто проникнут духом современной науки, не замедлит с ответом. Величие естественных наук состоит в том, что они не удовлетворяются наглядной эмпирией, что для них любое описание природы - лишь методический подступ к точному, в конечном счете физико-химическому объяснению. Считается, что "чисто описательные" науки привязывают нас к конечности земной среды. Математически точная наука о природе, напротив, охватывает своими методами бесконечность в ее действительных и реальных возможностях. Она воспринимает наглядно данное как чисто субъективное, относительное явление и учит путем систематического приближения постигать самое сверхсубъективную ("объективную") природу, обнаруживая в ней безусловно всеобщее в элементах и законах. Вместе с тем она учит объяснять все наглядно данные конкретности - будь то люди, животные или небесные тела - через конечно сущее, т. е. из соответствующих фактически данных явлений выводить будущие возможности и вероятности в таком объеме и с такой точностью, которые превосходят любую связанную с уровнем наглядности эмпирию. Результатом последовательного развития наук в Новое время стала подлинная революция в техническом господстве над природой.',-1),r("p",null,'К сожалению (в смысле уже проясняющейся для нас позиции), совсем иной, причем по внутренним основаниям, является методологическая ситуация в науках о духе. Ведь человеческий дух зиждется на человеческой природе. Душевная жизнь каждого человека коренится в телесности, а следовательно, и каждая общность - в телах отдельных людей, являющихся ее членами. Стало быть, если для явлений, входящих в сферу наук о духе, должно быть найдено действительно точное объяснение, и вслед за этим выработана также и достаточно широкоохватная научная практика, как в естественнонаучной сфере, то представителям наук о духе здесь надлежит не просто рассматривать дух как таковой, но направлять свой взор также и к его телесным основаниям и строить свои объяснения с помощью таких точных наук, как физика и химия. Этому, однако, препятствует (и в обозримом времени ничего не может измениться) сложность точных психофизиологических исследований, нужных уже для понимания человека, а тем более больших исторических общностей. Будь мир состоящим из двух, так сказать, равноправных сфер реальности - природы и духа, из которых ни одной не давалось бы предпочтения по отношению к другой методически и предметно, ситуация была бы иной. Однако лишь природу можно изучать саму по себе как замкнутый мир, лишь наука о природе может с твердой последовательностью абстрагироваться от всего духовного и заниматься природой как природой. В то же время ученого, интересующегося только духом, такое последовательное абстрагирование от природы не ведет vice versa к замкнутому в себе, чисто духовно организованному "миру", который мог бы стать темой чистой и универсальной науки о духе, параллели чистому естествознанию. Ибо анимальная духовность, духовность "душ" людей и животных, к которой сводятся все прочие проявления духа, неповторимым образом каузально фундирована в телесном. Так и получается, что ученый, интересующийся только духом как таковым, не может выйти за пределы описания истории духа и потому остается привязанным к наглядности конечного. Это видно повсюду. Например, историк не может заниматься Древней Грецией, не учитывая ее физической географии, древнегреческой архитектуры - без учета материала ее строений и т. д. и т. п. Это выглядит вполне убедительно.',-1),r("p",null,"А что, если весь изображенный здесь способ мышления основан на роковых предрассудках и именно его воздействие повинно в болезни Европы? Я убежден, что это действительно так, еще я надеюсь показать, что именно в нем лежит источник той уверенности, с которой современный ученый просто отвергает, считая, что не стоит даже пытаться, возможность обоснования чистой, в себе замкнутой всеобщей науки о духе.",-1),r("hr",null,null,-1),r("h3",{id:"феноменоnогия"},[r("a",{class:"header-anchor",href:"#феноменоnогия","aria-hidden":"true"},"#"),t(" Феноменология")],-1)];n.render=function(r,t,a,n,s,o){return e(),l("div",null,i)};export default n;export{a as __pageData};
