import{_ as l,c as i,o as e,d as a}from"./app.e7230eea.js";const f=JSON.parse('{"title":"Рецепты блинов","description":"","frontmatter":{"title":"Рецепты блинов","subtitle":"Самые простые полностью растительные блины","tags":"vegan"},"headers":[{"level":2,"title":"Блины на воде","slug":"блины-на-воде","link":"#блины-на-воде","children":[]},{"level":2,"title":"Плотные блины","slug":"плотные-блины","link":"#плотные-блины","children":[]}],"relativePath":"philosophy/vegan/pancake.md"}'),t={name:"philosophy/vegan/pancake.md"},n=a('<h2 id="блины-на-воде" tabindex="-1">Блины на воде <a class="header-anchor" href="#блины-на-воде" aria-hidden="true">#</a></h2><ul><li>Вода кипяченая холодная - 500 мл</li><li>Мука - 250 г</li><li>Сахар - 2-3 ст.л.</li><li>Масло растительное - 6 ст.л.</li><li>Сода пищевая - 2 г</li><li>Соль - 1 щепотка</li></ul><p>Простой и быстрый рецепт блинов на воде без яиц и молока.</p><ol><li>Смешать сухие ингредиенты.</li><li>Ввести в муку воду до консистенции жидкой сметаны.</li><li>Добавить масло и снова перемешать.</li><li>Жарить блины нужно на очень горячей сковороде, смазав ее каплей масла, или использовать сковороду с антипригарным покрытием.</li></ol><h2 id="плотные-блины" tabindex="-1">Плотные блины <a class="header-anchor" href="#плотные-блины" aria-hidden="true">#</a></h2><p>Ингредиенты:</p><ul><li>овсяное молоко — 4 стакана;</li><li>сахар — 3 ст. ложки;</li><li>мука — 2 стакана;</li><li>соль и сода — по половине ч. ложки;</li><li>растительное масло — около 4 ст. ложек.</li></ul><p>Овсяное молоко можно легко приготовить в домашних условиях. Просто заливаем стакан овсяных хлопьев тремя стаканами горячей воды. Настаиваем смесь в течение одного часа. Затем тщательно перемешиваем в блендере и отжимаем через марлю.</p><p>Процесс приготовления таких блинов довольно прост:</p><ol><li>Смешиваем сахар с содой и мукой;</li><li>В полученную массу вливаем овсяное молоко и аккуратно добавляем растительное масло;</li><li>Взбиваем смесь венчиком или блендером до однородной массы без комочков;</li><li>Выпекаем блины на раскаленной сковородке до появления золотистого цвета с обеих сторон.</li></ol>',10),o=[n];function r(s,c,_,p,d,h){return e(),i("div",null,o)}const m=l(t,[["render",r]]);export{f as __pageData,m as default};
